	["aq_handcannon"] = { name = "Handfeuerwaffe", desc = "Teile der Waffe bestehen aus Messing und wurden mit Säure patiniert, inspiriert von antiken Messingkanonen.\n\nBereiten Sie sich darauf vor, bei den kommenden Herausforderungen auf viele Dornen zu stoßen" },
	["am_metal_inlay"] = { name = "Mittelalter", desc = "Die Waffe wurde mit Metallicfarben lackiert, um auszusehen, als wäre sie mit einem Mittelaltermotiv tauschiert.\n\n„Ich könnte ihren Liebhaber töten … vielleicht würde dir das etwas Zeit mit Valeria verschaffen“ - Die Liebe eines Vaters, Teil 2" },
