	["so_indigo"] = { name = "Königsblau", desc = "Die Waffe wurde eintönig in Indigoblau, Blutrot und Grau lackiert.\n\n„Sie sollten öfter lächeln, Felix. Sie hätten viel mehr Glück bei den Frauen, wenn Sie nicht immer wie ein mürrischer Bastard aussehen würden“ - Imogen, Waffenhändler in Ausbildung" },
	["so_indigo_and_grey"] = { name = "Indigo", desc = "Die Waffe wurde eintönig in Indigoblau, Blau und Grau lackiert.\n\n„Alex Kincaide, ich bin mir sicher, dass du viele Fragen hast … bitte erlaube mir, dir Antworten zu liefern“ - Die Phoenix und der Geschichtenerzähler, Teil 1" },
	["cu_well_traveled_ak47"] = { name = "Jetset", desc = "Der Schaft wurde mit braunem Leder umwickelt. Diese Waffe hat schon viele Städte bereist.\n\nJeder, der aufmerksam ist, wird die Punkte verbinden können und sehen, was sie getan hat. Wir haben nicht viel Zeit, um eine Lösung zu finden … - Die Liebe eines Vaters, Teil 1" },
