	["cu_luggage_p2000"] = { name = "Touristenklasse", desc = "Die Waffe wurde durch Wassertransferdruck mit einem Karomuster versehen und Teile des Griffs wurden mit braunem Leder umwickelt.\n\nSie ist zwar nicht kunstvoll, bringt Sie jedoch an Ihren Bestimmungsort" },
