	["cu_luggage_sg553"] = { name = "Reisender", desc = "Die Waffe wurde mit einer Sonderlackierung in einem von altmodischem Reisegepäck inspirierten Streifendesign versehen und einige Teile wurden mit braunem Leder umwickelt.\n\nJeder hat eine Geschichte und Javier wird sie aus Ihnen herauspressen, bevor er Sie tötet" },
