	["gs_G3SG1_Ventilator"] = { name = "Ventilator", desc = "Die Waffe wurde mit elegantem schwarzen und chromfarbenen Lack sowie einem Gummischaft versehen.\n\n„Keine Sorge Naomi, ich werde Felix dafür bezahlen lassen“ – Mikha Biton, Scharfschütze der Phoenix" },
	["cu_Glock18_Weasel"] = { name = "Wiesel", desc = "Die Waffe wurde durch Wassertransferdruck mit Bobby, dem boshaften Wiesel, versehen.\n\nNur weil er niedlich ist, heißt das nicht, dass er nicht beißt" },
	["cu_MAG7_Tribal"] = { name = "Felsbild", desc = "Die Waffe wurde mit einer Sonderlackierung versehen, um die Illusion eines Felsbilds zu erzeugen.\n\n„Konflikte existieren, seit der erste Höhlenmensch gelernt hat, einen Stein aufzuheben“ – Booth, Waffenhändler" },
