	["cu_sg553_darkwing"] = { name = "Dunkler Flügel", desc = "Auf die Waffe wurde eine rote und schwarze Sonderlackierung mit Federn und anderen dunklen Details aufgebracht.\n\nAdleräugig und extrem tödlich; Sie werden keinen gefährlicheren flugunfähigen Vogel finden" },
	["cu_ssg08_fever_dream"] = { name = "Fiebertraum", desc = "Die Waffe wurde mit einer Sonderlackierung in pink, blau und lila Zeichnungen auf einer schwarzen Grundierung versehen. \n\nH@@HH@H@H@HA!!" },
	["cu_glock18_warmaiden"] = { name = "Patronenkönigin", desc = "Die Waffe wurde mit einer Sonderlackierung mit bedrohlichen Patronen und einer Jungfrau des Kriegs in einer pinken Jacke über einen gelben und schwarzen Grundierung versehen.\n\n„Ich hör auf zu schießen, wenn Sie keinen Mucks mehr von sich geben“" },
	["cu_negev_ultralight"] = { name = "Ultralux", desc = "Die Waffe wurde mit einer Sonderlackierung mit einem grauen digitalen Tarnmuster und seegrünen Pfeile versehen.\n\nHast du Feuer?" },
	["cu_ak47_anubis"] = { name = "Legion des Anubis", desc = "Eine Sonderlackierung mit den ägyptischen Göttern Anubis und Ra, die das Gleichgewicht zwischen Leben und Tod darstellen.\n\nMöge die Waage zu Ihren Gunsten kippen" },
	["aq_p2000_lost_world"] = { name = "Knorrig", desc = "Ein gewundenes, außerirdisches Wesen streckt sich vom Griff abwärts über die Folie dieser handbemalten P2000.\n\nAus den dunklen Ecken der unendlichen Leere" },
	["cu_mag7_monster_call"] = { name = "Ruf des Monsters", desc = "Die Waffe wurde mit einer Sonderlackierung mit Unterwasserkreaturen auf einer blauen Grundierung versehen.\n\nEs gibt immer einen größeren, grüneren Fisch" },
	["cu_printstream"] = { name = "Printstream", desc = "Die Waffe wurde mit einer Sonderlackierung mit einem schwarz-weißen Design versehen und mit Perlglanz-Highlights veredelt.\n\nWEISS_1; SCHWARZ_1; PERLGLANZ_1;" },
	["cu_glock_eyecontact"] = { name = "In Mode", desc = "Ein Augenpaar ziert den Schlitten dieser speziell lackierten, von der Pop Art inspirierten Glock.\n\n„Schau mich an, wenn ich auf dich schieße!“" },
	["gs_tec9_guerilla"] = { name = "Bruder", desc = "Die Waffe wurde mit einer Sonderlackierung mit einem braunen und goldenen Tarnmuster und einem T unterhalb der Kimme versehen.\n\nIch werde auf unsere Hintern aufpassen" },
	["cu_mac10_isoonna"] = { name = "Verlockung", desc = "Eine Frau in Seegrün, vermutlich Iso-onna, steht vor einem Koi und brechenden Wellen auf dieser speziell lackierten MAC-10.\n\nSchönheit ist die Mutter des Blutvergießens" },
	["gs_sg553_rusty"] = { name = "Ol' Rusty	", desc = "Feuchtigkeit hat das Aussehen dieser Waffe aus poliertem Stahl verändert. Über der Abzugshülse ist „Ol' Rusty“ gestempelt.\n\nEin rostiger, treuer Freund" },
	["cu_ssg08_mainframe"] = { name = "Mainframe 001", desc = "Auf die Waffe wurde eine High-Tech-Sonderlackierung in Blau und Lila aufgetragen.\n\nWelche Geheimnisse birgt _001?" },
	["cu_p250_cassette"] = { name = "Kassette", desc = "Die Waffe wurde mit einer Sonderlackierung versehen, die von der Verpackung von Videokassetten aus den 1990er Jahren inspiriert ist.\n\nKontrollieren Sie das Band" },
	["gs_p90_container"] = { name = "Fracht", desc = "Die Sonderlackierung ähnelt einem mit Graffiti beschmierten Zugwaggon.\n\nKunst ist kein Vandalismus" },
	["cu_xm1014_amulet_blue"] = { name = "Begraben", desc = "Diese vor kurzem ausgegrabene XM-1014, die seit Jahrtausenden begraben lag, wurde mit einer Sonderlackierung mit blauen Umhüllungen und ägyptischen Akzenten versehen.\n\nPassend für einen Pharao" },
	["cu_m4a4_queenfairy"] = { name = "Zahnfee", desc = "Die Waffe wurde mit einer Sonderlackierung mit der Darstellung von Schneide- und Backenzähnen und einem erschreckenden Blick auf die Zahnfee versehen.\n\nNiemand hat gesagt, dass die Zähne Ihre sein müssen …" },
	["gs_galil_phoenix"] = { name = "Verbindung", desc = "Die Logos der Phönix-Fraktion wurden über die weiße und rote Grundierung gesprüht.\n\nSie kommen mit den großen Geschützen" },
	["gs_bizon_hellraider"] = { name = "Runisch", desc = "Diese Sonderlackierung zeigt Runen unbekannter Herkunft und ein Magazin, das dem Laufbündel eines Gatling-Geschützes ähnelt.\n\nMach ihnen die Hölle heiß!" },
	["gs_mp5sd_wasteland_legacy"] = { name = "Kitbash", desc = "20 Jahre MP5-Entwürfe haben zu dieser Sonderlackierung beigetragen, die durch Klebeband, Kabelbinder und Klebstoff zusammengehalten wird.\n\nWirf die Flinte nicht gleich ins Korn" },
	["gs_awp_exoskeleton"] = { name = "Exoskelett", desc = "In das Metall dieser AWP wurden filigrane Muster ineinandergreifender Schädel gepresst.\n\nVoll Karacho" },
	["gs_cz75_vendetta"] = { name = "Vendetta", desc = "Im Laufe der Jahre wurde die Waffe eingeätzt, aufgetragen und durch Gebrauch gehärtet.\n\nAus meinen kalten, toten Händen" },
	["hy_p90_dino_rampage"] = { name = "Schokorausch", desc = "Die Waffe wurde durch Wassertransferdruck mit einem olivgrün-goldenen Muster von Cartoon-Dinos versehen.\n\nDinosaurier und Außerirdische und Kometen, oh je!" },
	["gs_dual_elites_dezastre"] = { name = "Dezastre", desc = "Die Waffe wurde mit einer Sonderlackierung in einem schwarz-weiß-roten High-Tech-Design versehen.\n\nBefolgen Sie zur Sicherheit stets die Anweisungen des Herstellers" },
	["cu_five_seven_diary"] = { name = "Märchen", desc = "Die Waffe wurde mit einer Sonderlackierung mit einer Prinzessin, Einhörnern, wunderschönen Blumen und einem Schloss im Hintergrund versehen.\n\n„Heute wird ein guter Tag, das kann ich fühlen“" },
	["gs_g3sg1_disrupt"] = { name = "Digitales Netz", desc = "Die Waffe wurde mit einem roten, weißen und kohlefarbenen DDPAT-Muster lackiert.\n\nErschütternd kompliziert" },
	["gs_galil_vandal"] = { name = "Vandale", desc = "Die grüne Grundierung der Waffe wurde mit gesprenkelten weißen Zeichnungen übersät.\n\nJede Zeichnung erzählt eine Geschichte, manche denkwürdiger als andere" },
	["cu_p250_infect"] = { name = "Schadstoff", desc = "Ihr kastanienbraunes Gehäuse wurde zerfressen, sodass eine zerfallende Waffe zurückblieb, die nur noch begrenzt einsetzbar ist.\n\nEs ist nur so schlimm, wie es aussieht" },
	["cu_m249_deep_relief"] = { name = "Relief", desc = "Eine sandfarbene Deckschicht weicht dunkleren Grüntönen, je tiefer man geht. Dies ist eine Sonderlackierung, die man fühlen kann.\n\nWie viele Kugeln braucht man, um ins Innere zu gelangen?" },
	["cu_m4a4_cyberpunk"] = { name = "Onlinesicherheit", desc = "Über die schwarze Grundierung dieser M4 wurde eine Sonderlackierung mit blauen, roten und goldenen diagonalen Bändern aufgetragen.\n\nJETZT BEI IHREM KYBERNETIKHÄNDLER ERHÄLTLICH" },
	["gs_mp5_conditionzero"] = { name = "Zustand Null", desc = "Die blau-gelbe Sonderlackierung ist eine Hommage an Counter-Striker: Condition Zero.\n\n„Panzerfaust auf drei Uhr! Ausweichen!“" },
	["cu_nova_polymer"] = { name = "Klares Polymer", desc = "Die Waffe wurde mit einer Sonderlackierung versehen, um das Äußere transparent aussehen zu lassen, so als würde man das Innere der Waffe sehen.\n\nIhre Ziele sind klar" },
	["cu_glock_noir"] = { name = "Neo-Noir", desc = "Die Waffe wurde mit einer Sonderlackierung mit einer stilisierten blau-magentafarbenen Frau auf einem grau skalierten Hintergrund versehen.\n\n„Die Dinge begannen sich zu wenden und sie begann, Hoffnung zu schöpfen … dann schaute sie auf“" },
	["cu_ssg08_chromatic"] = { name = "Parallaxe", desc = "Die Farben in dieser Sonderlackierung wurden in versetzten Positionen aufgetragen, was zu einem auffälligen Aussehen führte.\n\nZweimal schießen, einmal töten" },
	["gs_ump_gold_bismuth"] = { name = "Goldenes Bismut", desc = "Auf der Waffe wurde ein goldenes Bismut-Muster zwischen weißen und schwarzen Grundierungen eingeschlossen.\n\n„Sie haben einen teuren Geschmack …“" },
	["cu_usp_krokos"] = { name = "Monster-Mischmasch", desc = "Es wurde eine Sonderlackierung mit einem grünen Monster und anderen Ikonen aufgetragen, die von populärer urbaner Kunst inspiriert sind. Der Schalldämpfer ist perlmuttartig lackiert.\n\nX__X" },
	["cu_csgo_camo"] = { name = "Globale Offensive", desc = "Die Waffe wurde mit einer Sonderlackierung mit einem blau-gelb-grauem Tarnmuster versehen.\n\n„Wir nehmen Erstkontakt im Harz, Deutschland um 17:25 GMT+1 auf …“" },
