	["cu_mac10_portable"] = { name = "Knopfdrücker", desc = "Die Waffe wurde mit einer Sonderlackierung versehen, um einem Gamepad und verschiedenen Tasten zu ähneln, die durch ein transparentes Polymergehäuse ragen.\n\nGibt dem Ausdruck „Konsolenkrieg” eine völlig neue Bedeutung" },
