	["cu_deagle_kitch"] = { name = "Ozeanstraße", desc = "Die Waffe wurde mit einer Sonderlackierung mit übermäßig gesättigten Farben und verschiedenen Formen versehen.\n\nNicht zu übersehen – sowohl die Farben als auch der Einsatz der Waffe" },
	["gs_dual_berettas_tread"] = { name = "Profil", desc = "Beide Berettas wurden von Hand mit einem Wellenmuster geätzt. Die Schlitten sind goldfarben lackiert.\n\nFeine Details dauern am längsten, bringen am meisten und werden am wenigsten bemerkt." },
	["cu_famas_spectron"] = { name = "ZX-Spektron", desc = "Die Waffe wurde mit einer Sonderlackierung versehen, die von alten Gaming-PCs inspiriert wurde. Die schwarze Grundierung wurde mit mehrfarbigen Akzenten verziert.\n\nClive wäre stolz" },
	["gs_five_seven_efusion"] = { name = "Boost-Protokoll", desc = "Die Waffe wurde mit einer Sonderlackierung mit kontrastreichen Farben versehen. Das Robotersymbol unter der Mündung stimmt mit der Grafik auf dem Schlitten überein.\n\nRufzeichen: MECHA 0272" },
	["aa_mp9_fuji_pink"] = { name = "Fuji", desc = "Die Waffe wurde mit einer Acrylmalerei von Fuji bei Dämmerung versehen. Die Malerei wird als Muster verwendet, sodass jede Waffe anders aussehen kann.\n\nDie rote Sonne geht auf" },
	["cu_g3sg1_tacticalmap"] = { name = "Unter Überwachung", desc = "Die Waffe wurde mit einer Sonderlackierung versehen, die von der taktischen Kartenanzeige der Gefahrenzone inspiriert ist.\n\n„Sie haben alle Informationen, die Sie brauchen“ – Franz Kriegeld, Phoenix-Taktiker" },
	["cu_mp7_khaki"] = { name = "Guerilla", desc = "Diese Sonderlackierung zeigt eine grüne Grundierung, schwarze Details und einen nach vorne schauenden Affen.\n\nDer Löwe war nie König des Dschungels" },
	["gs_m4_flowers"] = { name = "Spinnenlilie", desc = "Die Waffe wurde mit einer Sonderlackierung mit einer dunkelblauen Grundierung und roten Spinnenlilien über Magazin und Abzug versehen.\n\nLeuchtende Farben sind eine Warnung, keine Einladung" },
	["cu_mac10_toybox"] = { name = "Spielzeugkiste", desc = "Diese Sonderlackierung zeigt farbenfrohe Zeichnungen von Hühnern, Außerirdischen, Entschärfern und mehr.\n\nBereinigen Sie Ihr Chaos" },
	["gs_bizon_flasher"] = { name = "Lumen", desc = "Die Waffe wurde mit einer Sonderlackierung schwarz mit roten Akzenten versehen. Das Magazin wurde so bemalt, dass es einer Taschenlampe ähnelt.\n\nBeleuchten Sie den Weg" },
	["cu_glock_snackattack"] = { name = "Heißhunger", desc = "Die Waffe wurde mit einer Sonderlackierung versehen, um wie in Harz eingebetteten Essensresten zu ähneln.\n\nDer Imbiss, der sich wehrt" },
	["cu_ssg_overtake"] = { name = "Turbomodus", desc = "Sportwagen rasen an der Seite dieses speziell lackierten, neonfarbenen Scharfschützengewehrs entlang.\n\nDriften ist ein Recht, kein Privileg" },
	["cu_usp_black_lotus"] = { name = "Black Lotus", desc = "Die Waffe wurde mit einer Sonderlackierung mit einer schwarzen Lotusblüte auf dem Schlitten versehen.\n\nHolt 3 Abschüsse in der Pistolenrunde und wird dann weggeworfen" },
	["gs_xm1014_watchdog"] = { name = "Wachhund", desc = "Die Waffe wurde mit einer Sonderlackierung mit einer jadegrünen Grundierung und zwei Wächterlöwen versehen, die in beide Richtungen schauen. Ein dritter Löwe wurde unter die Mündung gemalt.\n\nSchutz und Wohlstand" },
	["cu_ak47_nightwish"] = { name = "Nachtwunsch", desc = "Die Waffe wurde von Hand mit einem Reh und Traumfängern in Neonfarben bemalt und mit einem Perlmutteffekt versehen.\n\nIch habe die für einen besonderen Anlass aufgehoben … – Booth, Waffenhändler" },
	["cu_bizon_spacecat"] = { name = "Weltraumkatze", desc = "Die Waffe wurde mit einer Sonderlackierung einer Katze im Stil der Weltraumkunst über einer schwarzen und violetten Grundierung versehen.\n\nDie Pfoten der Schöpfung" },
	["cu_elites_beware"] = { name = "Frucht der Fantasie", desc = "Diese Berettas wurden mit einer Sonderlackierung versehen, die eine abartige und furchterregende Darstellung einer Wassermelone auf marineblauer Grundierung zeigt.\n\nSüße Träume …" },
	["gs_famas_rapid_eyes"] = { name = "Schnelle Augenbewegung", desc = "Die Waffe wurde mit rosa ineinandergreifenden Quadraten handbemalt. Die Quadrate haben lebensechte Augen.\n\nHektisch sind die Augen des Träumers" },
	["cu_fiveseven_alpha_omega"] = { name = "Kritzelei", desc = "Die Waffe wurden mit individuellen Designs mit kräftigen Strichen auf einem gelben und blaugrünen Untergrund handbemalt.\n\nDie erste und letzte" },
	["cu_g3sg1_glade"] = { name = "Waldlichtung", desc = "Ein dichter Wald lichtet sich in der Mitte dieser Sonderlackierung und enthüllt einen strahlend blauen Himmel.\n\n„Dort, eine Waldlichtung!“" },
	["gs_m4a1s_insomnia"] = { name = "Nächtlicher Terror", desc = "Die wirren Gedanken eines an Schlaflosigkeit Leidenden wurden auf diese sonderlackierte A1-S gekritzelt.\n\nNacht für Nacht bin ich um den Schlaf gebracht" },
	["cu_mac10_pixie"] = { name = "Gefangen", desc = "Die Waffe wurde mit einer Sonderlackierung versehen, die in der Waffe gefangene Kobolde zeigt, die unbedingt entkommen wollen.\n\nSie haben noch niemanden getroffen, den sie nicht hereinlegen konnten" },
	["cu_mag7_predictor"] = { name = "Voraussicht", desc = "Diese Sonderlackierung zeigt einen Oktopus, der den Ausgang eines Spiels vorhersagen soll und dabei nach dem „Sieg“-Schild greift.\n\nIch hab' ein gutes Gefühl dabei …" },
	["cu_mp7_fear"] = { name = "Abgründige Erscheinung", desc = "Diese Sonderlackierung stellt verlassene Seelen dar, die in eine Grube voller Albträume fallen.\n\nMan kann seinem Schicksal nicht entkommen" },
	["gs_mp9_starlight"] = { name = "Beschützer des Sternenlichts", desc = "Diese Sonderlackierung zeigt ein Einhorn mit Regenbogenmähne auf weißem Untergrund. Die Waffe wurde mit goldenen Akzenten versehen.\n\nMöge die Gottheit euch behüten, sollte die Gottheit euch erwählen" },
	["am_xm_zombie_offensive"] = { name = "Zombie-Offensive", desc = "Auf die Waffe wurde ein Muster von zombifizierten Counter-Strike-Agenten aufgebracht.\n\n„Untotes Spiel“" },
	["cu_usp_to_hell"] = { name = "Ticket zur Hölle", desc = "Auf die Waffe wurden weiße technische Designs auf einem schwarzen Untergrund gemalt.\n\nTod eines Designers" },
	["gs_mp5_kid_necronomicon"] = { name = "Necro Jr.", desc = "Die Waffe wurde mit einer Sonderlackierung versehen, die märchenhafte Kreaturen und einen Hahn auf dem Schalldämpfer zeigt.\n\nJetzt ist Schluss mit lustig!" },
	["cu_p2k_flying_dream"] = { name = "Gehobene Geister", desc = "Die Waffe wurde mit einer Sonderlackierung versehen, die aus einem Wald emporsteigende Geister zeigt.\n\nViele verlorene Seelen wollen nicht gefunden werden" },
	["sp_scar_chickenfight"] = { name = "Poltergeist", desc = "Diese Waffe wurde mit einem Muster spritzlackiert, das Hühner auf einem Schlachtfeld zeigt.\n\nEin Huhn reitet einen Hai in die Schlacht …" },
	["cu_sawedoff_ouija"] = { name = "Hexenbrett", desc = "Die Waffe wurde mit einer Sonderlackierung versehen, die einem stark abgenutzten Hexenbrett ähnelt.\n\n„Komm schon, ich weiß, dass du es bist, der den Zeiger bewegt …“" },
	["cu_ak47_cogthings"] = { name = "Eisgekohlt", desc = "Die Waffe wurde mit einer Sonderlackierung in einem leuchtend grünen und blauen Farbverlauf versehen.\n\nEine frostige Angelegenheit" },
	["cu_awp_chroma_pink"] = { name = "Chromatische Aberration", desc = "Die Farben in dieser Sonderlackierung wurden versetzt aufgetragen, was einen auffälligen Look erzeugt.\n\n(Nicht so) angenehm für die Augen …" },
	["gs_revolver_purple_elite"] = { name = "Verrückte 8", desc = "Die Waffe wurde mit einer Sonderlackierung mit einem lilafarbenem Lauf und der Zahl 8 auf dem Griff versehen.\n\nInspiriert durch die Ereignisse vom 8. Dezember 2015" },
	["gs_famas_corp_meow"] = { name = "Miau 36", desc = "Die grüne Grundierung der Waffe wurde mit der Zahl 36 verziert.\n\nEigentlich sind es vier Katzen, aber wer zählt schon mit?" },
	["cu_galil_destroyer"] = { name = "Zerstörer", desc = "Die Waffe wurde mit einer Sonderlackierung mit dem Wort „DESTROYER“ (übersetzt „ZERSTÖRER“) versehen, das aber nur aus bestimmten Blickwinkeln gelesen werden kann.\n\nZerstör sie alle" },
	["cu_m249_downvote"] = { name = "Innenstadt", desc = "Die Waffe wurde mit blauen und rosa Pfeilen im Graffiti-Stil besprüht, die in alle möglichen Richtungen zeigen.\n\nEine etwas andere Art von Spray-Muster" },
	["cu_m4a4_elite_tactical"] = { name = "Plastikmagazin", desc = "Diese Sonderlackierung verfügt über ein klares Polymermagazin und einen braunen Schaft, Handschutz und Griff.\n\nStandardverfahren" },
	["cu_mac10_monkeyflage"] = { name = "Affentarnung", desc = "Rote, braune und grüne Affen schmücken diese individuell bemalte MAC-10.\n\nWas für ein Affentheater!" },
	["cu_sg553_cyber_dragon"] = { name = "Drachentechnologie", desc = "Die Waffe wurde mit einer Sonderlackierung mit einem biomechanischen grünen Drachen versehen.\n\nDas ist nicht mein Drache …" },
	["cu_negev_clear_sky"] = { name = "Lass-mich-fallen", desc = "Diese Waffe wurde in der Nähe des oberen Griffs auf einem größtenteils braunen Hintergrund mit einem Stoppschild versehen.\n\nWenn das Stoppschild Sie nicht überzeugt, wird es der Kugelhagel tun" },
	["gs_p250_visions"] = { name = "Visionen", desc = "Die Waffe wurde mit einer Sonderlackierung in leuchtenden Farben versehen.\n\nEs fehlt nur eine Krähe für einen mörderischen Schwarm." },
	["gs_p90_tangled"] = { name = "Vent Rush", desc = "Die Waffe wurde mit einer Sonderlackierung versehen, um einer P90 zu ähneln, die mit Klebeband, Schläuchen und Feuer übersät ist.\n\n„Wissen Sie, wie schnell Sie waren?“" },
	["cu_sawedoff_kisslove"] = { name = "Kuss♥Liebe", desc = "Die Waffe wurde mit einer Sonderlackierung von einer Frau in Rosa und Violett und Sternenaugen versehen.\n\n„Ich akzeptiere kein Nein als Antwort an; ich muss das haben!“" },
	["cu_dual_elites_evil_flora"] = { name = "Flora Carnivora", desc = "Auf jeder Akimbo-Beretta wurden drei bunte, hungrige fleischfressende Pflanzen aufgemalt.\n\nHelfen Sie ihnen, groß und stark zu werden" },
	["gs_ump_roadblock"] = { name = "Straßensperre", desc = "Die Waffe wurde mit einer Sonderlackierung versehen, die einen orangefarbenen horizontalen Warnstreifen mit nach vorne weisenden Pfeilen zeigt.\n\nWenn möglich, bitte wenden" },
	["gs_glock_elite_camo"] = { name = "Winterfest", desc = "Die Waffe wurde mit einer Sonderlackierung eines Wintertarnmusters versehen.\n\nVorsicht, dünnes Eis!" },
	["cu_mag7_insomnia"] = { name = "Schlaflosigkeit", desc = "Die Waffe wurde mit einer Sonderlackierung in Orange und Schwarz versehen, die ein paar lebensmüde Fische zeigt, die in den offenen Mund einer hungrigen Person schwimmen.\n\nSchluck, schluck, schluck" },
	["cu_ak_head_shot_holo"] = { name = "Kopfschuss", desc = "Die Waffe wurde mit einer Sonderlackierung des Graffitos „HEAD SHOT GUN“ versehen und mit einer Perlglanzbeschichtung veredelt.\n\n„Jeder hat seine eigenen Ziele, aber wer ist schon bereit, die nötige Arbeit hineinzustecken, um sie auch zu erreichen?“ – Booth, Waffenhändler" },
	["gs_awp_limbo_snake"] = { name = "Doppelzüngigkeit", desc = "Die Waffe wurde mit einer Sonderlackierung versehen, die eine rote Schlange auf der einen und eine goldene Schlange auf der anderen Seite zeigt.\n\nNicht nur Schlangen sprechen mit gespaltener Zunge" },
	["cu_m4a1s_feeding_frenzy"] = { name = "Emphorosaurus-S", desc = "Die Waffe wurde mit einer Sonderlackierung versehen, die einen Dinosaurier dabei zeigt, wie er sich mit seinen Krallen einen Weg durch das Gehäuse der Waffe bahnt.\n\nFressenszeit!" },
	["cu_p2000_decline"] = { name = "Völlig krank", desc = "Diese Sonderlackierung mit neonfarbenen Motiven soll die Aufmerksamkeit des Betrachters erregen (und den Blick fesseln).\n\nDarf ich um Ihre Aufmerksamkeit bitten?" },
	["cu_ump_clutch_kick"] = { name = "Kreatives Köpfchen", desc = "Über einer Grundierung aus neonfarbenen Streifen zieren diese sonderlackierte UMP-45 einige Skizzen von Kugeln, eine Krone und noch andere handgezeichnete Motive.\n\nDas Konzept ist einfach, nur die Umsetzung ist schwierig" },
	["cu_mp9_superlight"] = { name = "Leichtgewicht", desc = "Die Waffe wurde mit einer Sonderlackierung in schwarzen und weißen Farbtönen versehen, um porös und für den Waffenträger leichter als in Wirklichkeit zu wirken.\n\nJeder Schütze weiß eine leichte Waffe zu schätzen" },
	["gs_scar_fragments_black"] = { name = "Fragmente", desc = "Diese Sonderlackierung in Rotguss erweckt die Illusion, dass die Waffe eine veränderte Geometrie aufweist.\n\nNur ein Fragment Ihrer Vorstellungskraft" },
	["cu_glock_moon_rabbit"] = { name = "Mondschattenhase", desc = "Die Waffe wurde mit einer Sonderlackierung versehen, die eine traumhafte Szene mit einem mystisch anmutenden Hasen zeigt, der vor dem Vollmond durch die Landschaft hoppelt.\n\nSpringen Sie über Ihren Schatten" },
	["cu_m4a4_temukau"] = { name = "Temukau", desc = "Die Waffe wurde mit einer Sonderlackierung versehen, die eine AT-Agentin im Anime-Stil zeigt, die sich auf den Ansturm der Terroristen vorbereitet.\n\n„Haltet die Stellung!“" },
	["cu_mac10_sakkaku"] = { name = "Sakkaku", desc = "Betrachtet man diese sonderlackierte MAC-10 im rechten Licht, leuchten die Augen des abgebildeten Mädchens rot auf.\n\nLass deine Wahrnehmung zu ihrer Realität werden" },
	["cu_p250_rebuilt"] = { name = "Neu.Erschaffung", desc = "Aneinandergereihte Dreiecke und andere schlichte, minimalistische Muster zieren diese sonderlackierte P250.\n\nAller guten Dinge sind … neun?" },
	["cu_mp5sd_quick_liquidation"] = { name = "Liquidierung", desc = "Die dunkle Grundierung und die sorgfältig ausgewählten roten und blauen Akzente lassen diese sonderlackierte MP5-SD bei entsprechendem Lichteinfall aufleuchten.\n\nUm stille, aber tödliche Akzente zu setzen" },
	["gs_r8_banana"] = { name = "Bananenballermann", desc = "Die Waffe wurde mit einer Sonderlackierung versehen, die einer äußerst reifen Banane ähnelt.\n\nVorsicht, Rutschgefahr!" },
	["gs_p90_neoqueen"] = { name = "Neokönigin", desc = "Die Waffe wurde mit einer Sonderlackierung versehen, die verschiedene Militär- und Sciencefiction-Motive in sich vereint.\n\nPerfekt dafür geeignet, wenn man es mal eilig hat" },
	["cu_sg553_cyberforce"] = { name = "Cyberforce", desc = "Die Waffe wurde mit einer Sonderlackierung versehen, die das Logo der CYBERFORCE INC über einer rot-schwarzen Grundierung zeigt.\n\n„CYBERFORCE INC: Wir sichern Ihre Zukunft, damit Sie es nicht müssen!“" },
	["cu_tec9_freedom"] = { name = "Rebell", desc = "Diese sonderlackierte Tec-9 wurde mit locker ineinander übergehenden Graffitimotiven in matten Farbtönen versehen.\n\nEin Schuss, ein Treffer, eine Runde nach der anderen" },
	["gs_revolver_acanthus_bronze"] = { name = "Intarsie", desc = "Die Waffe wurde mit einer Sonderlackierung versehen, die Akanthuswerk mit einem Griff aus Marmor ähneln soll.\n\nNoch nie war Opulenz derart zum Greifen nah" },
	["gs_ak47_strone"] = { name = "Stahldelta", desc = "Die Linien auf dieser Sonderlackierung ähneln den Gewässerverläufen auf einer topografischen Karte.\n\nBlut ist dicker als Wasser" },
	["gs_awp_strone"] = { name = "Schwarzer Nil", desc = "Die Linien auf dieser Sonderlackierung ähneln den Gewässerverläufen auf einer topografischen Karte.\n\nBlut ist dicker als Wasser" },
	["cu_glock_ramzes"] = { name = "Ramses’ Reich", desc = "Der Schlitten dieser sonderlackierten Waffe wurde mit einer Abbildung des Nils versehen und der Handgriff zeigt Pharao Ramses I., den Begründer der 19. Dynastie des alten Ägyptens.\n\n„Mein Volk verlangt nach seinen Göttern, und ich bin es, der es zu ihnen führt“" },
	["cu_famas_holo_ocean"] = { name = "Wasser der Nephthys", desc = "Die Blautöne dieser sonderlackierten FAMAS verändern sich bei unterschiedlicher Lichteinstrahlung.\n\nMit allen Wassern gewaschen" },
	["cu_m249_scarab"] = { name = "Überschwemmung", desc = "Die Waffe wurde mit einer Sonderlackierung in türkisfarben Wüstentarnfarben versehen, wobei die charakteristische Landschaft als tiefere Inspiration diente.\n\n„MAN HAT UNS REINGELEGT! SIE SIND EINFACH ÜBERALL!“" },
	["gs_m4a1s_milspec"] = { name = "Schlammtarnung", desc = "Die Waffe wurde mit einer Sonderlackierung in schwarzen und braunen Tarnfarben versehen.\n\nVerstärkung ist eingetroffen" },
	["gs_m4a4_ra"] = { name = "Auge des Horus", desc = "Die Waffe wurde mit einer Sonderlackierung in Form einer weißen Grundierung, goldenen Akzenten und dem ägyptischen Falkengott Horus versehen.\n\nFliege höher als alle anderen" },
	["cu_mac10_scarab"] = { name = "Singende Sande", desc = "Die Waffe wurde mit einer Sonderlackierung in Wüstentarnfarben versehen, wobei die charakteristische Landschaft als tiefere Inspiration diente. Ein Skarabäus wurde unter der ausklappbaren Schulterstütze aufgemalt.\n\n„MAN HAT UNS REINGELEGT! SIE SIND EINFACH ÜBERALL!“" },
