	["aq_steel"] = { name = "aq_steel", desc = "Die Waffe ist noch einwandfrei funktionsfähig, obwohl die äußere Schicht gerostet ist.\n\nManche Menschen müssen ihre dunklen Absichten nicht verstecken" },
