	["aq_famas_contour"] = { name = "aq_famas_contour", desc = "Die Waffe wurde mit einer Sonderlackierung in einem modernen roten und grauen Design versehen, das die Waffenform ergänzen soll.\n\nZusammen sind wir immer stärker" },
