	["ump45_neo_noir"] = { name = "ump45_neo_noir", desc = "Die Waffe wurde mit einer Sonderlackierung mit einer stilisierten blau-magentafarbenen Frau auf einem grauen Hintergrund versehen.\n\n„Sie nahm das Messer in die Hand und wusste, es ist Zeit, sich zur Wehr zu setzen.“" },
