text = {
	["Default"] = { name = "-", desc = "Kit de pintura sin nombre" },
	["so_yellow"] = { name = "Buldócer", desc = "Se han pintado algunas partes con espray de colores sólidos en tonos amarillos estilo cadena de montaje.\n\nEste rompehuesos es devastador." },
	["so_olive"] = { name = "Agua subterránea", desc = "Se han pintado algunas partes con espray en colores sólidos de tonos verde militar." },
	["so_red"] = { name = "Manzana de caramelo", desc = "Se han pintado algunas partes con espray de colores sólidos en tonos rojo y negro.\n\nIdeal en coches, perfecto en armas." },
	["so_purple"] = { name = "Ultravioleta", desc = "Se han pintado algunas partes con espray de colores sólidos en tonos negro y púrpura.\n\nUn diseño elegante unido a un propósito brutal." },
	["so_night"] = { name = "Noche cerrada", desc = "Se han pintado algunas partes con espray de colores sólidos en tonos oscuros.\n\nEs el susurro del viento... Es la mordedura en tu cuello." },
	["so_caramel"] = { name = "Caramelo", desc = "Se han pintado algunas partes con espray de colores sólidos en tonos caramelo y negro." },
	["so_grassland"] = { name = "Pradera", desc = "Se han pintado algunas partes con espray de colores sólidos en tonos verde pradera." },
	["so_moss"] = { name = "Pícea azul", desc = "Se han pintado algunas partes con espray de colores sólidos en tonos musgo." },
	["so_sand"] = { name = "Duna de arena", desc = "Se han pintado algunas partes con espray de colores sólidos en tonos arena.\n\nCon Turner muerto, tenemos una nueva prioridad: rescatar a Alex Kincaide —Felix Riley (oficial al mando)" },
	["so_stormfront"] = { name = "Tormenta", desc = "Se han pintado algunas partes con espray de colores sólidos en tonos fríos.\n\nTe estará esperando en el monorraíl." },
	["so_tornado"] = { name = "Tornado", desc = "Se han pintado algunas partes con espray de colores tornado sólidos.\n\nNo te dejes engañar por su sencillez." },
	["so_whiteout"] = { name = "Níveo", desc = "Se ha pintado completamente de blanco con espray.\n\nParece puro, pero las apariencias engañan." },
	["so_jungle"] = { name = "Jungla", desc = "Se han pintado algunas partes con espray de colores sólidos en tonos verde jungla." },
	["so_space_marine"] = { name = "Militar", desc = "Se han pintado algunas partes con espray de colores sólidos en tonos caqui y gris." },
	["so_pmc"] = { name = "Contratista", desc = "Se han pintado algunas partes con espray de colores sólidos en tonos tostado, azul marino y verde oscuro.\n\nRona Sabri aún no ha perdonado a Sebastien por no elegirla a ella para ir tras Turner." },
	["so_tangerine"] = { name = "Demolición", desc = "Se han pintado algunas partes con espray de colores sólidos en tonos negro y mandarina.\n\nRona es tan buena como ella piensa... solo que no es lo suficientemente mayor para darse cuenta de que no debería hacérselo notar a todo el mundo —Felix Riley (oficial al mando)." },
	["so_orange_accents"] = { name = "Nitro", desc = "so_orange_accents" },
	["cu_broken_path_famas"] = { name = "Fosfeno", desc = "Se ha personalizado con tiras de cintas rotas." },
	["cu_bullet_rain_m4a1"] = { name = "Lluvia de balas", desc = "Se ha personalizado con un gráfico de nubes con lluvia de balas." },
	["cu_catskulls_p90"] = { name = "Muerte por gatito", desc = "Se ha grabado mediante hidrosumersión un gráfico de calaveras de gatos superpuestas." },
	["CSGO_Doomkitty"] = { name = "Perdición gatuna", desc = "CSGO_Doomkitty" },
	["hy_ddpat"] = { name = "DDPAT bosque", desc = "Se ha pintado usando un patrón disruptivo digital (DDPAT) hidrográfico.\n\nPara cuando estás lo suficientemente cerca como para notar los píxeles, ya es demasiado tarde." },
	["hy_ddpat_urb"] = { name = "DDPAT urbano", desc = "hy_ddpat_urb" },
	["hy_ddpat_jungle"] = { name = "DDPAT jungla", desc = "hy_ddpat_jungle" },
	["hy_ddpat_orange"] = { name = "DDPAT naranja", desc = "hy_ddpat_orange" },
	["hy_ddpat_pink"] = { name = "DDPAT rosa", desc = "hy_ddpat_pink" },
	["hy_ddpat_desert"] = { name = "DDPAT del desierto", desc = "hy_ddpat_desert" },
	["hy_arctic"] = { name = "Camuflaje ártico", desc = "Se ha pintado usando una lámina hidrográfica de camuflaje ártico.\n\nLa nieve es fría; la muerte, aún más." },
	["hy_arctic_contrast"] = { name = "Camuflaje Polar", desc = "hy_arctic_contrast" },
	["hy_arctic_glacier"] = { name = "Camuflaje Glaciar Ártico", desc = "hy_arctic_glacier" },
	["hy_dive"] = { name = "Camuflaje submarino", desc = "Se le ha aplicado un adhesivo hidrográfico con patrón de camuflaje.\n\nEl nuestro es un mundo de intriga... pero a veces el camuflaje está destinado a ser visto —Booth (traficante de armas)" },
	["hy_ocean"] = { name = "Bright Water", desc = "hy_ocean" },
	["hy_forest"] = { name = "Camuflaje barnizado", desc = "Se ha pintado usando un camuflaje forestal hidrográfico.\n\nEl bosque puede ser un lugar peligroso... Nunca viajes solo" },
	["hy_forest_winter"] = { name = "Bosque invernal", desc = "hy_forest_winter" },
