	["hy_forest_night"] = { name = "Bosque nocturno", desc = "hy_forest_night" },
	["hy_desert"] = { name = "Tormenta del desierto", desc = "Se ha pintado usando un camuflaje desértico hidrográfico." },
	["hy_tiger"] = { name = "Tigre de Bengala", desc = "Se le ha aplicado un adhesivo hidrográfico con rayas de tigre." },
	["hy_copperhead"] = { name = "Cabeza de cobre", desc = "Se ha pintado usando un patrón de piel de víbora hidrográfico." },
	["hy_skulls"] = { name = "Calaveras", desc = "Se ha pintado usando un patrón de calaveras hidrográfico con detalles rojos o negros.\n\nUna fosa común que cabe perfectamente en la palma de tu mano	" },
	["hy_webs"] = { name = "Tela escarlata", desc = "Se ha pintado usando un patrón de telaraña hidrográfico sobre una capa roja y está barnizada con una capa superior satinada.\n\nTen cuidado por dónde andas, uno nunca sabe hasta dónde se extiende la telaraña" },
	["hy_splatter"] = { name = "Racha azul", desc = "Se le ha aplicado un adhesivo hidrográfico con un patrón de salpicadura sobre una capa base de azul agua." },
	["hy_ak47lam"] = { name = "Laminado rojo", desc = "Se le ha aplicado un laminado predeterminado." },
