	["hy_ak47lam_blue"] = { name = "Laminado azul", desc = "hy_ak47lam_blue" },
	["hy_gelpen"] = { name = "Ley del revólver", desc = "Se le ha aplicado un adhesivo hidrográfico de un garabato hecho con un permanente." },
	["hy_v_tiger"] = { name = "Tigre de la jungla", desc = "Se le ha aplicado un adhesivo hidrográfico atigrado de tonos verdes." },
	["hy_redtiger"] = { name = "Sangre de tigre", desc = "hy_redtiger" },
