	["sp_spray"] = { name = "Espray de contraste", desc = "Se ha pintado con espray a mano alzada usando líneas cortas y gruesas de colores que contrastan.\n\nPerfecto para el insurgente en movimiento." },
	["sp_spray_sage"] = { name = "Espray de salvia", desc = "sp_spray_sage" },
	["sp_spray_jungle"] = { name = "Espray de jungla", desc = "sp_spray_jungle" },
	["sp_spray_sand"] = { name = "Espray de arena", desc = "sp_spray_sand" },
