	["sp_tape_dots"] = { name = "Estación seca", desc = "Se ha pintado con espray usando tiras de cinta adhesiva rasgadas y metal perforado como plantillas." },
	["sp_tape_dots_urban"] = { name = "Perforación urbana", desc = "sp_tape_dots_urban" },
	["sp_tape_dots_waves"] = { name = "Olas perforadas", desc = "sp_tape_dots_waves" },
	["sp_leaves"] = { name = "Hojas del bosque", desc = "Se ha pintado con espray usando hojas como plantillas." },
	["sp_leaves_grassland"] = { name = "Hojas de pradera", desc = "sp_leaves_grassland" },
	["sp_short_tape"] = { name = "Liquen punteado", desc = "Se ha pintado con espray usando pequeños trozos de cinta adhesiva como plantillas." },
	["sp_short_tape_jungle"] = { name = "Punteado de jungla", desc = "sp_short_tape_jungle" },
