	["sp_tape"] = { name = "Máscara ósea", desc = "Se ha pintado con espray usando una cinta de pintor enredada como plantilla.\n\nEl verdadero poder se demuestra con sutileza." },
	["sp_tape_orange"] = { name = "Piel de naranja", desc = "sp_tape_orange" },
	["sp_tape_urban"] = { name = "Pátina urbana", desc = "sp_tape_urban" },
	["sp_mesh"] = { name = "Madera a la deriva", desc = "Se ha pintado con espray usando malla metálica y recortes de cartón como plantillas.\n\nUn depredador es un depredador, sin importar el entorno." },
	["sp_mesh_tan"] = { name = "Malla de safari", desc = "sp_mesh_tan" },
	["sp_mesh_arctic_contrast"] = { name = "Malla polar", desc = "sp_mesh_arctic_contrast" },
	["sp_mesh_fire"] = { name = "Condena", desc = "sp_mesh_fire" },
	["sp_mesh_glacier"] = { name = "Malla glaciar", desc = "sp_mesh_glacier" },
	["sp_mesh_sand"] = { name = "Malla de arena", desc = "sp_mesh_sand" },
	["sp_snake"] = { name = "Camuflaje serpentino", desc = "Se ha pintado con espray usando una plantilla de piel de serpiente en parches que se han perfilado a mano alzada." },
	["an_silver"] = { name = "Plateada", desc = "Se ha pintado con una base de cromo y decorado con un efecto de pintura anodizada plateado transparente.\n\nLa sencillez es bella —Franz Kriegeld (estratega de Phoenix)" },
	["an_red"] = { name = "Hot rod", desc = "Se ha pintado con una base de cromo y decorado con un efecto de pintura anodizada rojo transparente.\n\nAutomático. Sistemático. Hidromático." },
	["an_gunmetal"] = { name = "Bronce anodizado", desc = "an_gunmetal" },
	["an_navy"] = { name = "Azul marino anodizado", desc = "Se ha pintado con una base de cromo y decorado con un efecto anodizado azul marino transparente." },
	["am_urban"] = { name = "DDPAT metálico", desc = "Se ha pintado usando un patrón disruptivo digital hidrográfico sobre una capa base metálica." },
	["am_tiger"] = { name = "Tigre dorado", desc = "Se ha chapado en oro y grabado un patrón de rayas de tigre a mano." },
	["am_dragon_glock"] = { name = "Tatuaje de dragón", desc = "Se ha pintado usando un adhesivo de un dragón sobre una base de capa metálica.\n\nEn un cuento de hadas el caballero siempre mata al dragón... pero este es el mundo real —Valeria Jenner (revolucionaria)" },
	["am_caustics"] = { name = "Cáustico", desc = "Se le ha aplicado un adhesivo hidrográfico rojo y blanco sobre una capa base de carboncillo metalizado." },
	["am_lightning_awp"] = { name = "Relámpago", desc = "Se ha pintado un relámpago usando un efecto anodizante sobre una capa base metálica.\n\nEn ocasiones no es necesario atacar dos veces el mismo lugar" },
	["am_ossify"] = { name = "Osificado", desc = "Se le ha aplicado un adhesivo hidrográfico verde lima abstracto transparente sobre una capa base metálica." },
	["aa_flames"] = { name = "Llamarada", desc = "Se ha pintado aerografiando trazados transparentes sobre una capa base de cromo." },
	["aa_splash_p250"] = { name = "Salpicadura", desc = "Se ha pintado aerografiando trazados transparentes utilizando una plantilla sobre una capa base de cromo." },
	["aa_fade"] = { name = "Desteñido", desc = "Se ha pintado aerografiando trazados transparentes desteñidos sobre una capa base de cromo.\n\nEsto no es solo un arma, es un tema de conversación. —Imogen (traficante de armas en prácticas)" },
	["aa_fade_metallic"] = { name = "Ámbar desteñido", desc = "aa_fade_metallic" },
	["aa_fade_grassland"] = { name = "Ácido desteñido", desc = "aa_fade_grassland" },
	["aq_copper"] = { name = "Cobrizo", desc = "Se ha chapado en cobre." },
	["aq_blued"] = { name = "Azul metalizado", desc = "Se ha dejado en un acabado azul profundo.\n\nEste es el malbec del diseño de armas —Booth (traficante de armas)" },
	["aq_forced"] = { name = "Manchado", desc = "Se le ha aplicado una capa de óxido a propósito derramando gotas de limón y mostaza sobre su superficie.\n\nSi piensas que es confuso ahora, espera a verlo bajo una luz negra" },
	["aq_oiled"] = { name = "Acero templado", desc = "Se ha cementado el color mediante la aplicación de carbón vegetal a altas temperaturas.\n\nUn poco de color no le hace daño a nadie" },
	["sp_spitfire_famas"] = { name = "Escupefuego", desc = "sp_spitfire_famas" },
	["am_zebra"] = { name = "Masacre", desc = "Se ha pintado con un patrón de franjas en forma de cebra con pinturas de aluminio y cromo y con varios componentes reflectantes. Además, se ha cubierto con una capa de color rojo tomate.\n\nValeria no paga a Javier para que haga preguntas... Le paga para sacar las respuestas por la fuerza" },
	["am_zebra_dark"] = { name = "Aguas profundas", desc = "Se ha pintado con un patrón de bandas de tonos marinos y carboncillo metalizado de distintas gamas sobre una capa base de cromo.\n\nEl agua da la vida, pero también puede arrebatarla..." },
	["aa_vertigo"] = { name = "Hipnótico", desc = "Se le ha aplicado una capa base de color perla y luego se ha pintado con espray utilizando una plantilla con un patrón gráfico.\n\nNo te estás durmiendo, estás perdiendo el conocimiento debido a la pérdida de sangre." },
	["cu_spring_nova"] = { name = "Madera florecida", desc = "Se ha aerografiado a mano con un motivo de flores de cerezo cubierto por una capa de esmalte brillante." },
	["sp_hazard"] = { name = "Amenaza", desc = "sp_hazard" },
	["aq_rust"] = { name = "Oxidado", desc = "Pese a que la superficie se encuentra oxidada, esta arma de fuego está todavía perfectamente operativa." },
	["am_slither_p90"] = { name = "Sangre fría", desc = "Se ha pintado con una base de cromo y decorado con un efecto de pintura anodizada rojo transparente. Luego se ha grabado a láser un dibujo de una serpiente deslizándose." },
	["am_carbon_fiber"] = { name = "Fibra de carbono", desc = "Se le ha aplicado un adhesivo hidrográfico de fibra de carbono sobre una capa base de grafito.\n\nSolo porque llevase a mi equipo no significa que no fuese un trabajo en equipo —Rona Sabri (joven promesa)" },
	["am_scorpion_p2000"] = { name = "Escorpión", desc = "Se ha pintado haciendo uso de efectos de pintura anodizada en un patrón de ondas desteñidas con una calcomanía de un escorpión rojo en la empuñadura." },
	["hy_feathers_aug"] = { name = "Alas", desc = "Se ha decorado con pegatinas de plumas de águila.\n\nUn águila se ve majestuosa hasta que te arranca los ojos con las garras" },
	["sp_palm"] = { name = "Palmera", desc = "Se ha pintado con espray usando recortes de cartón, tela metálica fina y hojas de palmera como plantilla." },
	["sp_palm_shadow"] = { name = "Tormenta tropical", desc = "sp_palm_shadow" },
	["cu_walnut_nova"] = { name = "Nuez", desc = "Tiene una culata de madera de nogal." },
	["aq_brass"] = { name = "Latón", desc = "Algunas piezas del arma han sido sustituidas por latón.\n\nEl oro de los tontos." },
	["hy_blam"] = { name = "¡BOOOOM!", desc = "Se le ha aplicado un adhesivo hidrográfico con una onomatopeya de un cómic sobre una base cubierta de naranja." },
	["hy_blam_simple"] = { name = "BUM", desc = "hy_blam_simple" },
	["sp_dapple"] = { name = "Chamuscado", desc = "Se ha pintado con espray siguiendo un patrón de sol moteado.\n\nEl Fénix no es un símbolo de destrucción... sino de renacimiento. —Valeria Jenner (revolucionaria)." },
	["sp_splash_p250"] = { name = "Salpicado", desc = "Se ha pintado con espray y decorado usando una plantilla en forma de salpicadura." },
	["hy_hunter_modern"] = { name = "Cazador moderno", desc = "Se ha pintado usando una fotografía hidrográfica a modo de camuflaje de caza." },
	["hy_hunter_blaze_pink"] = { name = "Salpicadura concentrada", desc = "hy_hunter_blaze_pink" },
	["hy_hunter_blaze_orange"] = { name = "Llamarada naranja", desc = "hy_hunter_blaze_orange" },
	["sp_nukestripe_brown"] = { name = "Alerta de irradiación", desc = "sp_nukestripe_brown" },
	["sp_nukestripe_maroon"] = { name = "Advertencia de catástrofe", desc = "sp_nukestripe_maroon" },
	["sp_nukestripe_orange"] = { name = "Peligro de radiación", desc = "sp_nukestripe_orange" },
	["sp_nukestripe_green"] = { name = "Amenaza nuclear", desc = "sp_nukestripe_green" },
	["sp_zebracam"] = { name = "Depredador", desc = "Se ha pintado con espray usando un patrón de rayas de cebra." },
	["sp_zebracam_bw"] = { name = "Cebra desteñida", desc = "sp_zebracam_bw" },
	["sp_zebracam_blue"] = { name = "Cebra azur", desc = "sp_zebracam_blue" },
	["CSGO_Camo"] = { name = "Camuflaje GO", desc = "Se le ha aplicado un adhesivo hidrográfico con el patrón de camuflaje de Global Offensive." },
	["CSGO_Icosahedron"] = { name = "Recuerdo", desc = "Se le ha aplicado un adhesivo hidrográfico geométrico." },
	["cu_fireserpent_ak47_bravo"] = { name = "Serpiente de fuego", desc = "Este recuerdo de Ruins lleva pintado un dibujo de una serpiente de fuego.\n\nSi quieres sobrevivir en las calles, aprende a escupir fuego." },
	["cu_favela"] = { name = "Alcornoque", desc = "Se ha pintado con un motivo de alcornoque." },
	["cu_dragon_p90_bravo"] = { name = "Dragón esmeralda", desc = "Este recuerdo de Chinatown lleva pintado un dibujo de un dragón.\n\nLa tradición puede ser peligrosa." },
	["hy_siege_bravo"] = { name = "Sobrecrecido", desc = "Este recuerdo de Siege se ha pintado usando un hidrográfico de camuflaje.\n\nMírame a los ojos y dime que eso es un asunto común —Felix Riley (oficial al mando)." },
	["am_scales_bravo"] = { name = "Koi dorado", desc = "Este recuerdo de Chinatown se ha recubierto de una lámina metálica con un patrón de escamas.\n\nUn arma estridente en todos los sentidos." },
	["aq_etched_mac10_bravo"] = { name = "Grabado", desc = "Este recuerdo de Gwalior lleva piezas de latón grabadas.\n\nIncumple dos mandamientos a la vez." },
	["cu_season_elites_bravo"] = { name = "Limba negra", desc = "Estos recuerdos de Agency tienen una pátina oscura y empuñaduras de madera de limba negra.\n\nCuando la construcción clásica se junta con la estética moderna." },
	["hy_seaside_bravo"] = { name = "Tempestad", desc = "Este recuerdo de Seaside lleva un adhesivo hidrográfico de una ola y una nube.\n\nSe avecina una tormenta." },
	["hy_crumple_dark_bravo"] = { name = "Facetas", desc = "hy_crumple_dark_bravo" },
	["hy_crumple_bravo"] = { name = "Desmenuzado", desc = "Este recuerdo de Agency lleva un adhesivo hidrográfico poligonal.\n\nMis hombres son profesionales, harán bien el trabajo... —Elliott Kingsman (criminal de carrera)." },
	["hy_bluepolygon_bravo"] = { name = "Demeter", desc = "hy_bluepolygon_bravo" },
	["hy_ali_tile_bravo"] = { name = "Mosaico", desc = "hy_ali_tile_bravo" },
	["an_emerald_bravo"] = { name = "Emerald", desc = "Este recuerdo de Chinatown lleva una capa base de cromo y se ha decorado con un efecto de pintura anodizada esmeralda transparente.\n\n¿A esto ha quedado reducido el gran Sebastien Hennequet? ¿Una maestra de escuela gruñona? ¿Que ha pasado contigo? —El Maestro y El Iconoclasta, segunda parte." },
	["hy_mayan_dreams_bravo"] = { name = "Sueños mayas", desc = "Este recuerdo de Ruins lleva un hidrográfico de una nube maya.\n\nA veces se requiere un sacrificio." },
	["am_ossify_blue"] = { name = "Espuma oceánica", desc = "Se le ha aplicado un adhesivo hidrográfico azul abstracto y transparente sobre una capa base metálica." },
	["am_crumple"] = { name = "Grafito", desc = "Se ha pintado con un patrón poligonal de tonos metalizados de distintas gamas sobre una capa base de cromo." },
	["sp_skull_diagram_bravo"] = { name = "Pila de huesos", desc = "Este recuerdo de Cache se ha pintado con calaveras sobre un diagrama de cableado.\n\nUno más para la colección." },
	["sp_star_bravo"] = { name = "Zirka", desc = "Este recuerdo de Cache se ha pintado con el dibujo de una estrella.\n\nImogen comparte con su padre el buen ojo para la calidad, pero su ambición es aún más feroz." },
	["aq_steel_bravo"] = { name = "Capa de herrumbre", desc = "Este recuerdo de Siege sigue perfectamente operativo aunque las superficies exteriores se hayan oxidado.\n\nLas cicatrices forjan el carácter." },
	["cu_xray_m4"] = { name = "Rayos X", desc = "Un M4 personalizado que muestra su interior con rayos X." },
	["cu_xray_p250"] = { name = "Rayos X", desc = "Una P250 personalizada que muestra su interior con rayos X." },
	["an_titanium30v"] = { name = "Titanio azul", desc = "Está compuesta por partes de titanio de tonos azulados, característicos de una capa de óxido lograda por medio de una anodización controlada a 30 voltios." },
	["hy_bluehex"] = { name = "Hexagonal", desc = "hy_bluehex" },
	["hy_redhex"] = { name = "Colmena", desc = "hy_redhex" },
