	["cu_pinstripe_ak47"] = { name = "Rayado esmeralda", desc = "Se le ha aplicado una lámina de madera de roble y ha sido pintado con rayas verdes y blancas.\n\nCuestionar a Imogen es cuestionarme a mí. Ahora relájate, tómate un trago de whisky y hablemos de negocios ——El Padre y El Fénix, segunda parte" },
	["sp_nightstripe"] = { name = "Franja nocturna", desc = "Se ha pintado con espray usando un patrón de cinta de carrocero en una gama de colores nocturna.\n\nRocía... Reza... Pinta... Apuñala." },
	["cu_m4_asimov"] = { name = "Asiimov", desc = "Se ha personalizado con un diseño de ciencia ficción.\n\nCualquiera puede predecir el futuro. Un visionario le da forma." },
