	["am_thorny_rose_mp9"] = { name = "Rosa de hierro", desc = "Se ha pintado a mano el dibujo de una rosa con pinturas metalizadas de distintos colores.\n\nCuidado con las espinas" },
	["cu_skull_nova"] = { name = "Calavera naciente", desc = "Se ha personalizado con la imagen de una calavera usando pinturas mate y un marcador de pintura de óleo.\n\nSr. Izaki, no tengo objeción... ¿pero cuántas escopetas necesita? —Imogen y El Oni, primera parte" },
