	["am_royal"] = { name = "Cáliz", desc = "Se ha pintado con una base de cromo y decorado con un efecto de pintura anodizada azul real y ámbar transparente.\n\nHas elegido... sabiamente." },
	["am_metals"] = { name = "Caballero", desc = "Tiene partes de latón incrustadas con una temática de armadura renacentista.\n\n¿Qué significa que no puedes encontrar a Cocinero y a Izaki? —Felix Riley (oficial al mando)" },
	["am_chainmail"] = { name = "Cota de malla", desc = "Se le ha aplicado un adhesivo hidrográfico semitransparente con un patrón de cota de malla sobre una capa base plateada.\n\nDe todos los miembros de la Phoenix, ninguno es más leal a Valeria que su guardaespaldas Naomi." },
	["aq_handcannon"] = { name = "Cañón portátil", desc = "Tiene partes de latón a las que se le ha echado una pátina de ácido inspirado en antiguos cañones de latón.\n\nPrepárate para encontrarte con muchos contratiempos en las pruebas que están por venir" },
	["am_metal_inlay"] = { name = "Edad oscura", desc = "Se ha pintado usando tonos metalizados para dar la impresión de que se han incrustado metales con un motivo medieval.\n\nPodría matar a su amante... quizás eso te haría ganar algo de tiempo para estar con Valeria —Un amor de padre, segunda parte" },
	["hy_vines"] = { name = "Zarza", desc = "Se ha aplicado un adhesivo hidrográfico de enredaderas y flores.\n\nEl beso del amor verdadero no te salvará de sus espinas" },
	["so_indigo"] = { name = "Azul real", desc = "Se ha pintado con colores sólidos en tonalidades índigo, carmesí y gris.\n\nDeberías sonreír Felix, tendrías más suerte con las damas si no parecieras un cabrón adusto todo el tiempo. —Imogen (traficante de armas en prácticas)" },
	["so_indigo_and_grey"] = { name = "Índigo", desc = "Se ha pintado con colores sólidos en tonalidades índigo, azul y gris.\n\nAlex Kincaide, estoy seguro de que tienes muchas preguntas... Por favor, permíteme que te proporcione respuestas —El Fénix y El Contador de Historias, primera parte" },
	["cu_well_traveled_ak47"] = { name = "Jet set", desc = "Su empuñadura se ha recubierto de piel marrón. Esta arma ha estado en un montón de ciudades.\n\nCualquiera que preste la suficiente atención atará los cabos y verá lo que ella hizo. No tenemos mucho tiempo para encontrar una solución... —Un amor de padre, primera parte" },
	["cu_green_leather"] = { name = "Primera clase", desc = "Su empuñadura se ha recubierto de piel verde.\n\nMimosa no incluida" },
	["cu_brown_leather_p90"] = { name = "Piel", desc = "Su empuñadura se ha recubierto de piel marrón.\n\nNo te preocupes, la seguridad cumple con los estándares de la TSA" },
	["cu_luggage_mac10"] = { name = "Hombre de negocios", desc = "Se le ha aplicado un adhesivo hidrográfico de tartán y se ha recubierto parcialmente de imitación de piel de cocodrilo.\n\nHan mordido el anzuelo, pasemos a la acción... —Elliott Kingsman (mercenario)" },
	["cu_medieval_dragon_awp"] = { name = "Tradición del dragón", desc = "Se ha personalizado con un dragón celta.\n\nNi 200 llaves podrían revelar sus secretos" },
	["cu_luggage_p2000"] = { name = "Clase turista", desc = "Se le ha aplicado un adhesivo hidrográfico de tartán y partes de la empuñadura se han recubierto de piel marrón.\n\nNo es muy lujoso, pero te lleva a donde necesitas ir" },
	["aq_pilot_deagle"] = { name = "Piloto", desc = "Tiene una incrustación de latón con alas de piloto.\n\nAdopten posiciones de colisión" },
	["cu_leather_xm1014"] = { name = "Piel roja", desc = "Su empuñadura y demás detalles se han recubierto de piel roja.\n\nHaz las maletas. Vamos a matar a ese hijo de puta... —El Oni y La Valquiria, segunda parte" },
	["cu_luggage_sg553"] = { name = "Viajero", desc = "Se ha personalizado con un diseño a rayas inspirado en el equipaje de la época, con algunas partes recubiertas de piel marrón.\n\nTodo el mundo tiene una historia, y Javier te la sacará a la fuerza antes de matarte" },
	["cu_luggage_usp-s"] = { name = "Clase preferente", desc = "Se ha personalizado con colores y patrones inspirados en el equipaje de la época, con algunas partes recubiertas de piel marrón.\n\nAlgunas personas no quieren solo espacio para estirar las piernas... Quieren paz y tranquilidad" },
	["hy_plaid1"] = { name = "Tartán oliva", desc = "Se le ha aplicado un adhesivo hidrográfico de tartán en tonos beis y verde oliva.\n\nMe gusta. Resalta mis ojos en la cámara... —Rona Sabri (joven promesa)" },
