	["cu_tribute_ak47"] = { name = "Rebelde del yermo", desc = "Se ha envejecido, xilografiado y pintado con grafiti.\n\nUn día se preguntarán por qué luchamos, y cuando lo hagan lo entenderán —Naomi (guardaespaldas de Valeria Jenner)" },
	["aq_glock_coiled"] = { name = "Afilado", desc = "La cubierta de acero se ha pintado combinando el uso de plantillas con el pintado a mano utilizando pinturas al óleo.\n\nLa única forma de hacer diamantes es aplicando presión" },
	["am_g3sg1_murky"] = { name = "Turbio", desc = "Se ha anodizado y luego pintado con un adhesivo hidrográfico de manchas de leopardo.\n\nSiempre hay algún lugar más oscuro al que puedes ir" },
	["aq_m4a1s_basilisk"] = { name = "Basilisco", desc = "Se ha grabado con un basilisco.\n\nEs de mala educación mirar fijamente, pero la guerra no es momento para galanterías" },
	["cu_m4a4_griffin"] = { name = "Grifo", desc = "Se ha pintado a mano con la ilustración de un grifo.\n\nFelix eligió a su equipo de entre agentes de operaciones de todo el mundo... y aunque la gente cuestiona sus personalidades, nunca cuestiona los resultados" },
	["sp_mag7_firebitten"] = { name = "Incendiario", desc = "Se ha pintado con espray en capas y a continuación envejecido para dar la apariencia de bordes en llamas.\n\nSiempre empieza con una pequeña chispa." },
	["cu_mp9_chevron"] = { name = "Dardo", desc = "Se le ha aplicado un adhesivo hidrográfico a rayas siguiendo un patrón de galones.\n\nLánzales los suficientes y al final darás en la diana" },
	["aq_p250_cartel"] = { name = "Cartel", desc = "Ha sido grabado con esqueletos, flores y serpientes.\n\nUna pequeña intimidación que convierte cada mañana en el Día de los Muertos" },
	["cu_p2000_fire_elemental"] = { name = "Elemental de fuego", desc = "Se ha personalizado con la imagen de una criatura de fuego en un estilo gráfico.\n\nA veces jugar con fuego es algo bueno" },
	["aq_sawedoff_blackgold"] = { name = "Bandolero", desc = "Se ha lacado en negro y oro usando un motivo de cardo.\n\nNo es necesario extender tu meñique al apretar el gatillo, pero eso le da un toque interesante" },
	["cu_scar20_intervention"] = { name = "Cardíaco", desc = "Se le ha aplicado una envoltura termosoldada con vinilo preimpreso en un moderno diseño naranja y azul.\n\nPii. Pii. Piiiiiiiiiiiiiiiiiii..." },
	["sp_ump45_d-visions"] = { name = "Espejismo", desc = "Se ha serigrafiado con un diseño de nautilo.\n\nCarcosa te llama" },
	["cu_xm1014_caritas"] = { name = "Tranquilidad", desc = "Se ha pintado a mano la imagen de una mujer con pelo largo.\n\nNo estoy aquí por algo que necesito; estoy aquí por algo que quiero —Imogen y El Oni, segunda parte" },
	["am_awp_glory"] = { name = "Héroe de guerra", desc = "Se le ha aplicado una capa base de color azul metalizado sobre la que se ha pintado a mano con motivos de color oro metalizado.\n\nHaz que vuelvan sus cabezas antes de acabar con ellos" },
	["cu_elites_urbanstorm"] = { name = "Choque urbano", desc = "Se ha personalizado con camuflaje urbano y motivos azules.\n\nAl rayo siempre lo sigue el trueno" },
	["aq_deagle_naga"] = { name = "Naga", desc = "Ha sido grabado con un motivo mitológico.\n\nUna bestia en la mitología india y también en el campo de batalla" },
	["cu_galil_abrasion"] = { name = "Charlatán", desc = "Se ha envejecido y pintado a mano. La calavera pintada sobre el guardapolvo aparenta hablar cuando se mueve el cerrojo.\n\nHay quien dice que cuando ríes, el mundo ríe contigo... pero cuando el que ríe es el Charlatán, lo único que se oye son gritos" },
	["cu_glock_deathtoll"] = { name = "Catacumbas", desc = "Se le ha aplicado un adhesivo hidrográfico de calaveras sobre una base de pintura metálica plata.\n\nLa intimidación es la forma más sincera de adulación" },
	["cu_m4a4_ancestral"] = { name = "龍王 (Rey dragón)", desc = "Se ha personalizado con la imagen de un dragón en rojo y azul.\n\nUn arma digna del mismísimo Rey Mono —Booth (traficante de armas)" },
	["cu_m249_sektor"] = { name = "Bloqueo del sistema", desc = "Se ha personalizado con un diseño de ciencia ficción.\n\nEl fuego de cobertura nunca fue tan hermoso." },
	["am_mac10_malachite"] = { name = "Malaquita", desc = "Se ha pintado con un efecto metálico y se le ha aplicado un patrón ondulado mediante una herramienta de peine de goma.\n\nLuminosos colores ocultan un arma con un oscuro propósito" },
	["cu_mp9_deadly_poison"] = { name = "Veneno mortal", desc = "Se ha personalizado con formas angulares dinámicas y una araña estilizada.\n\nNo es el primer mordisco el que te mata, son los veintinueve que vienen después" },
	["cu_p250_mandala"] = { name = "Muertos", desc = "Se ha personalizado con motivos circulares.\n\nEmpapada en tradición y sangre" },
	["cu_sawedoff_deva"] = { name = "Serenidad", desc = "Se ha pintado a mano con la imagen de una mujer con alas.\n\nLa serenidad es un lujo que pocos pueden permitirse —Valeria Jenner (revolucionaria)" },
	["aq_scar20_leak"] = { name = "Gruta", desc = "Se ha pintado con esponja y con pincel en tonos azul metalizado.\n\nEl fundador de Season Corporation encargó esta pieza tras ser rescatado por el renombrado operador Kotaro Izaki" },
	["aq_xm1014_sigla"] = { name = "Azogue", desc = "Se le ha aplicado un adhesivo hidrográfico translúcido con un mapa del terreno sobre pintura metálica plata.\n\\A veces necesitas un elemento inestable para resolver tus problemas —Kotaro Izaki (experto en infiltración)" },
	["an_tiger_orange"] = { name = "Diente de tigre", desc = "Se ha anodizado en naranja y a continuación grabado a mano mediante un patrón de rayas de tigre.\n\nExcepcional como el tigre... Mortal como el tigre..." },
	["am_marble_fade"] = { name = "Mármol desteñido", desc = "Se ha pintado con un marmoleado medio usando tonos negros y plata metalizados, luego recubierto de caramelo en tres colores.\n\nLa hoja está hecha de muchos colores, pero pronto todo será rojo" },
	["am_marbleized"] = { name = "Doppler", desc = "Se ha pintado con un marmoleado medio usando tonos negros y plata metalizados, y luego se ha recubierto de caramelo.\n\nPerderse en su colorido puede resultar fatal" },
	["cu_ak47_mastery"] = { name = "Elite Build", desc = "It has been custom painted using a combination of hydrographics and careful stenciling and detailed with heat-transfer decals.\n\nTread softly Naomi...people in your line of work aren't known for their longevity - The Shield and The Serpent Part 2" },
	["aq_mp7_ultramodern"] = { name = "Núcleo acorazado", desc = "Se ha pintado con pintura oleosa negra y refinado con partes e incrustaciones de acero inoxidable pulidas.\n\nPerfecto para el agente de operaciones que valora tanto la sofisticación como la potencia de fuego —Booth (traficante de armas)" },
	["aq_awp_twine"] = { name = "Rey gusano", desc = "Se ha pintado con la imagen de una calavera y raíces usando pinturas esmaltadas de alta durabilidad.\n\nHay raíces a las que uno no se quiere agarrar" },
	["am_bronze_sparkle"] = { name = "Bronce decó", desc = "Se ha pintado con un patrón art decó usando pinturas metalizadas automovilísticas.\n\nTodo el mundo busca plata y oro" },
	["aq_p250_contour"] = { name = "Valencia", desc = "Se ha personalizado con un diseño azul y gris que complementa la forma del arma.\n\nNo pongo en duda tus habilidades, pero sí tu actitud —El Maestro y El Iconoclasta, primera parte" },
	["cu_fiveseven_banana"] = { name = "Simiesco", desc = "Es un plátano.\n\nAlgunas veces está bien visto jugar con la comida" },
	["cu_galil_eco"] = { name = "Eco", desc = "Se ha pintado de blanco y verde y detallado con pegatinas de transferencia térmica.\n\nPor suficiente dinero, la Unidad de élite se tomará cualquier causa como la suya" },
	["aq_famas_jinn"] = { name = "Djinn", desc = "Se ha incrustado con oro y níquel, así como grabado con la imagen de un genio.\n\nCuidado con lo que deseas" },
	["cu_m4a1_hyper_beast"] = { name = "Hyper Beast", desc = "It has been custom painted with a beastly creature in psychadelic colours.\n\nYou really want to impress me Booth? Make this black light sensitive - Rona Sabri, Rising Star" },
	["cu_mag7_redhot"] = { name = "PaintKit_cu_mac10_redhot_tag", desc = "PaintKit_cu_mac10_redhot" },
	["am_negev_glory"] = { name = "PaintKit_am_awp_glory_Tag", desc = "PaintKit_am_awp_glory" },
	["cu_mac10_neonrider"] = { name = "Piloto de neón", desc = "Se ha personalizado con la imagen de un letal motorista.\n\nUna parte de arma, dos partes de carpeta ochentera." },
	["cu_sawedoff_origami"] = { name = "Origami", desc = "Se ha personalizado con un diseño inspirado en el origami.\n\nSe necesita tiempo para crear algo hermoso, pero solo un momento para destruirlo" },
	["cu_cz75_precision"] = { name = "La pole", desc = "Se ha personalizado con un tema de carreras rojo y gris.\n\nSolo tenemos una oportunidad, así que hagámoslo bien —Felix Riley (oficial al mando)" },
	["am_ump_racer"] = { name = "Grand Prix", desc = "Se ha personalizado con un motivo de carreras antiguo.\n\nNo te olvides el casco" },
	["am_aqua_flecks"] = { name = "Crepúsculo galáctico", desc = "am_aqua_flecks" },
	["cu_chronos_g3sg1"] = { name = "Cronos", desc = "Se le ha aplicado un adhesivo hidrográfico de mecanismos de relojes con colores psicodélicos y acentuado con pegatinas de transferencia térmica.\n\nCon el tiempo, todo es posible." },
	["hy_hades"] = { name = "Hades", desc = "Se le ha aplicado un adhesivo hidrográfico de antiguos cascos griegos.\n\nSi quería que llevase a cabo una entrevista, probablemente había una manera más fácil de concertarla —El Escudo y El Contador de Historias, segunda parte" },
	["hy_icarus"] = { name = "Caída de Ícaro", desc = "Se le ha aplicado un adhesivo hidrográfico de plumas y cera derretida alrededor del sol.\n\nLa traición de Turner es mi fracaso... Tengo que hacerlo bien —Sebastien Hennequet (francotirador)" },
	["cu_labyrinth"] = { name = "Laberinto del Minotauro", desc = "Se le ha aplicado un adhesivo hidrográfico de un laberinto isométrico.\n\nNo hay forma de escapar" },
	["sp_labyrinth1"] = { name = "Asterión", desc = "Se ha pintado con espray usando una plantilla de un laberinto isométrico.\n\nLos reyes no son más que hombres... y ningún hombre debería ser adorado. —Valeria Jenner (revolucionaria)" },
	["sp_labyrinth2"] = { name = "Explorador", desc = "Se ha pintado con espray usando una plantilla de un laberinto isométrico.\n\nPersonalmente me quedo con la cuarta edición, pero el cliente siempre tiene la razón. —Booth (traficante de armas)" },
	["sp_labyrinth3"] = { name = "Dédalo", desc = "Se ha pintado con espray usando una plantilla de un laberinto isométrico.\n\nImogen... si alguna vez descubren lo que has hecho, nos podría costar todo. —Un asunto familiar, primera parte" },
	["cu_medusa_awp"] = { name = "Medusa", desc = "Se ha personalizado con la imagen de una gorgona.\n\nSi me puedes ver, estás muerto" },
	["gs_mother_of_pearl_elite"] = { name = "Duelista", desc = "Se le ha aplicado una empuñadura de nácar pintada y una pátina oscura a propósito.\n\nGominola no incluida" },
	["aa_pandora"] = { name = "La caja de Pandora", desc = "Se ha aerografiado con dibujos de espíritus.\n\n El mundo está en llamas, pero nos queda la esperanza" },
	["cu_poseidon"] = { name = "Poseidón", desc = "Se ha personalizado con una representación de la batalla entre Piscis y Poseidón.\n\nTres personas pueden guardar un secreto si dos de ellas están muertas" },
	["hy_zodiac1"] = { name = "Luna en Libra", desc = "Se le ha aplicado un adhesivo hidrográfico con un símbolo del zodiaco.\n\nHoy es un gran día para encontrar el amor y matar a tus rivales" },
	["hy_zodiac2"] = { name = "Sol en Leo", desc = "Se le ha aplicado un adhesivo hidrográfico con un símbolo del zodiaco.\n\nLos días se van a poner difíciles para tus enemigos" },
	["hy_zodiac3"] = { name = "Pronóstico marítimo", desc = "Se le ha aplicado un adhesivo hidrográfico con un símbolo del zodiaco.\n\nQuién sabe lo que aguarda el futuro" },
	["so_khaki_green"] = { name = "Verde caqui", desc = "Se han pintado algunas partes con espray de colores sólidos en tonos verde caqui y negro.\n\nQuien fuera no cambia en quien se ha convertido. No te preocupes, Felix... Yo lo detendré. —El Paladín y El Maestro, segunda parte" },
	["cu_anime_aug"] = { name = "Akihabara", desc = "Se ha decorado con un vinilo de cambio térmico de la portada de un anime.\n\n¡Yatta!" },
	["am_bamboo_jungle"] = { name = "Hidropónico", desc = "Se ha pintado con un motivo de bambú en tonos metalizados.\n\nFijaos cómo el armero Yukako usa la madera del AK-47 para resaltar su tono verdoso. No os equivoquéis: la hidroponía es bella y letal como la misma naturaleza. —Imogen (traficante de armas en prácticas)" },
	["hy_bamboo_jungle_ink"] = { name = "Impresión de bambú", desc = "Se le ha aplicado un adhesivo hidrográfico de bambú pintado a pincel.\n\nIntroducir aquí un juego de palabras sobre disparar" },
	["hy_bamboo_jungle_black"] = { name = "Sombra de bambú", desc = "Se le ha aplicado un adhesivo hidrográfico de bambú pintado a pincel.\n\nEsta arma le arrebató su hijo a Kotaro... y un día la utilizará para detener al responsable" },
	["hy_bamboo_jungle"] = { name = "Bosque de bambú", desc = "Se le ha aplicado un adhesivo hidrográfico de bambú pintado a pincel.\n\nSigo pensando que necesita un panda —Booth (traficante de armas)" },
	["am_geometric_steps"] = { name = "Terraza acuática", desc = "Se ha pintado con un motivo geométrico en tonos metalizados.\n\nSerenidad..." },
	["hy_geometric_steps"] = { name = "Geopasos", desc = "Se le ha aplicado un adhesivo hidrográfico con un motivo geométrico." },
	["hy_geometric_steps_green"] = { name = "Antiterraza", desc = "Se le ha aplicado un adhesivo hidrográfico con un motivo geométrico.\n\nOye Sabri, deberías coger esto... es ruidoso, como tú —Carmen Cocinero (experta en extracción)" },
	["hy_geometric_steps_yellow"] = { name = "Terraza", desc = "Se le ha aplicado un adhesivo hidrográfico con un motivo geométrico.\n\nEres demasiado confiada, Valeria... eso te va a matar —Franz Kriegeld (estratega de Phoenix)" },
	["hy_kimono_diamonds"] = { name = "Kimono de neón", desc = "Se le ha aplicado un adhesivo hidrográfico con un motivo geométrico.\n\nSé que te estoy pidiendo mucho... Turner era como tu hijo —El Paladín y El Maestro, primera parte" },
	["hy_kimono_diamonds_orange"] = { name = "Kimono naranja", desc = "Se le ha aplicado un adhesivo hidrográfico con un motivo geométrico.\n\nLa tradición es algo poderoso" },
	["hy_kimono_diamonds_red"] = { name = "Kimono carmesí", desc = "Se le ha aplicado un adhesivo hidrográfico con un motivo geométrico.\n\nTurner, todos los hombres pueden quebrarse... solo es cuestión de encontrar el punto justo de presión —El Traidor y El Buscador de la Verdad, segunda parte" },
	["sp_kimono_diamonds"] = { name = "Kimono de menta", desc = "Se ha pintado con espray usando un motivo geométrico.\n\nVen, Booth, deja que te enseñe en lo que he estado trabajando. —Yukako (armero)" },
	["am_seastorm"] = { name = "Tormenta de medianoche", desc = "Se le ha aplicado un adhesivo hidrográfico de un mar tormentoso sobre una capa de azul metalizado.\n\nNo te odio, Sebastien... te compadezco —Chase Turner (desertor)" },
	["am_seastorm_blood"] = { name = "Tormenta del ocaso 壱", desc = "Se le ha aplicado un adhesivo hidrográfico de un mar tormentoso sobre una capa de rojo metalizado.\n\nCrees que es lo mismo, pero fíjate bien... la sutileza marca la diferencia" },
	["am_seastorm_shojo"] = { name = "Tormenta del ocaso 弐", desc = "am_seastorm_shojo" },
	["am_kimono_sunrise"] = { name = "Alba", desc = "Se ha pintado la salida del sol con tonos metalizados.\n\nEl sol sale tanto para los justos como para los injustos" },
	["am_so_keycolors"] = { name = "Impactante", desc = "am_so_keycolors" },
	["so_aqua"] = { name = "Ave marina", desc = "Se han pintado algunas partes con espray de colores sólidos en tonos aguamarina y tostado.\n\nMío." },
	["cu_ak47_courage_alt"] = { name = "Venganza aguamarina", desc = "Se ha personalizado con la imagen de un grupo de delfines.\n\n¿Hay eco aquí?" },
	["cu_cz75a_chastizer"] = { name = "Avispa", desc = "Se ha personalizado con formas curvas.\n\nFlota como una mariposa, pica como un odioso e implacable mosquito" },
	["am_famas_dots"] = { name = "Red neural", desc = "Se le ha aplicado un adhesivo hidrográfico translúcido sobre una capa metalizada.\n\nLo sé. Pero era lo correcto... —Un asunto familiar, segunda parte" },
	["cu_galilar_particles"] = { name = "Despegue", desc = "Se ha personalizado con un diseño gráfico de rayas y partículas pixeladas.\n\nEl elemento perfecto para un caluroso día de verano" },
	["aq_glock18_flames_blue"] = { name = "Mechero Bunsen", desc = "Se le ha aplicado una pátina azul que se ha pulido hasta dar como resultado un diseño gráfico de llamas.\n\nTOC, TOC" },
	["cu_m4a4_evil_daimyo"] = { name = "Daimio malvado", desc = "Se ha personalizado con un diseño gráfico en rojo y negro.\n\nCarmen. Lo he encontrado... —El Oni y La Valquiria, primera parte" },
	["cu_mp7_nemsis"] = { name = "Némesis", desc = "Se ha personalizado con la imagen de un monstruo metálico.\nViene a por ti" },
	["am_mp9_nitrogen"] = { name = "Dardo venenoso rubí", desc = "Se ha pintado con tonos metalizados rojos y púrpuras sobre una capa base de color negro.\n\n¿Quién dice que los diamantes son los mejores amigos de una chica?" },
	["cu_negev_annihilator"] = { name = "Bocazas", desc = "Se ha personalizado con un diseño monstruoso y a continuación envejecido.\n\n¡Vuelve aquí, estoy hablando contigo!" },
	["cu_nova_ranger"] = { name = "Guardabosque", desc = "Se ha pintado con una pincelada hidrográfica y detallado con pegatinas de transferencia térmica.\n\n¿Quién lleva la cuenta?" },
	["aq_p2000_boom"] = { name = "Revólver", desc = "Se ha personalizado con el dibujo de una mano haciendo el gesto de apuntar.\n\nSe acabó jugar y divertirse" },
	["cu_ump45_uproar"] = { name = "Revuelta", desc = "Se ha pintado utilizando una combinación de adhesivos hidrográficos y pegatinas de transferencia térmica.\n\nSi no querías daños colaterales, deberías haber sido más específico... —Javier Alviso (puño del Fénix)" },
	["cu_usp_progressiv"] = { name = "Torsión", desc = "Se ha personalizado con un moderno diseño de tonos negro, blanco y amarillo.\n\nTe están manipulando, Kotaro. Y si no puedes verlo, estás metido en más problemas de los que crees... —Felix Riley (oficial al mando)" },
	["cu_ak47_winter_sport"] = { name = "Frente brumoso", desc = "Se ha pintado en vibrantes colores árticos\n\nMe encanta trabajar sobre la nieve... siempre deja una bonita imagen —Javier Alviso (puño del Fénix)" },
	["cu_dualberretta_dragons"] = { name = "Dragones duales", desc = "Tiene dragones grabados a láser en la corredera y el cañón.\n\nMarian siempre fue más legal que Sylvia" },
	["cu_famas_lenta"] = { name = "Superviviente Z", desc = "Se ha estampado mediante cinta adhesiva y luego envejecido.\n\n¿Qué es rojo y blanco y tiene un disparo automático?" },
	["gs_g3sg1_flux_purple"] = { name = "Flujo", desc = "Se ha pintado con pinturas moradas y plateadas metalizadas.\n\nUn plan es una lista de cosas que no van a ocurrir" },
	["gs_galil_nightwing"] = { name = "Piedra fría", desc = "Se ha resaltado con pintura azul metalizada. La culata y el cargador enseñan la marca de su creador.\n\nAl final debes alejarte de la sombra de tu mentor" },
	["gs_glock18_wrathys"] = { name = "Espectros", desc = "Tiene almas perdidas pintadas en la corredera.\n\nPrimero perfora la mortaja y después la sombra" },
	["gs_m249_nebula_crusader"] = { name = "Defensor nebulosa", desc = "Se ha personalizado para que parezca una máquina arcade clásica.\n\nPuedes utilizarla, pero mete una moneda primero" },
	["gs_m4a1s_snakebite_gold"] = { name = "Espiral dorada", desc = "Se ha personalizado con un diseño hidrográfico de una serpiente atacando.\n\nLa víbora puede atacar desde cualquier sitio." },
	["cu_mac10_alekhya_duo"] = { name = "Rangeen", desc = "Se ha personalizado con un motivo del Antiguo Egipto.\n\n¿Podrás resistirte a su iracunda mirada?" },
	["cu_mag7_myrcene"] = { name = "Núcleo de cobalto", desc = "Se ha personalizado con un diseño gráfico futurista.\n\nMantenlo a salvo, Carmen —La Valquiria y El Amante, primera parte" },
	["cu_mp7_classified"] = { name = "Entrega especial", desc = "Se ha -CLASIFICADO-\n\nSi hago lo que quieres, ¿me dejarás ir? —El Fénix y El Contador de Historias, primera parte" },
	["hy_p250_crackshot"] = { name = "Disparo alado", desc = "Se ha personalizado con un vibrante diseño hidrográfico.\n\nA veces un sutil empujoncito es todo lo que necesitas para conseguir lo que quieres —Franz Kriegeld (estratega de Phoenix)" },
	["gs_scar20_peacemaker03"] = { name = "Verde militar", desc = "Se le ha aplicado un patrón de camuflaje con pintura metalizada.\n\nEscuchad... Puede que Izaki y Cocinero se fueran sin decir nada, pero nuestra misión sigue siendo la misma: salvar a Alex Kincaide —Felix Riley (oficial al mando)" },
	["cu_ssg08_technicality"] = { name = "Hierro grande", desc = "Se ha personalizado con un diseño hidrográfico amarillo y naranja y después se ha envejecido ligeramente.\n\nUna victoria con un asterisco sigue siendo una victoria. —Rona Sabri (joven promesa)" },
	["cu_usp_kill_confirmed"] = { name = "Muerte confirmada", desc = "Se ha personalizado con la imagen de una bala rompiendo un cráneo.\n\n2 en el pecho, 1 en la cabeza" },
	["aq_xm1014_scumbria"] = { name = "Caballa", desc = "Se ha pintado sutilmente con pinturas metalizadas.\n\nNo nos apresuremos: puede que haya otra manera de llamar la atención de Valeria... —Un amor de padre, tercera parte" },
	["cu_ak47_point_disarray"] = { name = "Planos caóticos", desc = "Se ha personalizado con un diseño hidrográfico geométrico.\n\nDe cerca es un caos, pero de lejos es una belleza —Franz Kriegeld (estratega de Phoenix)" },
	["am_aug_jumble"] = { name = "Ricochet", desc = "Se ha personalizado con un patrón celta de dos tonos.\n\nKotaro no necesita nuestra protección —La Valquiria y El Amante, segunda parte" },
	["cu_bizon_noxious"] = { name = "Plutonio empobrecido", desc = "Se ha personalizado para crear la ilusión de munición irradiada.\n\nA veces la infantería tiene la sartén por el mango" },
	["aq_deagle_corinthian"] = { name = "Corintio", desc = "Se ha grabado a láser y se le ha añadido una empuñadura de cuero personalizada.\n\nProtección. Esperanza. Confianza. Perseverancia." },
	["cu_fiveseven_retrobution"] = { name = "Retrobución", desc = "Se ha personalizado con un diseño hidrográfico retro.\n\nAlgunas cosas nunca pasan de moda" },
	["cu_g3sg1_executioner"] = { name = "El verdugo", desc = "Se ha pintado a mano con una horripilante muestra de huesos.\n\nSiempre me llevo un trofeo —Javier Alviso (puño del Fénix)" },
	["gs_m4a4_royal_squire"] = { name = "Paladín real", desc = "Se ha grabado y pintado laboriosamente para dar una sensación de grandeza.\n\nEsta llamada de teléfono no ha tenido lugar, ¿entiendes? —El Paladín y el Padre, primera parte" },
	["cu_negev_impact"] = { name = "Power Loader", desc = "Se ha personalizado con un diseño hidrográfico de reminiscencias de maquinaria pesada.\n\nTemido por reinas. Amado por obispos." },
	["am_p2000_imperial_red"] = { name = "Imperial", desc = "Se ha grabado a láser y pintado con rojo y amarillo metalizados.\n\nMis sentimientos son irrelevantes, Keo; tengo un imperio que proteger —Booth (traficante de armas)" },
	["cu_p90_shapewood"] = { name = "Madera tratada", desc = "La culata de madera ha sido tallada a mano, lijada y laminada.\n\nPinzas no incluidas" },
	["gs_sawedoff_necromancer"] = { name = "Yorick", desc = "Se le ha aplicado un adhesivo hidrográfico de una calavera y detalles de oro pintados a mano.\n\nRosencrantz y Guildenstern nunca tuvieron una oportunidad..." },
	["hy_scar20_jungler"] = { name = "Epidemia", desc = "Se ha pintado con tonos verdes vibrantes.\n\nValeria es cada vez más audaz... ha llegado el momento de luchar contra ella —Felix Riley (oficial al mando)" },
	["gs_sg553_tiger_moth"] = { name = "Polilla tigre", desc = "Se ha personalizado con un patrón de ala de polilla.\n\nValeria es cada vez más audaz... ha llegado el momento de luchar contra ella —Felix Riley (oficial al mando)" },
	["cu_tec9_avalanche"] = { name = "Avalancha", desc = "Se ha personalizado con un diseño hidrográfico pulcro y futurista.\n\nTenemos que movernos en su contra de manera inteligente: un movimiento en falso y nos aplastará —La Serpiente y El Buscador de la Verdad" },
	["aq_xm1014_hot_rod"] = { name = "Encendedor Teclu", desc = "Se ha utilizado pintura metálica para añadir llamas.\n\nA veces un bunsen no basta..." },
	["gs_ak47_supercharged"] = { name = "Bujía inyectora", desc = "El cuerpo del rifle se ha pintado de amarillo brillante y el cargador se ha personalizado para que parezca una maquinaria.\n\nVive de medio cargador en medio cargador." },
	["cu_bizon_citizen"] = { name = "Zona fótica", desc = "Se le ha aplicado un patrón personalizado de camuflaje acentuado con pintura fluorescente.\n\nNo es el romper de las olas lo que oyes..." },
	["aq_deserteagle_kumichodragon"] = { name = "Dragón Kumicho", desc = "Se han grabado dragones con láser en corredera y empuñadura.\n\nSoy un gran admirador de tu trabajo, Yukako; deberíamos hablar... —Huxley (la competencia)" },
	["cu_fiveseven_augmented"] = { name = "Triunvirato", desc = "Se le ha aplicado un DDPAT hidrográfico de color azul claro y se ha personalizado la corredera.\n\nYo no saco provecho de la guerra, yo saco provecho de la condición humana... —Booth (traficante de armas)" },
	["gs_glock18_award"] = { name = "Legión real", desc = "Se ha grabado laboriosamente.\n\nNaomi tiene asumido que algún día morirá por Valeria." },
	["gs_m4a4_pioneer"] = { name = "Estrella de combate", desc = "Se han cortado las esquinas del recibo de la compra.\n\nCuidado con aquellos que hablan de la torre de vigilancia" },
	["am_mac10_electricity"] = { name = "Cocodrilo azul", desc = "Se le ha aplicado un adhesivo hidrográfico de vibrante piel de cocodrilo.\n\nHidrodeslizador no incluido" },
	["gs_mag7_praetorian"] = { name = "Pretoriana", desc = "Se ha pintado de forma reconocible con tonos metalizados.\n\nLa aguja se está formando" },
	["sp_mp7_impire"] = { name = "Luciferino", desc = "Se le ha aplicado un adhesivo hidrográfico que representa una turba de diablillos.\n\nLa aniquilación nunca había sido tan adorable." },
	["cu_ssg08_necropos"] = { name = "Necropos", desc = "Se ha pintado a mano con runas perversas.\n\nCada carpintero necesita su martillo; cada sastre, su husillo..." },
	["gs_tec9_jambiya"] = { name = "Jambiya", desc = "Se ha pintado a mano y grabado al agua fuerte con patrones de Oriente Medio.\n\nSherezade ha contado mil historias... esta es la última que oirás" },
	["gs_usp_voltage"] = { name = "Conducto de plomo", desc = "Se le ha aplicado un silenciador y una corredera personalizados con grabados.\n\nTienes lo que querías, ahora, ¿sabes lo que significa eso? –Booth (traficante de armas)" },
	["cu_aug_swallows"] = { name = "Golondrinas", desc = "Se le ha aplicado un adhesivo hidrográfico de pájaros volando entre las nubes.\n\nAlgunos pájaros representan la paz y la armonía... Estos no." },
	["cu_bizon_Curse"] = { name = "Juicio de Anubis", desc = "Se ha personalizado con un tema egipcio.\n\nSu gatillo pesa menos que una pluma" },
	["gs_cz75a_redastor"] = { name = "Azor rojo", desc = "Se ha pintado a mano con detalles blancos y rojos.\n\nBooth se ha convertido en sinónimo del drama familiar; ¿y quién tiene tiempo para dramas familiares cuando estamos hablando de negocios? —Huxley (la competencia)." },
	["gs_dualberettas_ventilators"] = { name = "Turbinas", desc = "Estas pistolas han sido elegantemente pintadas de cromo.\n\nUn arma creada por y para artistas" },
	["sp_g3sg1_militiaorange"] = { name = "Explosión naranja", desc = "Las plantillas y el espray naranja le dan a este rifle su aspecto distintivo.\n\nConviértete en un Shere Khan de hoy" },
	["gs_galilar_incenerator"] = { name = "Tiroteo", desc = "Se le han aplicado detalles de color carmín y un icono pintado a mano.\n\nBooth tenía esta arma encargada para Valeria en un intento por apaciguar las cosas con la Phoenix." },
	["cu_m249_spectre"] = { name = "Espectro", desc = "Se ha pintado de forma elegante y contemporánea.\n\nLa guerra civil de la Phoenix está a punto de comenzar." },
	["cu_m4a1s_soultaker"] = { name = "Fuego de Chantico", desc = "Se ha pintado a mano con colores vibrantes y está decorada con una carita sonriente en la culata.\n\nProtege lo que es tuyo." },
	["am_mp9_bioleak"] = { name = "Fuga biológica", desc = "Distintas tonalidades de verde crean la ilusión de un vertido químico.\n\nCUIDADO CON LA EXPOSICIÓN DIRECTA" },
	["hy_p2000_oceani"] = { name = "Oceánica", desc = "Se le ha aplicado una base negra sobre la que se han trazado diseños azules.\n\n¿Alguien sabe algo de Kotaro o Carmen? —Felix Riley (oficial al mando)" },
	["cu_p250_asiimov"] = { name = "Asiimov", desc = "Se ha personalizado con un diseño de ciencia ficción.\n\nSi crees que necesito un rifle para matar a un hombre, no tienes ni idea de con quién estás hablando... —Mikha Biton (francotirador de la Phoenix)" },
	["gs_sawedoff_fubar"] = { name = "Hecha polvo", desc = "Esta escopeta recortada con años de historia tiene un recuento de bajas grabado en el cañón.\n\nEmpieza con un disparo... y acaba con silencio" },
	["cu_sg553_atlas"] = { name = "Atlas", desc = "Se le ha aplicado un adhesivo hidrográfico de camuflaje moderno.\n\nEra un diseño destinado a las Fuerzas Operativas de la Coalición, pero Huxley se involucró en el espionaje corporativo para convertirla en una exclusiva de la Phoenix." },
	["gs_ssg08_armacore"] = { name = "Cruzado fantasma", desc = "Se ha pintado con tonos blancos con detalles árticos azules.\n\nNunca podré sustituir lo que nos arrebataron... pero puedo arrebatarles más de lo que ellos se llevaron —Phoenix y el novato, segunda parte" },
	["am_tec9_redblast"] = { name = "Ignición", desc = "Se le ha aplicado un adhesivo de llamas en el cañón.\n\nOdio tener siempre la razón. —Carmen Cocinero (experta en extracción)" },
	["cu_ump45_primalsaber"] = { name = "Sable primitivo", desc = "Se le ha aplicado un adhesivo de la calavera de un tigre dientes de sable.\n\nA todo depredador le llega su hora" },
	["cu_xm1014_spectrum"] = { name = "De punta en blanco", desc = "En cromo blanco y negro, esta escopeta es perfecta para ocasiones formales.\n\nTenemos a Izaki y a Cocinero. ¿Los necesitas para algo o puedo cargármelos ya? —El Mercenario y La Serpiente, primera parte" },
	["cu_lore"] = { name = "Tradicional", desc = "Se ha personalizado con un patrón celta." },
	["cu_stonewash"] = { name = "Laminado Negro", desc = "Se ha lavado a la piedra, otorgándole un laminado negro a la empuñadura." },
	["am_marbleized_g"] = { name = "Doppler gamma", desc = "Se ha pintado con un marmoleado medio usando tonos negros y plata metalizados, y luego se ha recubierto de caramelo." },
	["gs_autotronic"] = { name = "Autotrónico", desc = "Se ha anodizado en rojo y utilizado malla de acero para aligerar su peso." },
	["am_marked_up"] = { name = "Mano alzada", desc = "Se ha pintado en tonos negros y morados y despues se ha decorado con un rotulador de color metálico." },
	["gs_aug_aristocrat"] = { name = "Aristócrata", desc = "Tiene filigranas plateadas sobre una base anodizada azul claro.\n\nPara el 1 %." },
	["gs_awp_phobos"] = { name = "Fobos", desc = "Se ha construido con paneles de metal y componentes electrónicos duraderos.\n\nConstruye un mundo mejor." },
	["cu_five_seven_daimyo"] = { name = "Daimio violento", desc = "Se ha personalizado con un diseño gráfico en violeta y negro.\n\n¿Cómo vas a gobernar?" },
	["cu_glock_wasteland_rebel"] = { name = "Rebelde del yermo", desc = "Se ha envejecido, xilografiado y pintado con grafiti.\n\nRinde tributo." },
	["gs_m4a1_mecha_industries"] = { name = "Industria mecánica", desc = "Se ha construido con paneles de cerámica endurecida.\n\nCon una tecnología totalmente nueva." },
	["cu_m4a4_desolate_space"] = { name = "Espacio inhóspito", desc = "Se ha personalizado con un tema espacial.\n\n«Hemos perdido el contacto»." },
	["aq_mac_10_alien_camo"] = { name = "Carnívoro", desc = "Se le ha aplicado un adhesivo hidrográfico rojo y morado sobre una capa base metálica.\n\nPreparar antes de servir." },
	["aq_nova_sci_fi"] = { name = "Exo", desc = "Se le ha aplicado un adhesivo hidrográfico con un motivo plateado sobre una base con un patrón morado.\n\nLo que cuenta es lo que hay dentro." },
	["gs_p2000_imperial_dragon"] = { name = "Dragón imperial", desc = "Se ha personalizado con el motivo de un dragón pintado con tonos rojos metalizados.\n\nMira hacia el oeste." },
	["gs_p250_metal_panels"] = { name = "Blindado", desc = "Se ha decorado con paneles de metal remachados.\n\nFiabilidad y seguridad garantizadas." },
	["gs_p90_full_throttle"] = { name = "Acribillador", desc = "Se ha pintado con el diseño de una llama en tonos naranja y azul metalizados.\n\nClutch no incluido." },
	["gs_pp_bizon_harvester"] = { name = "Segadora", desc = "Se ha decorado con un motivo biomecánico.\n\nExperimentación biomecánica en su máximo esplendor." },
	["cu_r8_cybersport"] = { name = "Reinicio", desc = "Se ha decorado con detalles de color rojo sobre paneles plateados y negros.\n\n«¿Has probado a apagarlo y encenderlo de nuevo?»" },
	["cu_sawed_off_lime"] = { name = "Luz de lima", desc = "Se ha personalizado con amplias ondas en verde lima y blanco.\n\nTodas las miradas están puestas en ti." },
	["gs_scar20_bloodsport"] = { name = "Contacto sangriento", desc = "Se ha cubierto con adhesivos de logotipos blancos sobre un hidrográfico rojo y negro.\n\nEquipo antiterrorista patrocinado por..." },
	["cu_sg553_aerial"] = { name = "Aéreo", desc = "Se ha pintado de plata y amarillo y se le ha aplicado un adhesivo de un ala blanca.\n\nDemuestra tu superioridad aérea." },
	["am_tec_9_sea_salt"] = { name = "Capa de hielo", desc = "Se le ha aplicado un adhesivo hidrográfico abstracto en azul y blanco.\n\nHunde al adversario." },
	["bloodhound_black_silver"] = { name = "Chamuscado", desc = "Se han fabricado con cuero negro clásico.\n\nLas monedas de desafío son geniales, pero con ellas no salta sangre cuando le das un puñetazo a alguien —Carmen Cocinero (experta en extracción)" },
	["bloodhound_snakeskin_brass"] = { name = "Mordedura de serpiente", desc = "Se han confeccionado con una combinación de cuero y piel de serpiente.\n\nSeamos sinceros: nadie echa de menos a Sergei. —Hans Kriegeld (estratega de Phoenix)" },
	["bloodhound_metallic"] = { name = "Bronceado", desc = "El cuero se ha teñido en dos tonos de bronce.\n\nLa Operación Bloodhound marcó el final del francotirador Chase Turner." },
	["bloodhound_guerrilla"] = { name = "Guerrilla", desc = "Se han confeccionado con una combinación de cuero marrón y cuero sintético con estampado de camuflaje.\n\nValeria planea su venganza, de eso no cabe duda... —Sebastien Hennequet (francotirador)" },
	["handwrap_leathery"] = { name = "Cuero", desc = "Se han fabricado con cuero, gasas y malas intenciones.\n\nHazte notar." },
	["handwrap_camo_grey"] = { name = "DDPAT pícea", desc = "La envoltura exterior está hecha de tejido serigrafiado con un camuflaje digital gris.\n\nAlgunos dicen que son duros... otros lo demuestran." },
	["handwrap_red_slaughter"] = { name = "Masacre", desc = "Con estas vendas teñidas de carmesí, es difícil saber dónde acaba la tela y dónde empiezan las manchas sangre.\n\n¿A quién no le gusta trabajar con sus propias manos?" },
	["handwrap_fabric_orange_camo"] = { name = "Tierra yerma", desc = "Esta protección casera para las manos está hecha con materiales recuperados.\n\nFelix hace la vista gorda con la liga clandestina de boxeo de las Fuerzas Operativas de la Coalición." },
	["slick_black"] = { name = "Entrelazado lunar", desc = "Se ha entretejido ante azul medianoche con cuero negro azabache para darles a estos guantes un aspecto atemporal.\n\nEvitan que tus manos se manchen de sangre... metafóricamente hablando." },
	["slick_military"] = { name = "Convoy", desc = "Se han confeccionado con una combinación de cuero suave marrón y ante teñido.\n\nA veces se requiere discreción." },
	["slick_red"] = { name = "Entrelazado carmesí", desc = "Se ha entretejido ante negro con cuero escarlata para darles a estos guantes un aspecto atemporal.\n\nDeja tu marca, no tus huellas." },
	["slick_snakeskin_yellow"] = { name = "Diamantino", desc = "Se han confeccionado con una combinación de cuero marrón, cuero negro y piel de serpiente.\n\nLas ediciones estándar son para los pringados... —Imogen (traficante de armas en prácticas)" },
	["sporty_green"] = { name = "Laberinto de setos", desc = "Estos guantes verdes y blancos fueron fabricados por Icarus Athletics.\n\nSolo los cobardes temen volar cerca del sol." },
	["sporty_purple"] = { name = "Caja de Pandora", desc = "Se ha aplicado una impresión de un patrón sutil en las palmas de estos guantes negros y morados.\n\nIndispensables para expertos en demoliciones a los que les sudan las manos." },
	["sporty_light_blue"] = { name = "Superconductor", desc = "Los guantes azules y negros son ideales para quienes desean ser fenomenales.\n\nNo quieren nada." },
	["sporty_military"] = { name = "Árido", desc = "Los diversos tonos de marrón y un sutil estampado de camuflaje confieren un aspecto distinguido a estos guantes.\n\nSi vamos a ser héroes, podríamos también parecerlo... —Rona Sabri (joven promesa)" },
	["specialist_ddpat_green_camo"] = { name = "DDPAT boscoso", desc = "Se ha emparejado el cuero cosido a mano con una impresión de camuflaje DDPAT en tela resistente.\n\nA ciertas personas no les gusta mancharse las manos" },
	["specialist_kimono_diamonds_red"] = { name = "Kimono carmesí", desc = "Este par de guantes negros se ha decorado con un patrón de diamante rojo intenso.\n\nEmpieza a ver rojo." },
	["specialist_emerald_web"] = { name = "Telaraña esmeralda", desc = "Se ha aplicado un patrón de tela de araña al reverso de los guantes.\n\nLa disciplina es la base del éxito." },
	["specialist_white_orange_grey"] = { name = "Fundación", desc = "A estos guantes blancos y negros se les han aplicado detalles de color naranja.\n\nSe rumorea que Tortue se inspiró en la línea de armas Asiimov." },
	["motorcycle_basic_black"] = { name = "Eclipse", desc = "Este es el clásico diseño negro original que dio la fama a Tortue.\n\nNo lo llames crisis de los cuarenta" },
	["motorcycle_mono_boom"] = { name = "¡Bum!", desc = "Este par de guantes se ha decorado con onomatopeyas de cómics.\n\n¡PLAS! ¡BUM! ¡PAF!" },
	["motorcycle_mint_triangle"] = { name = "Hierbabuena", desc = "El cuero blanco y las costuras rojas hacen que estos guantes sean tan elegantes como cómodos.\n\nCon lo que nos cobra, Huxley ya podría al menos regalarnos unos caramelitos... —Felix Riley (oficial al mando)" },
