	["gs_AUG_Syd_Mead"] = { name = "Syd Mead", desc = "Personalizado por el reconocido artista Syd Mead.\n\nEn el pasado, nos dio visiones del futuro; en el presente, ha puesto el futuro en nuestras manos..." },
