	["aq_Desert_Eagle_Constable"] = { name = "Directiva", desc = "Está diseñada para ayudar a los futuros representantes de la ley en su labor de pacificación urbana.\n\nVivo o muerto, vendrá conmigo." },
	["gs_FAMAS_Rally"] = { name = "Jaula antivuelco", desc = "Se le ha aplicado un adhesivo personalizado similar a la carrocería de un coche manchado de barro.\n\nLa seguridad es lo primero." },
	["aq_Five_Seven_Scumbria"] = { name = "Caballa", desc = "Se ha decorado discretamente con rayas.\n\nNo puedes dejarlo morir, Félix; debes salvarlo. —La hija y el paladín, primera parte" },
	["gs_G3SG1_Ventilator"] = { name = "Turbina", desc = "Se le ha aplicado una elegante capa de pintura negra y cromo y se le ha añadido una culata de caucho personalizada.\n\nNo te preocupes, Naomi, haré que Felix pague. —Mikha Biton (francotirador de la Phoenix)" },
	["cu_Glock18_Weasel"] = { name = "Comadreja", desc = "Se le ha aplicado un adhesivo hidrográfico de Bobby, la comadreja traviesa. No te metas con Bobby.\n\nQue sea bonita, no significa que no muerda" },
	["cu_MAG7_Tribal"] = { name = "Petroglifo", desc = "Se ha personalizado para dar aspecto a roca tallada.\n\nEl conflicto ha estado ahí desde que el primer cavernícola aprendió a recoger piedras —Booth (traficante de armas)" },
	["cu_MP9_Narcis"] = { name = "Cámara estanca", desc = "Se ha pintado de blanco con el cargador rojo para que destaque.\n\nPerfecta para disparar a las tostadoras." },
	["hy_Negev_Dazzle"] = { name = "Deslumbramiento", desc = "Se ha cubierto con adhesivos con estampado de cebra azul y blanco.\n\nPor fin, un arma para los amantes de las curiosidades." },
	["cu_P90_Grimm"] = { name = "Macabro", desc = "Se le ha aplicado un adhesivo hidrográfico de espectros asesinos y se han pintado detalles en verde con pincel seco.\n\nImogen... ¿me estás vendiendo un arma o una lámina para colorear? —Rona Sabri (joven promesa)" },
	["gs_SCAR20_Powercore"] = { name = "Núcleo de energía", desc = "Se ha personalizado en color verde con toques de amarillo y se le ha aplicado un código de barras para una compra rápida.\n\nRegla n.º 1: Todo duelo exige una satisfacción." },
	["cu_SG556_Triarch"] = { name = "Triarca", desc = "Este rifle fue encargado por la realeza y se le ha aplicado un diseño personalizado de camuflaje desértico.\n\nDales recuerdos." },
	["gs_TEC9_Supercharged"] = { name = "Bujía inyectora", desc = "La parte principal del arma se ha pintado de amarillo y al cargador se le ha aplicado un adhesivo hidrográfico similar a la correa de un motor. Una empuñadura de cuero cosida a mano, completa este clásico de Seattle.\n\nEl equipo de mecánicos no va incluido" },
	["cu_UMP45_Metritera"] = { name = "Informe", desc = "Se le ha aplicado un adhesivo hidrográfico de un mapa topográfico.\n\nEsta reunión va a ser breve." },
	["hy_XM1014_Fractal_Blue"] = { name = "Rebufo", desc = "Se ha personalizado con tonos azul vibrante y gris humo.\n\nTen paciencia, Elliott; aún tienen un papel que desempeñar... —El Mercenario y La Serpiente, segunda parte" },
	["am_czv2_mf"] = { name = "Polímero", desc = "Se le ha aplicado un adhesivo a la corredera que se asemeja a la fórmula de un polímero.\n\nNo tendrás que volver a lidiar con la Phoenix. Sabemos cuidar de los nuestros... —La hija y el paladín, segunda parte" },
	["aq_glock_dark-fall"] = { name = "Hierro artesanal", desc = "Se han grabado una hiedra y unas espinas con láser en la corredera.\n\nSi se abandona, cualquier jardín se convierte en una maleza de hierbas enmarañadas." },
	["cu_ssg08_dragonfire_scope"] = { name = "Aliento de dragón", desc = "Se le ha aplicado un adhesivo hidrográfico de un dragón monstruoso que echa fuego por la nariz.\n\nSiéntate sobre tu tesoro y espera a quien quiera venir a llevárselo." },
	["gs_dual_berettas_golden_venice"] = { name = "Consorte Real", desc = "Entre las empuñaduras verdes efecto perla, las placas laterales con grifos y las correderas grabadas con láser, estas pistolas redefinen la elegancia.\n\nYa puedes soltar las palomas." },
	["gs_famas_mecha"] = { name = "Industria mecánica", desc = "Su austero diseño blanco y negro hace que parezca sacado de una serie de ciencia ficción.\n\nPelo azul de punta y compañero robot no incluidos." },
	["gs_final_pooldeadv2"] = { name = "Cirro", desc = "Se ha pintado de azul metalizado y se le ha aplicado un adhesivo de una calavera.\n\nBaja de las nubes." },
	["gs_g3sg1_viper_yellow"] = { name = "Aguijón", desc = "Se les han aplicado detalles amarillos y grises.\n\nUn instante de dolor. Luego nada." },
	["cu_galil_ar-camo"] = { name = "Arena negra", desc = "Se ha personalizado con tonos negros y de camuflaje.\n\nMikah Baton está a punto de empezar su campaña contra las Fuerzas Operativas de la Coalición" },
	["aa_hide-mp9"] = { name = "Escamas de arena", desc = "Se ha personalizado en gris y bronce.\n\nDebemos prepararnos para el contraataque —Felix Riley (oficial al mando)" },
