	["am_mag7_malform"] = { name = "Sonar", desc = "Se le ha aplicado un ligero estarcido en color turquesa.\n\nTienes razón, perder es una experiencia nueva para mí. —El Oni y La Valquiria, segunda parte" },
	["am_nova_sand"] = { name = "Gila", desc = "Se ha cubierto con un adhesivo de piel de lagarto.\n\nActúas como si nunca antes te hubieran secuestrado unos mercenarios. —El Oni y La Valquiria, primera parte" },
	["gs_p2000-sport"] = { name = "Césped", desc = "La empuñadura y la corredera se han pintado de color verde metálico.\n\nEl césped siempre está más rojo al otro lado." },
	["gs_p90_shallow_grave"] = { name = "Sepultura superficial", desc = "Las empuñaduras se han pintado para que parezcan piedras rúnicas y se ha aplicado un adhesivo hidrográfico de un esqueleto a la culata.\n\nCon la cantidad de cuerpos que estás apilando, ¿quién tiene tiempo de cavar?" },
	["cu_usp_cyrex"] = { name = "Cyrex", desc = "Se le han aplicado unos discretos realces en rojo para dar una sensación futurista.\n\nImogen es el pasado, Felix. Yo soy el futuro. —Huxley (la competencia)" },
	["cu_wp_sawedoff"] = { name = "Princesa del yermo", desc = "Alguien ha estado llevando la cuenta de las víctimas haciendo marcas en el cañón.\n\nEl regalo perfecto para el sociópata que ya tiene de todo." },
	["gs_ak47_bloodsport"] = { name = "Contacto sangriento", desc = "Se ha cubierto con adhesivos de logotipos blancos sobre un hidrográfico rojo y negro.\n\nSe busca copiloto." },
