	["cu_ump45_x-ray_machine"] = { name = "Exposición", desc = "Se ha aplicado una pintura personalizada que simula una radiografía, con su caja torácica y todo.\n\n«Por favor, sáquese todos los objetos metálicos de los bolsillos»." },
	["aq_xm1014_ziggy_anarchy"] = { name = "Ziggy", desc = "Se ha personalizado con un patrón aleatorio de trazos verdes, morados y plateados.\n\nOs presentamos un poquito de anarquía." },
