	["gs_nova_anchorite"] = { name = "Seis salvaje", desc = "Se ha aplicado una pintura personalizada que evoca imágenes posapocalípticas.\n\nMi arma, mis normas." },
	["cu_p2000_urban_hazard"] = { name = "Peligro urbano", desc = "Se ha personalizado con una empuñadura de camuflaje en tonos grises y una corredera roja.\n\nA partir de aquí es obligatorio el uso de casco." },
