	["gs_mp5_etch"] = { name = "Lavado con ácido", desc = "Antes de ensamblarse, su capa superior azul metálica se expuso al ácido. Tiene un acabado metálico de latón.\n\nNegro y azul, y latón por todos lados." },
	["gs_tec9_decimator"] = { name = "Diezmador", desc = "Se ha personalizado con tonos azules y rosas totalmente radicales.\n\n¡Disponible por 4 sencillos pagos de 124,95 $!" },
	["cu_nova_featherswing"] = { name = "Pluma", desc = "La pintura personalizada de plumas marrones se superpone a una caja de mecanismos y una culata de madera.\n\nEsto va a alborotar al gallinero." },
	["cu_g3sg1_blacksand"] = { name = "Arena negra", desc = "Se ha personalizado con un patrón de camuflaje militar en tonos canela y negro.\n\nLas Fuerzas Operativas de la Coalición iban ganando, luego Mikah adoptó una posición... más agresiva." },
	["gs_r8_memento"] = { name = "Recuerdo", desc = "El lujo de su empuñadura de marfil va bien con los intrincados grabados en metal del cañón, el marco y el cilindro.\n\n¡Arre!" },
	["cu_mp7_replica"] = { name = "Capa de neón", desc = "Se ha laminado y rellenado con cada color de neón imaginable.\n\n«¿Rojo, naranja, amarillo, verde, azul, añil y violeta? No me suenan de nada»." },
	["cu_p2000_obsidian"] = { name = "Obsidiana", desc = "Se ha personalizado para que parezca que se ha sumergido en lava y se solidifica como obsidiana a medida que se enfría.\n\nCon ello el término «cañón de cristal» pasa a tener un significado totalmente nuevo." },
	["gs_dual_elites_rose"] = { name = "Equilibrio", desc = "Se ha pintado una rosa roja en ambas empuñaduras sobre una base negra con filigranas plateadas.\n\nEn todas las cosas." },
	["sp_scar20_striker_dust"] = { name = "Rasgado", desc = "Se ha pintado con espray usando un patrón de rayas aleatorias.\n\nEn pedazos..." },
	["cu_sg553_reactor"] = { name = "Colonia IV", desc = "Se ha pintado a mano un esqueleto de hormiga roja sobre máscaras de gas y una corriente anaranjada y amarilla.\n\nConviértete en parte de su mundo." },
	["gs_mac10_stalker"] = { name = "Acechador", desc = "Se ha personalizado con la imagen de un gato azul neón y rayas amarillas y negras.\n\nVen, gatito, gatito..." },
	["cu_ssg08_tickler"] = { name = "Inyectado en sangre", desc = "Se ha personalizado con una monstruosidad que tiene tentáculos verdes, dientes y un ojo.\n\nEs para verte mejor..." },
	["gs_m249_warbird_veteran"] = { name = "Pájaro de guerra", desc = "Originalmente era azul con un escudo térmico amarillo, el tiempo y el uso han hecho mella en su apariencia.\n\nEsta antigüedad se merece un descanso." },
	["aq_m249_aztec"] = { name = "Azteca", desc = "Algunas piezas de esta M249, extraída de unas ruinas aztecas, se han oxidado y cubierto de musgo.\n\nNunca te fíes de un puente de cuerda" },
	["gs_dual_elites_classic"] = { name = "Elite 1.6", desc = "La palabra ELITE aparece estampada en el lateral de estás clásicas Berettas dobles.\n\nMaterial base" },
	["cu_famas_nuke_tension"] = { name = "Desmantelado", desc = "Esta pintura personalizada está inspirada en elementos que se encuentran en de_nuke de Counter-Strike 1.6.\n\nSi se encuentran, por favor, devuélvelos a la central nuclear de Cedar Creek" },
	["cu_tec9_flash"] = { name = "Aturdidora", desc = "Se ha personalizado para simular una granada aturdidora.\n\nTema: Clases de baile con Jordan" },
	["cu_fiveseven_gsg9"] = { name = "Colega", desc = "Se ha personalizado con un diseño de camuflaje y una textura personalizada bajo la mira posterior.\n\nTe cubro la espalda." },
	["gs_p250_inferno"] = { name = "Inferno", desc = "Se ha personalizado de modo que refleje el espíritu del mapa de_inferno.\n\n«¡Pero mira la hora!»" },
	["gs_mac10_dust_crate"] = { name = "Caja clásica", desc = "Se ha pintado a mano para que se parezca a las cajas de metal verdes que había originalmente en de_dust2.\n\n¡Rush B, no paréis!" },
	["gs_mag7_popdog"] = { name = "Popdog", desc = "Se ha personalizado para que parezca un vagón de tren con un Pop Dog pintado en el lateral.\n\n¡El perro que te refresca!" },
	["cu_mp9_hydra"] = { name = "Hydra", desc = "Se ha aplicado una pintura personalizada que hace referencia a Hydra, la octava operación de CS:GO.\n\nLa espera valió la pena" },
	["cu_p90_nostalgia"] = { name = "Nostalgia", desc = "Se ha personalizado con AT del menú principal original de Counter-Strike.\n\nToma unos minutos instalarlo y una eternidad dominarlo" },
	["am_aug_death_by_doggy"] = { name = "Muerte por perrito", desc = "Se ha aplicado un diseño multicolor de Pop Dog.\n\n¡Que no se confunda con una escalera de mano!" },
	["cu_scar_assault"] = { name = "Asalto", desc = "Se ha pintado a mano el logotipo de un centro de investigación en color blanco sobre un fondo azul.\n\nTambién está disponible en rojo" },
	["gs_mp5_fbi"] = { name = "Agente", desc = "Se ha personalizado con un color azul FBI y detalles de acabado en amarillo.\n\n«Es hora de tomar control de nuestra historia»" },
	["cu_ump_bomb"] = { name = "Plastique", desc = "Se ha aplicado una pintura personalizada de cables, una placa de circuito verde y un teclado numérico.\n\n73556**" },
	["cu_awp_wildfire"] = { name = "Wildfire", desc = "El logotipo de la Operación Wildfire se ha pintado a mano sobre una base roja y negra.\n\nLa punta que sobresale se encuentra con el martillo" },
	["cu_glock_hero"] = { name = "Sacrificio", desc = "Esta pintura personalizada homenajea al desactivador de fuegos de olofmeister en Overpass del campeonato ESL One Colonia 2014.\n\n«La desactivación se acerca, ¡él todavía sigue!»" },
	["gs_famas_legacy_gold"] = { name = "Conmemoración", desc = "Se ha personalizado con un color dorado y destaca varios detalles del vigésimo aniversario de Counter-Strike.\n\n¡Celebrando otros 20 más!" },
	["gs_ak47_gold_arabesque"] = { name = "Arabesco dorado", desc = "Se ha pintado a mano un diseño complejo sobre la culata y el guardamanos. El metal ha sido pintado en dorado con detalles de filigranas.\n\n«¿Qué otro truco se sacará Booth de la manga?»." },
	["am_lizard_red"] = { name = "Anolis naranja", desc = "Se ha aplicado un patrón rojo y dorado con escamas de lagartija.\n\nPintado a escala." },
	["hy_brush_camo_tan"] = { name = "Pinceladas del desierto", desc = "Se ha aplicado un patrón hidrográfico color arena.\n\nPrueba de la efectividad del camuflaje, V1." },
	["hy_ddpat_urban_red"] = { name = "DDPAT rojo", desc = "Se ha pintado usando un patrón disruptivo digital (DDPAT) hidrográfico rojo.\n\nPara cuando estás lo suficientemente cerca como para notar los píxeles, ya es demasiado tarde." },
	["hy_dry_wood"] = { name = "Madera descolorida", desc = "Se ha aplicado un patrón hidrográfico que imita la apariencia de madera seca.\n\nToca madera, porque vas a necesitar toda la suerte del mundo. - Felix Riley, oficial al mando." },
	["hy_desert_multicam"] = { name = "Tostada", desc = "Se ha aplicado un hidrográfico multicámara en tonos canela y negro.\n\nNo puedes fallar si no disparas." },
	["hy_torn_camo_paints"] = { name = "Arenas movedizas", desc = "Se ha aplicado un patrón hidrográfico en tonos bronce y azul a la culata y la empuñadura. El cañón se ha pintado de azul marino.\n\nQue sea «agradable a la vista» sería decirlo a la ligera." },
	["sp_moro_carving_lightblue"] = { name = "Nuevas raíces", desc = "Se ha pintado con espray usando un patrón de vides y flores con varios tonos verdes.\n\nZona de cultivo." },
	["sp_moro_carving_yellow"] = { name = "Viejas raíces", desc = "Se ha pintado con espray usando un patrón de vides y flores con varios tonos verdes.\n\nVetusto, añejo, degradado; o simplemente «viejo»." },
	["sp_moro_textile_purple_yellow"] = { name = "Enredadera marchita", desc = "Se ha pintado con espray usando un patrón de flores en tonos púrpura y oro.\n\nMolestia para algunos; belleza para otros." },
	["sp_palm_night"] = { name = "Palmera de medianoche", desc = "Se ha pintado con espray usando unas hojas de palmera negras sobre un patrón de camuflaje gris y morado.\n\n«¡El letrero dice cerrado!»." },
	["sp_desert_skulls"] = { name = "Blanqueado", desc = "Se ha pintado con espray usando un patrón de calaveras de animales.\n\nLo que nos queda." },
	["sp_zebracam_red"] = { name = "Presa", desc = "Se ha pintado con espray usando un patrón de rayas de cebra de color óxido.\n\nCome, duerme, caza, repite." },
	["cu_ssg08_scorpion"] = { name = "Golpe letal", desc = "Se ha personalizado con el dibujo de un escorpión emperador entre unas corrientes de arena aplicado a esta SSG 08.\n\nEso va a picar..." },
	["hy_desert_bloom"] = { name = "Parcheado", desc = "Se ha pintado con espray usando un patrón de flores brillantes sobre una base de aspecto seco y agrietado.\n\nLa naturaleza siempre gana." },
	["am_ddpat_purple"] = { name = "DDPAT púrpura", desc = "Se ha pintado usando un patrón disruptivo digital (DDPAT) hidrográfico púrpura.\n\nPara cuando estás lo suficientemente cerca como para notar los píxeles, ya es demasiado tarde." },
	["gs_awp_hydra"] = { name = "Hidra del desierto", desc = "Se ha personalizado esta AWP con serpientes de oro enroscadas lo largo del arma. La mira, el cañón y la culata se han pintado de blanco.\n\nSi le cortas la cabeza, le crecerán dos más. ¿Es un riesgo que estás dispuesto a correr? —Valeria Jenner (revolucionaria)" },
	["gs_deagle_fennec"] = { name = "Zorro fénec", desc = "Se ha personalizado con un zorro fénec en el cañón. La empuñadura tiene diseños azulados y naranjas.\n\nLo mejor que puedes escuchar." },
	["am_mirage_flowers_metalic"] = { name = "Caja de música", desc = "Se ha aplicado un patrón de damasco dorado.\n\nPelea por gusto." },
	["am_moro_textile_bright"] = { name = "Enredaderas elegantes", desc = "Se ha aplicado un patrón de flores rojo y verde.\n\nAlgunos ven una incomodidad, yo veo belleza." },
	["am_navy_shine"] = { name = "Brillo azul marino", desc = "Se ha aplicado un patrón de camuflaje variado azul marino anodizado.\n\n¿Por qué tan azul?" },
	["gs_aug_sand_storm"] = { name = "Tormenta de arena", desc = "Se ha aplicado una pintura personalizada con una representación de una tormenta de arena pasajera bajo un cielo nocturno.\n\nHay belleza por dondequiera, solo hay que saber dónde buscarla." },
	["sp_dry_wood"] = { name = "Madera a la deriva", desc = "Se ha pintado con espray usando un patrón que se asemeja a la madera seca.\n\nToca madera; porque vas a necesitar toda la suerte del mundo. —Felix Riley (oficial al mando)" },
	["sp_desert_skulls_dawn"] = { name = "Sequía", desc = "Se ha pintado con espray usando un patrón de cráneos de animales.\n\nTodo lo que queda." },
	["sp_mirage_flowers_tan"] = { name = "Damasco siena", desc = "Se ha pintado con espray patrón de flores de damasco sobre la base bronceada del arma.\n\nTécnicamente, los mejores camuflajes son aquellos que no ves..." },
	["sp_moro_carving_burnt"] = { name = "Humidor", desc = "Se ha pintado con espray usando un patrón de vides y flores en tonos marrones y púrpuras.\n\n«¿Qué guardan ahí?»." },
	["sp_moro_textile_green_vine"] = { name = "Crecimiento verde", desc = "Se ha pintado con espray usando un patrón de flores en varios tonos verdes.\n\nMolestia para algunos; belleza para otros." },
	["gs_mp5_neon_flektarn"] = { name = "Oasis de óxido", desc = "Se ha aplicado un patrón de camuflaje en tonos naranja y azul verdoso brillantes. El arma se ha decorado con detalles dorados.\n\nUn arma tan discreta no debería ser tan ruidosa." },
	["aa_desert_bloom_bright"] = { name = "Floración desértica", desc = "Se ha pintado con espray usando un patrón de flores brillantes sobre una base de aspecto seco y agrietado.\n\nLa naturaleza siempre gana." },
	["hy_lizard_skin"] = { name = "Anolis", desc = "Se ha aplicado un patrón hidrográfico de un lagarto a escala en tonos verde y negro.\n\nPintado a escala." },
	["cu_ak-47_phantom_disruptor"] = { name = "Disruptor fantasma", desc = "Se ha personalizado con la imagen de una criatura quimérica sobre una base negra mate.\n\n«Puede ser lo que quiera, donde quiera, y aun así elige morar contigo...»" },
	["gs_aug_thunderstorm"] = { name = "Gato malo", desc = "Su capa base de color plomo se ha rematado con detalles dorados, flechas apuntando hacia atrás y un gato de aspecto malvado.\n\nLos truenos son el ronroneo de la naturaleza." },
	["cu_awp_vein"] = { name = "Capilares", desc = "Se ha aplicado una pintura personalizada compuesta por una intrincada red de venas de colores celeste y coral.\n\nLa llave al corazón de un agente cuesta 4750 dólares." },
	["cu_cz75_cerakote"] = { name = "Gastado", desc = "El acabado cerámico de color cobrizo se está gastando con el uso.\n\nMejora con la edad." },
	["cu_deagle_replica"] = { name = "Capa azul", desc = "Se ha laminado y rellenado con una empuñadura de color ceniza y corredera azul.\n\nADVERTENCIA: Esto es una réplica y no se ha probado con munición real." },
	["cu_m4a1s_csgo2048"] = { name = "Jugador dos", desc = "Se ha personalizado con colores brillantes y versiones de dibujos animados de un antiterrorista GIGN y Pop Dog.\n\nPress Start..." },
	["cu_mac10_nacre"] = { name = "Discotech", desc = "Se ha personalizado usando un acabado nacarado para asemejarse a un vinilo holográfico autoadhesivo. Su apariencia cambia dependiendo de la luz.\n\nCuidado al apuntar esa cosa." },
	["gs_mag7_justice"] = { name = "Justicia", desc = "Se ha personalizado usando la carta del tarot de la Justicia como inspiración.\n\nJuicio para todos." },
	["cu_mp5_desert_strike"] = { name = "Golpe desértico", desc = "Se ha personalizado con un diseño de alta tecnología en color bronce y negro.\n\nGolpea el primero, golpea el último." },
	["cu_negev_prototype"] = { name = "Prototipo", desc = "Se ha pintado provisionalmente en blanco y rojo mientras se termina su diseño.\n\nv1.2.1, PAT. 3422107" },
	["aq_p2000_acid_clover"] = { name = "Grabado al ácido", desc = "Se le ha aplicado un patrón multicolor aleatorio con un singular trébol de cuatro hojas.\n\n¿Te sientes afortunado?" },
	["gs_r8_leviathan"] = { name = "Forjado óseo", desc = "En la empuñadura de este revólver se han dibujado los restos de una antigua criatura marina.\n\nLo que acecha por debajo." },
	["cu_sawedoff_apocalypto"] = { name = "Apocalipsis", desc = "Se ha personalizado con la imagen de una mano en descomposición tratando de alcanzar un planeta con apariencia de bomba sobre un fondo espacial púrpura.\n\n«¿Tienes hora?»" },
	["gs_scar20_enforcer"] = { name = "Ejecutor", desc = "Se ha pintado meticulosamente un gradiente de puesta de sol en diferentes partes del arma.\n\nSoluciones automáticas de SYMMETRY SMART GUN CORP." },
	["cu_sg553_darkwing"] = { name = "Alaoscura", desc = "Se le ha aplicado una pintura personalizada roja y negra con plumas y otros detalles oscuros.\n\nCon vista de águila y extremadamente letal, no hallarás un ave no voladora más peligrosa." },
	["cu_ssg08_fever_dream"] = { name = "Sueño febril", desc = "Se ha personalizado con delirantes dibujos rosas, azules y morados sobre una base negra.\n\n¡¡J@@JJ@J@J@JA!!" },
	["cu_glock18_warmaiden"] = { name = "Reina de las balas", desc = "Se ha personalizado con el dibujo de unas balas con gesto amenazador y una Doncella de la Guerra con chaqueta rosa sobre una base amarilla y negra.\n\n«Dejaré de disparar cuando dejes de hablar»." },
	["cu_negev_ultralight"] = { name = "Ultraligero", desc = "Se ha personalizado con un patrón de camuflaje gris digital y flechas de un tono verde azulado.\n\n«Ligero»." },
	["cu_ak47_anubis"] = { name = "Legión de Anubis", desc = "Se ha aplicado una pintura personalizada con los dioses egipcios Ra y Anubis, representando el equilibrio entre la vida y la muerte.\n\nPuede inclinar la balanza a tu favor." },
	["aq_p2000_lost_world"] = { name = "Retorcido", desc = "Se ha pintado a mano el dibujo de un alien retorciéndose a lo largo de la empuñadura de esta P2000.\n\nDesde las oscuras esquinas del Vacío infinito." },
	["cu_mag7_monster_call"] = { name = "Monstruo marino", desc = "Se ha personalizado con una base azul y criaturas submarinas en primer plano.\n\nSiempre hay un pez más grande y más verde." },
	["cu_printstream"] = { name = "PrintStream", desc = "Se ha personalizado con un austero diseño blanco y negro, y un acabado con brillos nacarados.\n\nWHITE_1; BLACK_1; PEARLESCENT_1;" },
	["cu_glock_eyecontact"] = { name = "Vogue", desc = "Se ha personalizado esta Glock al estilo del arte pop con unos ojos que adornan la corredera del arma.\n\n«¡Mírame a los ojos cuando te disparo!»." },
	["gs_tec9_guerilla"] = { name = "Hermano", desc = "Se ha personalizado con un diseño de camuflaje marrón y dorado y un parche del bando T en la empuñadura.\n\nOs cubro la espalda." },
	["cu_mac10_isoonna"] = { name = "Seducción", desc = "Se ha personalizado esta MAC-10 con el dibujo de una mujer con vestido verde azulado, que puede ser Iso Onna, posando frente a un koi mientras rompen las olas de fondo.\n\nLa belleza deviene en derramamientos de sangre." },
	["gs_sg553_rusty"] = { name = "«Ol' Rusty»", desc = "Originalmente era acero pulido, pero la humedad cambió el aspecto del arma. Se ha estampado «Ol' Rusty» sobre el guardamonte.\n\nUn fiel y oxidado amigo." },
	["cu_ssg08_mainframe"] = { name = "Ordenador central 001", desc = "Se ha aplicado un patrón personalizado de alta tecnología en tonos azules y morados.\n\n¿Qué secretos esconde _001?" },
	["cu_p250_cassette"] = { name = "VHS", desc = "Se ha aplicado una pintura personalizada inspirada en las cajas de las cintas de vídeo de los 90.\n\nRevisa las cintas." },
	["gs_p90_container"] = { name = "Vagón", desc = "Se ha personalizado con pintadas que recuerdan a los grafitis de los trenes abandonados.\n\nEl arte no es un crimen." },
	["cu_xm1014_amulet_blue"] = { name = "Sepultada", desc = "Sepultada durante milenios. Esta XM-1014 recientemente desenterrada fue personalizada con tonos azules y detalles egipcios.\n\nIdeal para un faraón." },
	["cu_m4a4_queenfairy"] = { name = "Hada de los Dientes", desc = "Se ha aplicado una pintura personalizada con incisivos, molares y una versión escalofriante del Hada de los Dientes.\n\nNadie dijo que tus dientes te pertenecieran…" },
	["gs_galil_phoenix"] = { name = "Phoenix Connexion", desc = "Se han pintado con espray los logotipos de la facción Phoenix sobre sus capas blanca y roja.\n\nEstán trayendo el armamento pesado." },
	["gs_bizon_hellraider"] = { name = "Rúnica", desc = "Se ha personalizado con runas de origen desconocido y un cargador que recuerda a una ametralladora Gatling.\n\nDales caña." },
	["gs_mp5sd_wasteland_legacy"] = { name = "Canibalización", desc = "20 años de diseños de MP5 han hecho posible esta pintura personalizada, compuesta por cinta americana, bridas y pegamento.\n\nSi no sale a la primera (ni a la segunda ni a la tercera)…" },
	["gs_awp_exoskeleton"] = { name = "Exoesqueleto", desc = "El metal de este AWP se ha prensado meticulosamente con un patrón de calaveras entrelazadas.\n\nInclinación total." },
	["gs_cz75_vendetta"] = { name = "Venganza", desc = "A lo largo de los años se ha grabado, pintado y endurecido con el uso.\n\nDe mis manos frías y muertas." },
	["hy_p90_dino_rampage"] = { name = "Chocoexterminio", desc = "Se le ha aplicado un patrón hidrográfico de color oliva y oro con dinosaurios de dibujos animados.\n\nDinosaurios, alienígenas y cometas... ¡Buah!" },
	["gs_dual_elites_dezastre"] = { name = "Desastre", desc = "Se han personalizado con un diseño de alta tecnología en negro, blanco y rojo.\n\nSigue siempre las indicaciones del fabricante para un uso seguro." },
	["cu_five_seven_diary"] = { name = "Cuento de hadas", desc = "Se ha personalizado con princesas, unicornios, flores hermosas y un castillo a lo lejos.\n\n«Me da que hoy va a ser un buen día»." },
	["gs_g3sg1_disrupt"] = { name = "Malla digital", desc = "Se ha pintado con un patrón disruptivo digital (DDPAT) personalizado de color rojo, blanco y gris marengo.\n\nDisruptivamente intrincado." },
	["gs_galil_vandal"] = { name = "Vándalo", desc = "Su capa verde se ha cubierto con dibujos blancos dispersos.\n\nCada dibujo cuenta una historia, algunas más memorables que otras." },
	["cu_p250_infect"] = { name = "Contaminante", desc = "Su capa superior granate se ha carcomido, dejando un arma en descomposición con poco tiempo de vida.\n\nEs tan malo como parece." },
	["cu_m249_deep_relief"] = { name = "Relieve profundo", desc = "La capa superior con tonos arena da paso a verdes más oscuros con cada capa. Una pintura personalizada que puedes sentir.\n\n¿Cuántas balas para llegar al centro?" },
	["cu_m4a4_cyberpunk"] = { name = "Ciberseguridad", desc = "Se ha personalizado la base negra de este M4 con rayas diagonales azules, rojas y doradas.\n\nYA DISPONIBLE EN TU TIENDA CIBERNÉTICA LOCAL." },
	["gs_mp5_conditionzero"] = { name = "Condition Zero", desc = "Su pintura personalizada azul y amarilla rinde homenaje a Counter-Strike: Condition Zero.\n\n«¡RPG a las tres en punto! ¡Evasión!»" },
	["cu_nova_polymer"] = { name = "Polímero transparente", desc = "Se ha personalizado para que parezca que la carcasa es transparente y que te deja ver el interior del arma.\n\nTus objetivos están claros." },
	["cu_glock_noir"] = { name = "Neonoir", desc = "Se ha personalizado con una mujer estilizada en azul-magenta sobre un fondo gris.\n\n«Las cosas iban cambiando y ella empezó a esperanzarse; pero luego miró hacia arriba»." },
	["cu_ssg08_chromatic"] = { name = "Paralaje", desc = "Los colores en esta pintura personalizada se han aplicado en posiciones desplazadas, dando lugar a una apariencia espectacular.\n\nDispara dos veces, mata una." },
	["gs_ump_gold_bismuth"] = { name = "Bismuto dorado", desc = "Se ha intercalado un patrón bismuto dorado entre capas de color blanco y negro.\n\n«Tienes gustos caros...»" },
	["cu_usp_krokos"] = { name = "Mezcla monstruosa", desc = "Se le ha aplicado una pintura personalizada con un monstruo verde y otros motivos inspirados en el arte urbano popular. El silenciador tiene un acabado nacarado.\n\nX__X" },
	["cu_csgo_camo"] = { name = "Ofensiva global", desc = "Se ha personalizado con un camuflaje azul, amarillo y gris.\n\n«Estableceremos el primer contacto en Harz, Alemania, a las 17:25 GMT+1...»" },
	["am_authority_brown"] = { name = "Aforamiento", desc = "Se le ha aplicado un patrón amarronado de sellos, garabatos y texto de la carta magna.\n\nToda persona está sujeta al debido proceso, incluso tú." },
	["hy_authority_purple"] = { name = "Carta Magna", desc = "Se le ha aplicado un patrón morado de sellos, garabatos y texto de la carta magna.\n\nTodos tenemos derecho a un juicio justo, incluido tú." },
	["am_intelligence_grey"] = { name = "Vigilancia", desc = "Se le ha aplicado una pintura personalizada con un HUD a vista de pájaro superpuesto a un patrón de mapa.\n\nObjetivo localizado." },
