	["cu_ump_crime_scene"] = { name = "Escena del crimen", desc = "Se ha personalizado con una cinta de precaución amarilla sobre una base roja y azul.\n\nDespejen la zona, tenemos trabajo para hacer…" },
	["am_m4a1s_bluesmoke"] = { name = "Fósforo azul", desc = "Se han aplicado varias capas de pintura azul metálico sobre una capa base azul cromo.\n\n¿El azul te deprime?" },
	["cu_ak_xray"] = { name = "Rayos X", desc = "AK-47 con pintura personalizada que muestra su interior con rayos X.\n\nCuando todo se revele puede que no te guste lo que veas." },
	["am_heist_plans_green"] = { name = "Robo", desc = "Su base se ha pintado con un patrón de mapa en beige anodizado. Parece que alguien dibujó en el mapa desde que se aplicó.\n\nPlanificar es una cosa, llevar a cabo es otra." },
	["am_heist_plans_purple"] = { name = "Robo nocturno", desc = "Su base se ha pintado con un patrón de mapa en morado anodizado. Parece que alguien dibujó en el mapa desde que se aplicó.\n\nPlanificar es una cosa, llevar a cabo es otra." },
	["am_heist_plans_yellow"] = { name = "Robo de la caja fuerte", desc = "Su base se ha pintado con un patrón de mapa en gris anodizado. Parece que alguien dibujó en el mapa desde que se aplicó.\n\nPlanificar es una cosa, llevar a cabo es otra." },
	["hy_houndstooth_brown"] = { name = "Pata de gallo", desc = "Se ha aplicado un patrón hidrográfico de pata de gallo marrón.\n\nEquípate para el rol que quieres, no para el que tienes." },
	["gs_mac10_snake"] = { name = "Serpientes calientes", desc = "Se ha personalizado con un patrón de piel de serpiente de bronce y otra enroscada bajo la culata en forma de alambre.\n\nEse silbido son las balas que pasan a tu alrededor." },
	["hy_phoenix_tags_lilac"] = { name = "Tiza Phoenix", desc = "Se le ha aplicado un patrón multicolor con varios grafitis de la Conexión Phoenix.\n\n«¡Eres un estúpido al creer que vas a ganar!»" },
	["am_phoenix_tags_blue"] = { name = "Rotulador de Phoenix", desc = "Se le ha aplicado un patrón multicolor con varios grafitis de la Conexión Phoenix.\n\n«Ahora no pueden detenernos»." },
	["hy_phoenix_tags_red"] = { name = "Plantilla de Phoenix", desc = "Se le ha aplicado un patrón multicolor con varios grafitis de la Conexión Phoenix.\n\n«No se nos puede matar»." },
	["sp_phoenix_tags_purple"] = { name = "Luz negra Phoenix", desc = "Se le ha aplicado un patrón multicolor con varios grafitis de la Conexión Phoenix.\n\n«Bailaremos sobre vuestras tumbas»." },
	["hy_tigers_tan"] = { name = "Emboscada arcillosa", desc = "Se ha aplicado un patrón hidrográfico atigrado marrón.\n\nNormalmente, depredadores solitarios, pero harán una excepción contigo." },
	["am_tigers_brown"] = { name = "Foso del tigre", desc = "Se ha aplicado un patrón hidrográfico atigrado marrón sobre una base negra.\n\nDonde sea menos aquí." },
	["am_tigers_blue"] = { name = "Tigre de seda", desc = "Se ha aplicado un patrón hidrográfico atigrado sobre una base azul.\n\nTesoro nacional —el tigre, no el AWP." },
	["cu_m4a1_snake"] = { name = "Bienvenidos a la jungla", desc = "Esta pintura personalizada muestra una iconografía antigua con una víbora amarilla y un fondo pintoresco de jungla.\n\nAtaca sin avisar." },
	["cu_ak_jaguar"] = { name = "Panthera onca", desc = "La madera de la culata y el guardamano presentan hermosas tallas, que a menudo roban el protagonismo al jaguar que lleva pintado en el lateral.\n\n¿Quién dijo que no puedes cambiar de manchas?" },
	["cu_jaguar_p90"] = { name = "Huye y escóndete", desc = "Se ha pintado a medida con un patrón de jaguar y terminado con cuero granulado.\\\n\nCualquiera puede comprar lo que está de moda, pero muy pocos poseen el verdadero estilo." },
	["aa_ancient_brown"] = { name = "Tierra ancestral", desc = "Se le ha aplicado un patrón marrón y dorado con intrincadas líneas y criaturas ancestrales.\n\nEl privilegio es tuyo." },
	["am_ancient_warm"] = { name = "Tradición ancestral", desc = "Se ha aplicado un patrón verde pálido, beige y rojo de mosaicos antiguos.\n\n«Aún hay esperanza, incluso para quienes empezaron su jornada yendo por el camino equivocado»." },
	["aa_ruins_green"] = { name = "Ruinas exuberantes", desc = "Este patrón adornado con hojas muestra un fondo oscuro de dioses antiguos.\n\n«Atentos todos, nos están observando...»." },
	["hy_ancient_tiles_peach"] = { name = "Recuerdos del pasado", desc = "Se ha aplicado un patrón verde pálido y beige de mosaicos antiguos.\n\n«Aún hay esperanza, incluso para quienes empezaron su jornada yendo por el camino equivocado»." },
	["am_gold_brick"] = { name = "Ladrillo dorado", desc = "Su acabado dorado brilla a la luz y resalta cada hermosa imperfección del arma.\n\nNo todo lo que brilla..." },
	["sp_ancient_bright"] = { name = "Visiones ancestrales", desc = "Se le ha aplicado un patrón verde y rojo con intrincadas líneas y criaturas ancestrales.\n\nEl privilegio es tuyo." },
	["hy_ruins_red"] = { name = "Ruinas crepusculares", desc = "Este patrón adornado con hojas muestra un fondo oscuro de dioses antiguos.\n\n«Atentos todos, nos están observando...»." },
	["am_jade"] = { name = "Jade tallado", desc = "Se ha pintado con una base de cromo y decorada con un efecto de pintura anodizada verde jade transparente.\n\nLo mejor de lo mejor." },
	["am_ancient_wine"] = { name = "Ritual ancestral", desc = "Se le ha aplicado un patrón marrón y plateado con intrincadas líneas y criaturas ancestrales.\n\nEl privilegio es tuyo." },
	["hy_drywood_green"] = { name = "Hierba alta", desc = "Se ha aplicado un patrón hidrográfico de hierba verde.\n\nNunca pasa nada bueno en la hierba alta." },
	["am_black_panther"] = { name = "Camuflaje pantera", desc = "Se le ha aplicado un acabado con manchas de pantera negra.\n\nEncuentra tu zona oscura." },
	["gs_ak47_professional"] = { name = "Pizarra", desc = "Se le ha aplicado una pintura personalizada que solo se puede describir como «negro sobre negro sobre negro pizarra».\n\n«Llamad a un médico, porque lo van a necesitar»." },
	["cu_cz75_whirlwind"] = { name = "Circaetus", desc = "Se ha personalizado esta CZ75 con el dibujo de un ave de presa cazando a una serpiente.\n\nUn devorador de serpientes... excelente banda sonora." },
	["cu_mp9_food_chain"] = { name = "Cadena alimenticia", desc = "Este colorido MP9 se ha personalizado con una cadena alimenticia de monstruos.\n\nComer o ser comido." },
	["cu_galil_chroma_pink"] = { name = "Aberración cromática", desc = "Los colores en esta pintura personalizada se han aplicado en posiciones contrapuestas, dando lugar a una apariencia espectacular.\n\n(No demasiado) agradable a la vista..." },
	["gs_glock_polymer"] = { name = "Polímero transparente", desc = "Se ha pintado la corredera de color bronce y el gatillo de azul. La empuñadura de la pistola está fabricada de un polímero transparente.\n\nLo que ves es lo que hay." },
	["gs_usps_hangedman"] = { name = "El Traidor", desc = "Se ha personalizado usando como inspiración la carta de tarot del Colgado.\n\nLos errores del pasado son los cimientos de los éxitos del futuro." },
	["cu_m4a4_love"] = { name = "Colores vivos", desc = "Se ha personalizado con colores neón y cubierto con diseños dibujados a mano.\n\nLucha por uno, lucha por todos." },
	["gs_m249_combine"] = { name = "R.P.R.V.", desc = "Esta M249 se ha personalizado con pintura gris plomo y un logotipo de la Alianza.\n\nRifle de Pulsos Reglamentario de la Vigilancia." },
	["cu_negev_devtexture"] = { name = "dev_texture", desc = "Se ha personalizado con una base naranja y texturas de desarrollo grises.\n\nHora de hammer.exe." },
	["gs_p250_cybershell"] = { name = "Ciberescudo", desc = "Se ha personalizado con una corredera gris y una empuñadora texturizada con hexágonos.\n\nRepresentas la primera línea de nuestra ciberseguridad." },
	["cu_mac10_portable"] = { name = "Machacabotones", desc = "Se ha personalizado con un aspecto que recuerda a un mando de consola, con varios botones en una carcasa de polímero.\n\nDándole a la «guerra de consolas» un significado completamente nuevo." },
	["aq_xm1014_punk"] = { name = "TQM", desc = "Se le ha aplicado un patrón de estilo punk con colores vivos.\n\nSí, claro…" },
	["gs_r8_rustking"] = { name = "Carne de desguace", desc = "Se ha cubierto una base azul con dibujos y escritos a mano en plata. El cilindro y la mirilla frontal se han pintado de rojo.\n\nIdeal para un rey." },
	["gs_sg553_deathmetal"] = { name = "Metal pesado", desc = "Se han aplicado detalles en naranja y amarillo sobre su base negra. Se han pintado tres estrellas en la parte trasera de la culata.\n\n«Uno de estos pernos era el botón de Ráfaga, pero no recuerdo cuál…»." },
	["am_ump45_electrowave"] = { name = "Oscilador", desc = "Se le ha aplicado un patrón abstracto en tonos naranja, negro y gris.\n\nTodo el mundo tiene un punto de ruptura; ¿cuál es el tuyo?" },
	["cu_deag_trigger_discipline"] = { name = "Disciplina de gatillo", desc = "Se ha personalizado el cañón con un patrón isométrico en tonos rosas. El martillo y la mira frontal se han pintado en azul celeste.\n\nLa paciencia es la madre de todas las ciencias." },
	["sp_nova_wind_dispersal"] = { name = "Viento", desc = "Se ha pintado con espray un patrón abstracto de un campo de dientes de león.\n\nFuera de mi jardín." },
	["am_nuclear_pattern4_famas"] = { name = "Cataclismo", desc = "Se ha pintado con un patrón de símbolos de peligro nuclear usando tonos metalizados.\n\nMantente alejado durante un tiempo..." },
	["aa_spacerace_orange"] = { name = "Carrera espacial", desc = "Se ha aplicado un patrón con temática espacial, compuesto por estrellas y satélites.\n\nEnviar y recibir." },
	["sp_spacerace_blue"] = { name = "Sputnik", desc = "Se ha aplicado un patrón de temática espacial consistente en estrellas y satélites.\n\nEnviar y recibir." },
	["sp_technowar_red"] = { name = "POP efervescente", desc = "Se le ha aplicado un patrón bronce y rojo inspirado en tecnología retro.\n\nLas guerras se ganan con información." },
	["hy_technowar_rwb"] = { name = "POP AWP", desc = "Se le ha aplicado un patrón blanco, rojo y azul inspirado en tecnología retro.\n\nLas guerras se ganan con información." },
	["hy_trainarchitect_green"] = { name = "Sarga primaveral", desc = "Se ha aplicado un patrón hidrográfico en blanco y verde con diferentes diseños.\n\nÚltima oportunidad, el tren sale de la estación." },
	["hy_trainarchitect"] = { name = "Sarga otoñal", desc = "Se ha aplicado un patrón hidrográfico en blanco y verde con diferentes diseños.\n\nÚltima oportunidad, el tren sale de la estación." },
	["gs_m4a4_coalition"] = { name = "La Coalición", desc = "Se ha aplicado una pintura personalizada en dorado y negro con triángulos entrelazados y azul acentuado.\n\nEsta alianza es temporal, pero me estás haciendo reconsiderarla..." },
	["cu_mac10_propaganda"] = { name = "Propaganda", desc = "Se ha personalizado con la forma de los ojos y varias palabras en ruso, incluyendo «control» y «miedo».\n\nNo les crees, ¿verdad?" },
	["gs_train_cz75"] = { name = "Sindicato", desc = "Se ha aplicado una pintura en dorado y negro personalizado con triángulos entrelazados y brillos azules.\n\nJuntos llegaremos." },
	["hy_ak47lam_green"] = { name = "Laminado verde", desc = "Se ha aplicado un laminado verde a la culata, empuñadura y guardamanos de este AK.\n\nLos detalles importan." },
	["sp_galil_caution"] = { name = "¡PRECAUCIÓN!", desc = "Se ha pintado a espray con galones negros y amarillos de alto contraste.\n\nConsuélate sintiéndote incómodo." },
	["hy_geometric_steps_pearl"] = { name = "Terraza de prismas", desc = "Se ha aplicado un patrón de ilusión óptica nacarado.\n\n¿Dónde empieza y dónde acaba? Si es que acaba..." },
	["gs_m4a1_vertigo"] = { name = "Peligro inminente", desc = "Se le ha aplicado una pintura personalizada de señales de seguridad multicolores.\n\nNo digas que no te lo advertí… —Booth (traficante de armas)" },
	["hy_vertigogeo"] = { name = "Esquemático", desc = "Se ha aplicado un patrón multicolor de una cuadrícula isométrica.\n\nTú recuerdas el dolor, yo la victoria." },
	["hy_vertigospray_blue"] = { name = "Planes de acción", desc = "Se le ha aplicado un patrón hidrográfico de los planos de construcción, climatización y cableado del piso 51.\n\nTodo según el plan…" },
	["hy_vertigoillusion"] = { name = "Entorno de trabajo", desc = "Se ha aplicado un patrón hidrográfico de ilusión óptica gris y bronce.\n\n¿Lo has visto bien?" },
	["hy_vertigoillusion_yellow"] = { name = "Enclavamiento", desc = "Se ha aplicado un patrón hidrográfico de una ilusión óptica en tonos amarillo y azul.\n\n¿Le has echado un buen vistazo?" },
	["hy_vertigospray"] = { name = "Infraestructura", desc = "Se le ha aplicado un patrón hidrográfico de los planos de construcción, climatización y cableado del piso 51.\n\nTodo según el plan…" },
	["aa_vertigogeo_neon"] = { name = "Arquitecto digital", desc = "Se ha aplicado un patrón multicolor con una cuadrícula en vista isométrica.\n\nTú recuerdas el dolor, yo recuerdo la victoria." },
	["cu_sg553_caution"] = { name = "Riesgo de pago", desc = "Se ha personalizado con una base amarilla y detalles naranjas de cono de tráfico.\n\nLanza advertencias al viento, en forma de bala." },
	["aa_wiring"] = { name = "Caja de fusibles", desc = "Se ha aplicado un patrón anodizado de un diagrama de cableado.\n\nConsulta con un electricista antes de ponerte a hacer chapuzas." },
	["aa_wiring_yellow"] = { name = "Cableado defectuoso", desc = "Se ha aplicado un patrón de un diagrama de cableado.\n\nPor favor, consulte a un electricista antes de llevar a cabo sus propias reparaciones." },
	["sp_engine_dirty"] = { name = "Cambio de aceite", desc = "Se ha pintado con espray usando un patrón de componentes de motor.\n\n«No tiene buena pinta...»" },
	["aa_engine_performance"] = { name = "Mecanismo", desc = "Se ha aplicado un patrón anodizado de componentes de motor.\n\n«No tiene buena pinta...»" },
	["cu_vertigo_fiveseven"] = { name = "Peligro de caída", desc = "Se ha personalizado con franjas multicolor y las palabras inglesas «FALL» y «CONSTRUCTION».\n\nCuidado, no te caigas." },
	["sp_tire_tread_red"] = { name = "Neumático rojo", desc = "Se ha pintado con espray usando un patrón de marcas de neumático de color negro y rojo.\n\nQuema goma." },
	["sp_tire_tread_blue"] = { name = "Neumático azul", desc = "Se ha pintado con espray usando un patrón de marcas de neumático de color negro y azul.\n\nQuema goma." },
	["gs_ak47_abstract"] = { name = "Cubismo", desc = "Se ha personalizado con representaciones cubistas de la unidad de élite terrorista entrando en Dust II.\n\n«Todo lo que puedas imaginar es real»." },
	["gs_aug_plague"] = { name = "Plaga", desc = "Se ha personalizado con un motivo inspirado en fantasía oscura y terror.\n\n«Como una polilla a una llama»." },
	["gs_mag7_bismuth"] = { name = "Espectro Bi83", desc = "Se ha personalizado para asemejarse a los cristales de bismuto iridiscentes.\n\n¿Por qué las cosas más raras son las más bellas?" },
	["cu_deagle_kitch"] = { name = "Ocean Drive", desc = "Se ha personalizado con colores sobresaturados y diversas formas.\n\nEs imposible fallar, ya sea combinando colores o usando el arma." },
	["gs_dual_berettas_tread"] = { name = "Dibujo", desc = "Se ha grabado a mano un patrón de ondas en ambas Berettas. Las correderas están bañadas en oro.\n\nLos detalles más finos son los que más tiempo llevan, los que más hacen y los que menos se notan." },
	["cu_famas_spectron"] = { name = "ZX Spectron", desc = "Esta pintura personalizada se inspira en los ordenadores para juegos de antaño. Su base negra se ha rematado con detalles multicolor.\n\nClive estaría orgulloso." },
	["gs_five_seven_efusion"] = { name = "Protocolo de impulso", desc = "Se ha personalizado con colores de alto contraste. El icono del robot de la mira trasera coincide con la ilustración lateral.\n\nIdentificación: MECHA 0272." },
	["aa_mp9_fuji_pink"] = { name = "Monte Fuji", desc = "Se ha aplicado un diseño acrílico del Monte Fuji. La pintura se utilizó como un patrón para que cada arma sea diferente.\n\nEl sol rojo sale por el horizonte." },
	["cu_g3sg1_tacticalmap"] = { name = "Llevando las cuentas", desc = "Se ha personalizado usando como inspiración el mapa táctico de Danger Zone.\n\nYa tienes toda la información que necesitas —Franz Kriegeld (estratega de Phoenix)" },
	["cu_mp7_khaki"] = { name = "Guerrilla", desc = "Esta pintura personalizada muestra una base verde, detalles en negro y un mono orientado hacia delante.\n\nEl león nunca fue el rey de la selva." },
	["gs_m4_flowers"] = { name = "Lirio araña", desc = "Se ha personalizado con una base azul oscura y lirios araña rojos sobre el cargador y el gatillo.\n\nLos colores brillantes son una advertencia, no una invitación." },
	["cu_mac10_toybox"] = { name = "Caja de juguetes", desc = "Esta pintura personalizada muestra coloridos dibujos de pollos, extraterrestres, kits de desactivación...\n\nRecoge tus cosas." },
	["gs_bizon_flasher"] = { name = "Lumen", desc = "Se ha personalizado en color negro con detalles rojos. El cargador se ha pintado de modo que parezca una linterna.\n\nIlumina el camino." },
	["cu_glock_snackattack"] = { name = "Tentempiés", desc = "Se ha personalizado para que se parezca a las chucherías de comida basura colgando de resina.\n\nEl bocadillo que contraataca." },
	["cu_ssg_overtake"] = { name = "Turbo Peek", desc = "Coches deportivos compiten a los lados en este rifle de francotirador de color neón.\n\nDerrapar es un derecho, no un privilegio." },
	["cu_usp_black_lotus"] = { name = "Loto negro", desc = "Se ha personalizado con una flor de loto negra en la corredera.\n\nAñade 3 víctimas durante la ronda de pistolas, luego se descarta." },
	["gs_xm1014_watchdog"] = { name = "Guardián", desc = "Se ha personalizado con una base de color verde jade y dos leones guardianes vigilando en ambas direcciones. Se ha pintado un tercer león debajo de la mira trasera.\n\nProteger y prosperar." },
	["cu_ak47_nightwish"] = { name = "Deseo nocturno", desc = "Se ha personalizado con los diseños de un ciervo y varios atrapasueños en colores neón y un acabado de efecto nacarado.\n\nHe estado guardando esto para una ocasión especial... —Booth (traficante de armas)" },
	["cu_bizon_spacecat"] = { name = "Gato cósmico", desc = "Se ha personalizado con una representación de arte espacial de un gato sobre una base negra y morada.\n\nLas zarpas de la creación." },
	["cu_elites_beware"] = { name = "Melondrama", desc = "Estas se han personalizado con una representación distorsionada y aterradora de una sandía sobre un fondo azul marino.\n\nDulces sueños..." },
	["gs_famas_rapid_eyes"] = { name = "Movimientos oculares rápidos", desc = "Se ha personalizado con celdas rosas conectadas entre sí que llevan pintados ojos de aspecto realista.\n\nFrenéticos están los ojos del soñador." },
	["cu_fiveseven_alpha_omega"] = { name = "Garabatos", desc = "Se le han pintado a mano diseños personalizados con trazos gruesos sobre una base de color amarillo y verde azulado.\n\nEl primero y el último." },
	["cu_g3sg1_glade"] = { name = "Claro de ensueño", desc = "Se ha personalizado con el dibujo de un bosque verde que da paso a un cielo azul en el centro del diseño.\n\n«¡Ahí, un claro en el bosque!»" },
	["gs_m4a1s_insomnia"] = { name = "Terror nocturno", desc = "Se ha personalizado con garabatos de pensamientos dispersos de la mente de un insomne.\n\nEn el seno de la mansa noche, es cuando busco la luz." },
	["cu_mac10_pixie"] = { name = "Atrapados", desc = "Se le ha aplicado una imagen personalizada de unos duendecillos atrapados en el interior del arma y deseosos de escapar.\n\nAún tienen que encontrar a alguien a quien no puedan engañar." },
	["cu_mag7_predictor"] = { name = "Predicción", desc = "Esta pintura personalizada muestra un pulpo que extiende sus tentáculos hacia la placa de «VICTORIA» al pedirle que prediga el resultado de una partida.\n\nTengo un buen presentimiento sobre esto..." },
	["cu_mp7_fear"] = { name = "Aparición abisal", desc = "Se ha personalizado con una imagen de almas errantes cayendo en un pozo de pesadillas.\n\nNo puedes huir de tu destino." },
	["gs_mp9_starlight"] = { name = "Protector de luz estelar", desc = "Esta pintura personalizada muestra un unicornio con crin arcoíris sobre una base blanca. Se ha aplicado un acabado con detalles dorados.\n\nQue la deidad te proteja si la deidad te elige." },
	["am_xm_zombie_offensive"] = { name = "Ofensiva zombi", desc = "Se le ha aplicado un patrón personalizado de agentes de Counter-Strike zombificados.\n\n«Este juego está no muerto»." },
	["cu_usp_to_hell"] = { name = "Billete al infierno", desc = "Se ha personalizado con diseños técnicos blancos sobre una base negra.\n\nMuerte de diseño." },
	["gs_mp5_kid_necronomicon"] = { name = "Necro jr.", desc = "Se ha personalizado con dibujos de criaturas fantásticas y un pollo en el silenciador.\n\n¡Se acabaron los juegos!" },
	["cu_p2k_flying_dream"] = { name = "Espíritus elevados", desc = "Se ha personalizado con el dibujo de un bosque sobre el que ascienden unos cuerpos.\n\nMuchas almas perdidas no desean ser halladas." },
	["sp_scar_chickenfight"] = { name = "Alectorofobia", desc = "Se ha pintado con espray usando un patrón que representa pollos en un campo de batalla.\n\nUn pollo cabalga sobre un tiburón hacia la batalla..." },
	["cu_sawedoff_ouija"] = { name = "Tablero de espíritus", desc = "Se ha personalizado para que parezca un tablero de invocación de espíritus muy usado.\n\n«Venga ya, sé que lo estás moviendo tú...»." },
	["cu_ak47_cogthings"] = { name = "Hielo carbonizado", desc = "Se ha personalizado con un brillante gradiente de color verde y azul.\n\nFrío al tacto." },
	["cu_awp_chroma_pink"] = { name = "Aberración cromática", desc = "Los colores en esta pintura personalizada se han aplicado en posiciones contrapuestas, dando lugar a una apariencia espectacular.\n\n(No demasiado) agradable a la vista..." },
	["gs_revolver_purple_elite"] = { name = "8 loco", desc = "Se ha personalizado con un cañón morado y el número 8 en la empuñadura.\n\nInspirada en los eventos del 8 de diciembre de 2015." },
	["gs_famas_corp_meow"] = { name = "Miau 36", desc = "Se ha engalanado su base verde con el número 36.\n\nNo le busques tres pies..." },
	["cu_galil_destroyer"] = { name = "Destroyer", desc = "Se ha personalizado con la inscripción «DESTROYER», pero solo se puede leer desde determinados ángulos.\n\nDestrúyelos a todos." },
	["cu_m249_downvote"] = { name = "Centro urbano", desc = "Se ha marcado con flechas de color azul y rosa que apuntan en todas las direcciones.\n\nUn estampado de espray diferente." },
	["cu_m4a4_elite_tactical"] = { name = "Polímero", desc = "Se ha personalizado con un cargador de polímero transparente y las piezas de la culata, el guardamanos y la empuñadura en color bronce.\n\nProcedimiento operativo estándar." },
	["cu_mac10_monkeyflage"] = { name = "Bandera macaca", desc = "Se ha adornado esta MAC-10 con ilustraciones de monos rojos, marrones y verdes.\n\n¡Qué monada!" },
	["cu_sg553_cyber_dragon"] = { name = "Ciberdragón", desc = "Se ha personalizado con un dragón verde biomecánico.\n\nEse no es mi dragón…" },
	["cu_negev_clear_sky"] = { name = "Déjame", desc = "Se ha personalizado con una señal de STOP junto a la empuñadura de asalto sobre una base mayormente marrón.\n\nSi la señal de STOP no te convence, la ráfaga de balas quizá lo haga." },
	["gs_p250_visions"] = { name = "Visiones", desc = "Se ha personalizado con un diseño en colores vibrantes.\n\nMata dos pájaros de un tiro." },
	["gs_p90_tangled"] = { name = "Gas a fondo", desc = "Se ha personalizado para que parezca un P90 con cinta de reparación, tubos y fuego.\n\n«¿Usted sabe a qué velocidad iba?»" },
	["cu_sawedoff_kisslove"] = { name = "Beso♥Amor", desc = "Se ha personalizado con la imagen de una mujer con estrellas en los ojos en tonos rosas y morados.\n\n«No aceptaré un no por respuesta. ¡Tengo que tenerlo!»" },
	["cu_dual_elites_evil_flora"] = { name = "Flora carnívora", desc = "Se ha personalizado con tres plantas carnívoras hambrientas en colores brillantes en cada una de las Berettas.\n\nAyúdalas a crecer sanas y fuertes." },
	["gs_ump_roadblock"] = { name = "Barricada", desc = "Se ha personalizado con líneas horizontales de color naranja y flechas apuntando hacia delante.\n\nBusca rutas alternativas." },
	["gs_glock_elite_camo"] = { name = "Invernal", desc = "Se ha personalizado con un diseño de camuflaje militar en tonos invernales.\n\n¡No te muevas!" },
	["cu_mag7_insomnia"] = { name = "Insomnio", desc = "Se ha personalizado con un diseño representando peces nadando hacia la boca de una persona, usando una paleta de colores naranja y negra.\n\nGlub, glub, glub" },
	["cu_ak_head_shot_holo"] = { name = "Disparo a la cabeza", desc = "Se ha personalizado con las palabras «HEAD SHOT GUN» en grafiti, con un revestimiento nacarado.\n\nTodos tenemos metas, pero ¿estás dispuesto a hacer el esfuerzo necesario para alcanzarlas? - Booth, Traficante de Armas" },
	["gs_awp_limbo_snake"] = { name = "Dualidad", desc = "Se ha personalizado con una serpiente a cada lado. Una es roja y la otra, dorada.\n\nToda historia tiene dos caras." },
	["cu_m4a1s_feeding_frenzy"] = { name = "Emphorosaurio-S", desc = "Se ha personalizado con un dinosaurio que parece estar rasgando el costado del arma con sus garras.\n\nFrenesí alimenticio" },
	["cu_p2000_decline"] = { name = "Llamativa y mortal", desc = "Se ha personalizado con diseños de color neón con el propósito de captar (y mantener) tu atención.\n\n¿Puedes prestarme atención, por favor?" },
	["cu_ump_clutch_kick"] = { name = "Niño salvaje", desc = "Bajo bocetos de balas, una corona y otros diseños, varias bandas de neón forman la base de esta pintura personalizada para la UMP-45.\n\nEl concepto es simple, la ejecución es lo complicado." },
	["cu_mp9_superlight"] = { name = "Peso pluma", desc = "Se ha personalizado con una pintura en blanco y negro para aparentar porosidad, lo que reduce el peso para quien sujeta el arma.\n\nUn buen tirador nunca culpa a sus herramientas" },
	["gs_scar_fragments_black"] = { name = "Fragmentos", desc = "Se ha personalizado con una pintura plateada que crea la ilusión de una geometría modificada en el arma.\n\nFragmentos de tu imaginación" },
	["cu_glock_moon_rabbit"] = { name = "Conejo umbral", desc = "Se ha personalizado con una escena de ensueño de un conejo místico saltando junto a una luna llena.\n\nDa un salto de fe" },
	["cu_m4a4_temukau"] = { name = "Temukau", desc = "Se ha personalizado con un estilo de animación japonesa que muestra a una agente CT, enfrentándose a un avance rápido de los terroristas.\n\n«¡Mantén tu posición!»" },
	["cu_mac10_sakkaku"] = { name = "Sakkaku", desc = "Bajo la iluminación adecuada, los ojos de esta pintura personalizada para el MAC-10 brillan de color rojo.\n\nConvierte tu percepción en su realidad" },
	["cu_p250_rebuilt"] = { name = "Re.Construido", desc = "Triángulos enfrentados y otros limpios diseños de carácter minimalista forman esta pintura personalizada para la P250.\n\nA la novena bala vencida" },
	["cu_mp5sd_quick_liquidation"] = { name = "Liquidación", desc = "Su oscura base, junto a los meticulosamente seleccionados reflejos rojos y azules, hacen que esta pintura personalizada para el MP5-SD parezca estar brillando bajo luz ambiental.\n\nReflejos calmados" },
	["gs_r8_banana"] = { name = "Cañón banana", desc = "Se ha personalizado para que se parezca a una banana muy madura.\n\nPíllales resbalando" },
	["gs_p90_neoqueen"] = { name = "Neorreina", desc = "Se ha personalizado con una mezcla de estilos militares y de ciencia ficción.\n\nCreación personalizada para todas tus necesidades urgentes" },
	["cu_sg553_cyberforce"] = { name = "Ciberfuerza", desc = "Se ha personalizado con el logo de CYBERFORCE INC sobre una base negra y roja.\n\n«CYBERFORCE INC: ¡Aseguramos tu futuro para que no tengas que hacerlo tú!»" },
	["cu_tec9_freedom"] = { name = "Rebelde", desc = "Se ha personalizado la TEC-9 con diseños vagamente relacionados utilizando una paleta de colores apagados.\n\nUn tiro, una víctima, una bala tras otra" },
	["gs_revolver_acanthus_bronze"] = { name = "Incrustación", desc = "Se ha personalizado para que parezcan grabados de acantos y una empuñadura de mármol.\n\nOpulencia en la palma de tu mano" },
	["gs_ak47_strone"] = { name = "Delta de acero", desc = "Se ha personalizado con líneas que parecen surcos de un mapa topográfico.\n\nSigue el rastro de la sangre" },
	["gs_awp_strone"] = { name = "Nilo negro", desc = "Se ha personalizado con líneas que parecen surcos de un mapa topográfico.\n\nSigue el rastro de la sangre" },
	["cu_glock_ramzes"] = { name = "Imperio de Ramsés", desc = "Se ha personalizado con el río Nilo en la corredera y una representación de Ramsés I, faraón fundador de la XIX dinastía del antiguo Egipto, en la empuñadura.\n\n«A través de mí, mi pueblo tiene a sus dioses más cerca»" },
	["cu_famas_holo_ocean"] = { name = "Aguas de Neftis", desc = "Este FAMAS se ha personalizado con un color azul que cambia con la luz.\n\nEl agua puede con todo" },
	["cu_m249_scarab"] = { name = "Inmersión", desc = "Se ha personalizado con un diseño de camuflaje en color verde azulado inspirado en el desierto.\n\n«¡ES UNA EMBOSCADA! ¡ESTÁN POR TODAS PARTES!»" },
	["gs_m4a1s_milspec"] = { name = "Manchas de barro", desc = "Se ha personalizado con un diseño de camuflaje en marrón y negro.\n\nHan llegado los refuerzos" },
	["gs_m4a4_ra"] = { name = "Ojo de Horus", desc = "Se ha personalizado con un fondo blanco, adornos dorados y una imagen del dios egipcio Horus en forma de halcón.\n\nQue nada se interponga en tu camino" },
	["cu_mac10_scarab"] = { name = "Eco en la arena", desc = "Se ha personalizado con un diseño de camuflaje inspirado en el desierto y un escarabajo debajo de la culata en forma de alambre.\n\n«¡ES UNA EMBOSCADA! ¡ESTÁN POR TODAS PARTES!»" },
	["gs_mag7_goold"] = { name = "Revestimiento de cobre", desc = "Se ha personalizado para que parezca que estuvo debajo de un manantial de cobre líquido.\n\nFue un accidente, no un error..." },
	["gs_mp7_strone"] = { name = "Tostado al sol", desc = "Se ha personalizado con líneas que parecen surcos de un mapa topográfico.\n\nSigue el rastro de la sangre" },
	["gs_nova_sobek"] = { name = "Dentellada de Sobek", desc = "Se ha personalizado con diseños inspirados en Egipto en color dorado y negro.\n\nLas aguas traen el caos" },
	["gs_p250_apep"] = { name = "Maldición de Apep", desc = "Se ha personalizado utilizando cenefas y materiales del antiguo Egipto como inspiración.\n\nEn contra de Ra" },
	["hy_dune_trap"] = { name = "Foso de serpientes", desc = "Se ha personalizado con serpientes y anfibios.\n\nMalas intenciones" },
	["cu_p90_scarab_of_wisdom"] = { name = "Escarasalto a B", desc = "Se ha personalizado con escarabajos y diseños egipcios.\n\nNo hay resurrección sin muerte ni muerte sin rusheo" },
	["gs_ssg08_anubis"] = { name = "Glifo celeste", desc = "Se ha personalizado con jeroglíficos egipcios sobre un fondo en color verde agua y un acabado en madera a lo largo del arma.\n\nAlerta de inundaciones" },
	["cu_tec9_mummy"] = { name = "Putrefacción momificada", desc = "Se ha personalizado para que parezca una momia y con una cobra de ojos verdes debajo de la mira posterior.\n\nQue no se entere nadie" },
	["gs_usps_tactical_camo"] = { name = "Tácticas del desierto", desc = "Se ha personalizado con un diseño de camuflaje táctico en color verde olivo.\n\nLos modelos base no tienen por qué ser aburridos" },
	["cu_xm1014_pharaoh"] = { name = "Jeroglífico", desc = "Se ha personalizado con adornos y jeroglíficos egipcios.\n\nLa moraleja de la historia" },
	["sp_nukestripe"] = { name = "sp_nukestripe", desc = "Se ha pintado con espray usando patrones de señales de peligro radiológico." },
	["sp_spray_waves_bravo"] = { name = "sp_spray_waves_bravo", desc = "Este recuerdo de Seaside tiene unas líneas gruesas por su superficie pintadas a mano alzada con colores que contrastan entre ellos.\n\nPerfecto para el insurgente en movimiento." },
	["hy_ocean_bravo"] = { name = "hy_ocean_bravo", desc = "Este recuerdo de Seaside se ha pintado usando un hidrográfico de camuflaje azul.\n\nEl nuestro es un mundo de intrigas... pero a veces el camuflaje está destinado a ser visto —Booth (traficante de armas)." },
	["sp_spitfire_famas_bravo"] = { name = "sp_spitfire_famas_bravo", desc = "Este recuerdo de Cache lleva pintada una boca de tiburón similar a la decoración del morro de algunos aviones.\n\nJester está muerto." },
	["hy_tile_bravo"] = { name = "hy_tile_bravo", desc = "Este recuerdo de Ali lleva un hidrográfico de un mosaico.\n\nLa paciencia es una virtud." },
	["an_navy_bravo"] = { name = "an_navy_bravo", desc = "Este recuerdo de Ali lleva una capa base de cromo y se ha decorado con un efecto de pintura anodizada azul marino transparente.\n\nYa perdí a un hombre por las mentiras de Valeria... No tengo intención de perder a nadie más —Felix Riley (oficial al mando)." },
	["sp_hazard_bravo"] = { name = "sp_hazard_bravo", desc = "Este recuerdo de Siege se ha pintado con espray simulando el bandeado de una cinta de peligro.\n\nNO PASAR." },
	["sp_tape_dots_bravo"] = { name = "sp_tape_dots_bravo", desc = "Este recuerdo de Gwalior se ha pintando con espray usando tiras de cinta adhesiva rasgadas y metal perforado como plantilla.\n\nNo te preocupes... el negocio está a punto de mejorar —Booth (traficante de armas)." },
	["sp_palm_bravo"] = { name = "sp_palm_bravo", desc = "Este recuerdo de Gwalior se ha pintando con espray usando recortes de cartón, tela metálica fina y hojas de palmera como plantilla.\n\nBella hoy... cenizas mañana." },
	["hy_ddpat_bravo"] = { name = "hy_ddpat_bravo", desc = "Este recuerdo de Ruins se ha pintado usando un hidrográfico de un patrón disruptivo digital.\n\nIncluso aunque no puedas verla, sin duda la oirás." },
	["so_jungle_bravo"] = { name = "so_jungle_bravo", desc = "Este recuerdo de Ruins lleva pintadas algunas partes con espray de colores sólidos en tonos jungla.\n\nPara aquellos con un apetito por la destrucción." },
	["so_tornado_bravo"] = { name = "so_tornado_bravo", desc = "Este recuerdo de Ali lleva pintadas algunas partes con espray de colores sólidos en tonos azul y marrón.\n\nLas palabras son viento, pero el viento puede matar." },
	["so_sand_bravo"] = { name = "so_sand_bravo", desc = "Este recuerdo de Gwalior lleva pintadas algunas partes con espray de colores sólidos en tonos arena.\n\nEl periodista gonzo Alex Kincaide desapareció tras viajar al Magreb para cubrir la Operación Vanguard." },
	["so_olive_bravo"] = { name = "so_olive_bravo", desc = "Este recuerdo de Cache lleva pintadas algunas partes con espray de colores sólidos en tonos verde militar.\n\nNunca des la victoria por segura." },
	["an_gunmetal_bravo"] = { name = "an_gunmetal_bravo", desc = "Este recuerdo de Siege se ha pintado con un efecto de pintura anodizada color plomo sobre una base de cromo.\n\nNo conoces a Felix Riley como yo... Ese hombre es imposible de domar —El Traidor y El Buscador de la Verdad, primera parte." },
	["am_ossify_blue_p2000_bravo"] = { name = "am_ossify_blue_p2000_bravo", desc = "Este recuerdo de Seaside lleva un hidrográfico abstracto azul transparente sobre una capa base metálica.\n\nLas olas rompen, nuestra voluntad no —Kotaro Izaki (experto en infiltración)." },
	["am_crumple_bravo"] = { name = "am_crumple_bravo", desc = "Este recuerdo de Agency se ha pintado siguiendo un patrón poligonal con pinturas metalizadas de diferentes reflectividades sobre una capa base de cromo.\n\nLápices abajo, manos arriba." },
	["aq_steel"] = { name = "aq_steel", desc = "Sigue perfectamente operativa aunque las superficies exteriores se hayan oxidado.\n\nHay quien no necesita ocultar cuán sucios son sus actos" },
	["hy_hex"] = { name = "hy_hex", desc = "Se le ha aplicado un adhesivo hidrográfico hexagonal." },
	["hy_camo_large"] = { name = "hy_camo_large", desc = "Se le ha aplicado un adhesivo hidrográfico con un patrón de camuflaje descomunal." },
	["am_ddpatdense"] = { name = "am_ddpatdense", desc = "Se ha cubierto con una lámina metálica estampada con un patrón disruptivo digital." },
	["twigs"] = { name = "twigs", desc = "Se ha pintado con espray a mano alzada usando ramitas como plantilla." },
	["varicamo"] = { name = "varicamo", desc = "Se le ha aplicado un adhesivo hidrográfico de camuflaje variado.\n\nLa reconstrucción solo viene a raíz de la destrucción" },
	["aq_damascus_knife"] = { name = "aq_damascus_knife", desc = "Se ha forjado a partir de una moldura a base de dos tipos de acero de carbono.\n\nFinalmente, un arma lo suficientemente fuerte para adecuarse a tus propósitos" },
	["am_crystallized"] = { name = "am_crystallized", desc = "Se ha pintado con pintura metalizada, y utiliza una máscara de cristalización para crear el patrón.\n\nTodo el mundo quiere causar impacto." },
	["snakeskin"] = { name = "snakeskin", desc = "Se le ha aplicado un adhesivo hidrográfico de piel de serpiente.\n\nSé que no crees en la causa... y cuando te ponga al descubierto, Valeria tendrá tu cabeza —El Escudo y La Serpiente, primera parte" },
	["cu_usp-s_elegant"] = { name = "cu_usp-s_elegant", desc = "Se ha pintado de azul cobalto y tiene estampado el logotipo antiterrorista en el lateral.\n\nPuedo seguir el procedimiento y sacar a 7, o puedo hacerlo a mi manera y salvar a los 10... tú eliges —Carmen Cocinero (experta en extracción)" },
	["hy_plaid2"] = { name = "hy_plaid2", desc = "Se le ha aplicado un adhesivo hidrográfico de tartán en tonos gris pardo y azul verdoso.\n\nNo es una gaita, pero hace sonar una canción antes de cada funeral" },
	["cu_fiveseven_urban_hazard"] = { name = "cu_fiveseven_urban_hazard", desc = "Se ha personalizado con un camuflaje urbano y detalles en rojo.\n\nCada año, Booth reúne a los armeros más importantes del mundo para exhibir sus últimos diseños" },
	["an_emerald"] = { name = "an_emerald", desc = "Se ha pintado con una base de cromo y decorado con un efecto de pintura anodizada esmeralda transparente.\n\n¿A esto ha quedado reducido el gran Sebastien Hennequet? ¿Una profesora de escuela gruñona? Qué te ha pasado... —El Maestro y El Iconoclasta, segunda parte" },
	["so_keycolors"] = { name = "so_keycolors", desc = "Se han pintado algunas partes con espray de colores sólidos en tonos azules y naranjas.\n\nEl gran ecualizador." },
	["cu_awp_mastery"] = { name = "cu_awp_mastery", desc = "Se ha personalizado usando una combinación de adhesivos hidrográficos y cuidadoso estarcido y se le han añadido detalles con pegatinas de transferencia térmica.\n\nChase Turner era un gran hombre... tienes el listón muy alto —Phoenix y el novato, primera parte" },
	["aq_dualberettas_cartel"] = { name = "aq_dualberettas_cartel", desc = "Ha sido grabado con esqueletos, flores y serpientes.\n\nEsa es la razón por la que Huxley está haciendo el trabajo pesado, todo lo que tenemos que hacer es apoyarla... —La Serpiente y el Buscador de la Verdad, segunda parte" },
	["aq_famas_contour"] = { name = "aq_famas_contour", desc = "Se ha personalizado con un diseño rojo y gris que complementa la forma del arma.\n\nJuntos siempre seremos más fuertes" },
	["cu_nova_hyperbeast"] = { name = "cu_nova_hyperbeast", desc = "Se ha personalizado con una horrible criatura en colores psicodélicos.\n\nTe llaman el Oni, ¿verdad? Qué nombre más aterrador para un tipo tan triste... –Elliot Kingsman (mercenario)" },
	["hy_ocean_knife"] = { name = "hy_ocean_knife", desc = "Se ha pintado usando un camuflaje azul hidrográfico." },
	["cu_m4a1s_printstream"] = { name = "cu_m4a1s_printstream", desc = "Se ha personalizado con un austero diseño blanco y negro, y un acabado con brillos nacarados.\n\nWHITE_1; BLACK_1; PEARLESCENT_1; SILENCED_1;" },
	["cu_usp_printstream"] = { name = "cu_usp_printstream", desc = "Se ha personalizado con un austero diseño blanco y negro, y un acabado con brillos nacarados.\n\nWHITE_1; BLACK_1; PEARLESCENT_1; SILENCED_1; PISTOL_1;" },
}
