text = {
	["Default"] = { name = "-", desc = "Kit di vernice senza nome" },
	["so_yellow"] = { name = "Bulldozer", desc = "Ha le varie parti spruzzate di vernice a tinta unita in uno schema di colori giallo in stile catena di montaggio.\n\nQuesto spaccaossa è devastante." },
	["so_olive"] = { name = "Groundwater", desc = "Ha le varie parti spruzzate di vernice a tinta unita in uno schema di colori in stile verde oliva." },
	["so_red"] = { name = "Candy Apple", desc = "Ha le varie parti spruzzate di vernice a tinta unita in uno schema di colori rosso e nero.\n\nPerfetta per le auto, ancora meglio sulle armi." },
	["so_purple"] = { name = "Ultraviolet", desc = "Ha le varie parti spruzzate di vernice a tinta unita in uno schema di colori nero e viola.\n\nUno stile elegante combinato a un intento brutale." },
	["so_night"] = { name = "Night", desc = "Ha le varie parti spruzzate di vernice a tinta unita in uno schema di colori in stile operazioni notturne.\n\nÈ il vento che soffia... è una morsa al collo." },
	["so_caramel"] = { name = "Caramel", desc = "Ha le varie parti spruzzate di vernice a tinta unita in uno schema di colori nero e caramello." },
