	["so_sand"] = { name = "Sand Dune", desc = "Ha le varie parti spruzzate di vernice a tinta unita in uno schema di colori in stile duna di sabbia.\n\nMorto Turner, abbiamo una nuova priorità: salvare Alex Kincaide. - Felix Riley, Ufficiale comandante." },
	["so_stormfront"] = { name = "Storm", desc = "Ha le varie parti spruzzate di vernice a tinta unita in uno schema di colori in stile tempesta.\n\nTi aspetterà alla monorotaia." },
	["so_tornado"] = { name = "Tornado", desc = "Ha le varie parti spruzzate di vernice a tinta unita in uno schema di colori in stile tornado.\n\nLa semplicità la sua forza." },
	["so_whiteout"] = { name = "Whiteout", desc = "È spruzzato di vernice tutta bianca.\n\nSembra innocente, ma l'aspetto può ingannare." },
	["so_jungle"] = { name = "Jungle", desc = "Ha le varie parti spruzzate di vernice a tinta unita in uno schema di colori in stile giungla." },
	["so_space_marine"] = { name = "Colony", desc = "Ha le varie parti spruzzate di vernice cachi e grigia." },
	["so_pmc"] = { name = "Contractor", desc = "Ha le varie parti spruzzate di vernice marrone chiaro, blu navy e verde scuro.\n\nRona Sabri non ha ancora perdonato Sebastien per non aver scelto lei per inseguire Turner." },
	["so_tangerine"] = { name = "Demolition", desc = "Ha le varie parti spruzzate di vernice a tinta unita in uno schema di colori nero e arancio mandarino.\n\nRona è ben consapevole del suo talento... ma non è abbastanza matura da capire che non dovrebbe ostentarlo. - Felix Riley, Ufficiale comandante." },
	["so_orange_accents"] = { name = "Nitro", desc = "so_orange_accents" },
	["cu_broken_path_famas"] = { name = "Afterimage", desc = "È stato verniciato usando una decalcomania con la trama di nastri intrecciati." },
	["cu_bullet_rain_m4a1"] = { name = "Bullet Rain", desc = "È stato verniciato con l'immagine di nuvole che fanno piovere proiettili." },
	["cu_catskulls_p90"] = { name = "Death by Kitty", desc = "È stato ricoperto con una stampa ad acqua di teschi di gatti in sovrapposizione." },
	["CSGO_Doomkitty"] = { name = "Doomkitty", desc = "CSGO_Doomkitty" },
	["hy_ddpat"] = { name = "Forest DDPAT", desc = "È stato verniciato usando una stampa ad acqua del Digital Disruptive Pattern (DDPAT).\n\nQuando sarai abbastanza vicino da notare i pixel sarà già troppo tardi." },
