	["hy_dive"] = { name = "Dive Camo", desc = "È stato verniciato usando una stampa idrografica a mimetizzazione blu.\n\nIl nostro è un mondo di inganni... ma a volte una mimetica è fatta per essere notata. - Booth, trafficante d'armi." },
