	["sp_mesh"] = { name = "Driftwood Mesh", desc = "È stato spruzzato di vernice attraverso una maglia di rete metallica e sagome di cartone ritagliate.\n\nUn predatore resta un predatore, indipendentemente da dove si trova." },
