	["sp_snake"] = { name = "Snake Camo", desc = "È stato spruzzato di vernice usando sagome a pelle di serpente all'interno di settori precedentemente delineati a mano libera." },
	["an_silver"] = { name = "Silver", desc = "È stato verniciato con uno strato base di cromatura caramellato da una tinta effetto argento anodizzato trasparente.\n\nC'è poesia nella semplicità. - Franz Kriegeld, Tattico dei Phoenix." },
	["an_red"] = { name = "Hot Rod", desc = "È stato verniciato con uno strato base di cromatura caramellato da una tinta effetto rosso anodizzato trasparente.\n\nAutomatico. Sistematico. Idromatico." },
	["an_gunmetal"] = { name = "Anodized Gunmetal", desc = "an_gunmetal" },
	["an_navy"] = { name = "Anodized Navy", desc = "È stato verniciato con uno strato base di cromatura caramellato da una tinta effetto blu navy anodizzato trasparente." },
	["am_urban"] = { name = "Metallic DDPAT", desc = "È stato verniciato usando una stampa idrografica Digital Disruptive Pattern (DDPAT) urbana su di uno strato base metallizzato." },
	["am_tiger"] = { name = "Gold Tiger", desc = "È stato placcato in oro e inciso a mano a fantasia di strisce di tigre." },
	["am_dragon_glock"] = { name = "Dragon Tattoo", desc = "È stato verniciato usando la decalcomania di un drago su di uno strato base metallizzato.\n\nNelle fiabe il cavaliere uccide sempre il drago... ma questo è il mondo reale. - Valeria Jenner, rivoluzionaria." },
	["am_caustics"] = { name = "Caustics", desc = "È stato verniciato con una stampa idrografica rossa e bianca su di uno strato base metallizzato color carboncino." },
	["am_lightning_awp"] = { name = "Lightning Strike", desc = "È stato verniciato con il motivo di un fulmine usando una tinta a effetto anodizzato su di uno strato base metallizzato.\n\nA volte non c'è bisogno di colpire lo stesso punto due volte." },
	["am_ossify"] = { name = "Ossified", desc = "È stato verniciato usando una stampa idrografica astratta verde lime trasparente su di uno strato base metallizzato." },
	["aa_flames"] = { name = "Blaze", desc = "È stato verniciato spruzzando tinta trasparente su di uno strato base cromato." },
	["aa_splash_p250"] = { name = "Splash", desc = "È stato verniciato spruzzando tinta trasparente in una sagoma su di uno strato base cromato." },
	["aa_fade"] = { name = "Fade", desc = "È stato verniciato spruzzando tinta trasparente in dissolvenza su di uno strato base cromato.\n\nAmbita da molti, specialmente dai tuoi nemici. - Imogen, trafficante d'armi stagista." },
	["aa_fade_metallic"] = { name = "Amber Fade", desc = "aa_fade_metallic" },
	["aa_fade_grassland"] = { name = "Acid Fade", desc = "aa_fade_grassland" },
	["aq_copper"] = { name = "Copper", desc = "È stato placcato in rame." },
	["aq_blued"] = { name = "Blue Steel", desc = "È ricoperto di acciaio bluito.\n\nQuesto è il malbec delle armi. - Booth, trafficante d'armi." },
	["aq_forced"] = { name = "Stained", desc = "Gli è stata applicata forzatamente una patina usando limone e mostarda gocciolati sulla superficie.\n\nE se pensi che ora sia un casino prova a guardarlo sotto ad una lampada ad ultravioletti." },
	["aq_oiled"] = { name = "Case Hardened", desc = "È stata cementata con il colore finale attraverso l'applicazione di carboncino vegetale ad alta temperatura.\n\nUn po' di colore non ha mai fatto male a nessuno." },
	["sp_spitfire_famas"] = { name = "Spitfire", desc = "sp_spitfire_famas" },
	["am_zebra"] = { name = "Slaughter", desc = "È stato verniciato a fantasia di strisce di zebra con tinta di alluminio e cromo a vari livelli di riflettenza, ed è stato poi ricoperto da uno strato di dolciumi colore rosso pomodoro.\n\nValeria non paga Javier perché lui le faccia delle domande... lo paga per ottenere delle risposte." },
	["am_zebra_dark"] = { name = "Dark Water", desc = "È stato verniciato a fantasia di strisce acquatiche con una tinta metallizzata color carboncino a vari livelli di riflettenza su di uno strato base cromato.\n\nL'acqua dona la vita, così come può prendersela..." },
	["aa_vertigo"] = { name = "Hypnotic", desc = "È stato verniciato con uno strato base perlaceo, e poi spruzzato di vernice nera attraverso una sagoma con una fantasia.\n\nNon ti stai assopendo, stai svenendo a causa di un'emorragia." },
	["cu_spring_nova"] = { name = "Bloomstick", desc = "È stato spruzzato di vernice a mano libera per formare un motivo a ciliegi in fiore, e poi è stato rivestito con uno strato lucido." },
	["sp_hazard"] = { name = "Hazard", desc = "sp_hazard" },
	["aq_rust"] = { name = "Rusty", desc = "Sebbene la superficie esterna si sia arrugginita, quest'arma è ancora perfettamente funzionante." },
	["am_slither_p90"] = { name = "Cold Blooded", desc = "È stato verniciato con uno strato base di cromatura caramellato da una tinta effetto rosso anodizzato trasparente. È stato rifinito con un'incisione al laser del simbolo di un serpente." },
	["am_carbon_fiber"] = { name = "Carbon Fiber", desc = "È stata verniciata usando una stampa idrografica in fibra di carbonio su di uno strato base di grafite.\n\nSolo perché ho portato la mia squadra alla vittoria, non significa che abbia fatto tutto io. - Rona Sabri, astro nascente." },
	["am_scorpion_p2000"] = { name = "Scorpion", desc = "È stato verniciato usando una tinta ad effetto anodizzato a fantasia di increspature in dissolvenza con una decalcomania di uno scorpione rosso sull'impugnatura." },
	["hy_feathers_aug"] = { name = "Wings", desc = "È stato decorato con una decalcomania a trasferimento a secco rappresentante piume d'aquila.\n\nLe aquile appaiono maestose, fino a quando non ti strappano gli occhi dalla testa." },
	["sp_palm"] = { name = "Palm", desc = "È stato spruzzato di vernice usando pezzi di cartone ritagliati, maglia metallica sottile e foglie di palma come sagome." },
	["sp_palm_shadow"] = { name = "Tropical Storm", desc = "sp_palm_shadow" },
	["cu_walnut_nova"] = { name = "Walnut", desc = "Ha un calcio a fantasia di legno di noce." },
	["aq_brass"] = { name = "Brass", desc = "Parti di quest'arma sono in ottone.\n\nSembra oro ma non è." },
	["hy_blam"] = { name = "KABOOM!", desc = "È stato verniciato usando una stampa idrografica rappresentante parole onomatopeiche da fumetto su di uno strato base arancione." },
	["hy_blam_simple"] = { name = "BOOM", desc = "hy_blam_simple" },
	["sp_dapple"] = { name = "Scorched", desc = "È stato spruzzato di vernice a fantasia brizzolata di sole.\n\nLa fenice non è un simbolo di distruzione, ma di rinascita. - Valeria Jenner, rivoluzionaria." },
	["sp_splash_p250"] = { name = "Splash", desc = "È stato spruzzato di vernice e decorato usando una sagoma a forma di schizzo." },
	["hy_hunter_modern"] = { name = "Modern Hunter", desc = "È stato verniciato usando una stampa idrografica di mimetizzazione per la caccia fotografica" },
