	["sp_zebracam_blue"] = { name = "Azure Zebra", desc = "sp_zebracam_blue" },
	["CSGO_Camo"] = { name = "GO Camo", desc = "È stato verniciato usando una stampa idrografica a fantasia della mimetizzazione di Global Offensive." },
	["CSGO_Icosahedron"] = { name = "Memento", desc = "È stato verniciato usando una stampa idrografica a fantasia geometrica." },
	["cu_fireserpent_ak47_bravo"] = { name = "Fire Serpent", desc = "Quest'arma arrivata da Ruins è stata verniciata con il disegno di un serpente di fuoco.\n\nSe vuoi sopravvivere per strada, impara a sputare fuoco." },
	["cu_favela"] = { name = "Corticera", desc = "È stato verniciato con la trama di una corteccia d'albero." },
	["cu_dragon_p90_bravo"] = { name = "Emerald Dragon", desc = "Quest'arma arrivata da Chinatown è stata verniciata con il disegno di un drago.\n\nLe tradizioni possono essere pericolose." },
	["hy_siege_bravo"] = { name = "Overgrowth", desc = "Quest'arma arrivata da Siege è stata verniciata usando una stampa mimetica idrografica.\n\nGuardami negli occhi e dimmi se ti sembra una dotazione standard. - Felix Riley, Ufficiale comandante." },
	["am_scales_bravo"] = { name = "Golden Koi", desc = "Quest'arma arrivata da Chinatown è stata ricoperta con una lamina metallica con un motivo a scaglie.\n\nUn'arma tonante, in tutti i sensi." },
	["aq_etched_mac10_bravo"] = { name = "Graven", desc = "Quest'arma arrivata da Gwalior ha delle parti incise in ottone.\n\nInfrange due comandamenti in un colpo solo." },
	["cu_season_elites_bravo"] = { name = "Black Limba", desc = "Quest'arma arrivata da Agency ha una patina scura e le impugnature di fraké nero.\n\nQuando la meccanica classica incontra l'estetica moderna." },
	["hy_seaside_bravo"] = { name = "Tempest", desc = "Quest'arma arrivata da Seaside è stata verniciata usando una stampa idrografica con una fantasia a onde e nuvole.\n\nUna tempesta è in arrivo." },
	["hy_crumple_dark_bravo"] = { name = "Facets", desc = "hy_crumple_dark_bravo" },
	["hy_crumple_bravo"] = { name = "Shattered", desc = "Quest'arma arrivata da Agency è stata verniciata usando una stampa idrografica con una fantasia geometrica.\n\nI miei uomini sono professionisti, faranno ben il loro lavoro... - Elliot Kingsman, criminale in carriera." },
	["hy_bluepolygon_bravo"] = { name = "Demeter", desc = "hy_bluepolygon_bravo" },
	["hy_ali_tile_bravo"] = { name = "Mosaico", desc = "hy_ali_tile_bravo" },
	["an_emerald_bravo"] = { name = "Emerald", desc = "Quest'arma arrivata da Chinatown è stata verniciata con uno strato base cromato caramellato con una tinta smeraldina anodizzata trasparente.\n\nÈ a questo che si è ridotto il grande Sebastien Hannequet? A una maestrina petulante? Che fine hai fatto... - L'insegnante e l'iconoclasta, parte seconda." },
	["hy_mayan_dreams_bravo"] = { name = "Mayan Dreams", desc = "Quest'arma arrivata da Ruins è stata verniciata usando una stampa idrografica con la fantasia di una nuvola maya.\n\nTalvolta un sacrificio si rende necessario." },
	["am_ossify_blue"] = { name = "Ocean Foam", desc = "È stato verniciato con una stampa idrografica blu trasparente a motivo astratto su di uno strato base metallizzato." },
	["am_crumple"] = { name = "Graphite", desc = "È stato verniciato con una fantasia geometrica con una tinta metallizzata su di uno strato base cromato a vari livelli di riflettenza." },
	["sp_skull_diagram_bravo"] = { name = "Bone Pile", desc = "Quest'arma arrivata da Cache è stata verniciata con dei teschi sopra uno schema di cablature.\n\nUna in più per la mia collezione..." },
	["sp_star_bravo"] = { name = "Zirka", desc = "Quest'arma arrivata da Cache è stata verniciata con una fantasia di stelle.\n\nImogen ha lo stesso fiuto del padre per la qualità, ma molta più ambizione." },
	["aq_steel_bravo"] = { name = "Rust Coat", desc = "Quest'arma arrivata da Siege è ancora perfettamente funzionante sebbene la scocca esterna si sia arrugginita.\n\nLe cicatrici temprano il carattere." },
	["cu_xray_m4"] = { name = "X-Ray", desc = "Un M4 con una verniciatura personalizzata che mostra le sue componenti interne come in una lastra." },
	["cu_xray_p250"] = { name = "X-Ray", desc = "Una P250 con una verniciatura personalizzata che mostra le sue componenti interne come in una lastra." },
	["an_titanium30v"] = { name = "Blue Titanium", desc = "Ha delle parti in titanio il cui caratteristico colore blu dello strato ossidato è stato ottenuto da un'anodizzazione controllata a 30 volt." },
	["hy_bluehex"] = { name = "Hexane", desc = "hy_bluehex" },
