	["so_green"] = { name = "Green Apple", desc = "Ha le varie parti spruzzate di vernice a tinta unita in uno schema di colori verde e nero.\n\nUna mela al giorno tiene il premio assicurativo dintorno." },
	["cu_money"] = { name = "Franklin", desc = "È stato verniciato usando la stampa idrografica di una banconota da cento dollari americana.\n\nIl denaro non parla... uccide." },
	["so_orca"] = { name = "Tuxedo", desc = "Ha le varie parti spruzzate di vernice a tinta unita in uno schema di colori bianco e nero.\n\nPer quando il crimine è una questione di eleganza." },
	["am_army_shine"] = { name = "Army Sheen", desc = "È stato ricoperto da una patina metallizzata stampata in un motivo a mimetizzazione.\n\nRicorda il tuo addestramento." },
	["am_oval_hex"] = { name = "Caged Steel", desc = "È stato verniciato con un motivo esagonale usando delle sagome per coprire la tinta metallizzata a cristalli, e poi è stato ricoperto con una stampa idrografica ritagliata a forma ovale.\n\nL'aspetto conta." },
	["cu_pinstripe_ak47"] = { name = "Emerald Pinstripe", desc = "Ha il calcio realizzato in legno di quercia di palude ed è decorato a righine bianche e verdi.\n\nMettere in dubbio quello che dice Imogen significa mettere in dubbio me. Ora rilassati, fatti uno scotch e parliamo d'affari. - Il padre e la fenice, parte seconda." },
	["sp_nightstripe"] = { name = "Night Stripe", desc = "È stato spruzzato di vernice dai colori notturni e con un motivo simile a del nastro adesivo.\n\nFra una spruzzata di vernice e una coltellata." },
	["cu_m4_asimov"] = { name = "Asiimov", desc = "È stato verniciato con una fantasia in stile fantascientifico.\n\nTutti possono fare previsioni per il futuro... ma solo un visionario può dargli forma." },
	["cu_sawedoff_octopump"] = { name = "The Kraken", desc = "È stato verniciato a mano con l'immagine di una piovra.\n\nIl mostro è stato liberato!" },
	["cu_m4a1-s_elegant"] = { name = "Guardian", desc = "È stato verniciato di blu cobalto, con lo stemma degli Anti-Terroristi spruzzato sul caricatore.\n\nNon devi piacermi per forza, devo solo proteggerti. - Carmen Cocinero, esperta di estrazione di ostaggi." },
	["cu_p250_refined"] = { name = "Mehndi", desc = "È stato verniciato con una stampa idrografica di una trama di cashmere.\n\nNamaste." },
	["cu_awp_cobra"] = { name = "Redline", desc = "È stato verniciato usando una stampa idrografica a fibra di carbonio, con una decalcomania trasferita a secco di tessuto gessato rosso.\n\nMai aver paura di spingersi al limite." },
	["cu_famas_pulse"] = { name = "Pulse", desc = "È stato verniciato con una stampa idrografica di frammenti di colore fucsia intenso.\n\nColori folgoranti per un'arma folgorante." },
	["hy_marina_sunrise"] = { name = "Marina", desc = "È stato verniciato con una stampa idrografica di una trama a forte contrasto giallo e blu.\n\nPer qualcuno sarai un eroe... per qualcun altro, un cattivo." },
	["am_thorny_rose_mp9"] = { name = "Rose Iron", desc = "È stato verniciato a mano con un motivo a rose e spine con una vernice metallizzata di colori diversi.\n\nNon c'è rosa senza spine." },
	["cu_skull_nova"] = { name = "Rising Skull", desc = "È stato personalizzato con l'immagine di un teschio usando una vernice satinata e un pennarello indelebile a olio.\n\nSignor Izaki, non è per lamentarmi... ma quanti fucili a pompa le servono? - Imogen e l'Oni, parte prima." },
	["cu_sandstorm"] = { name = "Sandstorm", desc = "È stato verniciato usando una stampa idrografica in uno schema viola e beige.\n\nCi vorrà ben più della tela di una tenda per proteggerti." },
	["hy_kami"] = { name = "Kami", desc = "È stato verniciato usando una stampa idrografica a fantasia di manga giapponese.\n\nAlcuni fantasmi non si arrendono alla tomba." },
	["aq_obsidian"] = { name = "Magma", desc = "Ha delle parti placcate di rame coperte da una trama di roccia fusa e patinate di rosso usando il cloruro di sodio e alte temperature. Le parti scoperte sono state scurite in seguito con l'ossidazione usando il fegato di zolfo.\n\nUna raffica di fuoco... bruciante." },
	["am_turqoise_halftone"] = { name = "Cobalt Halftone", desc = "È stato verniciato usando una stampa idrografica con una fantasia sovradimensionata a mezzatinta su di uno strato base cromato di vernice metallizzata turchese.\n\nFelix, tu ed io sappiamo benissimo che Valeria sta per cominciare una guerra. La sola domanda è: sei pronto? - Booth, trafficante d'armi." },
	["cu_aug_chameleonaire"] = { name = "Chameleon", desc = "È stato verniciato con l'immagine di un camaleonte.\n\nLa discrezione è fondamentale." },
	["cu_ump_corporal"] = { name = "Corporal", desc = "È stato verniciato con una fantasia in stile militare.\n\nIl rispetto non si chiede, si guadagna." },
	["cu_p90_trigon"] = { name = "Trigon", desc = "È stato verniciato in uno schema ad alto contrasto di colore arancio, grigio e nero.\n\nDiventa un tifone umano." },
	["cu_mac10_redhot"] = { name = "Heat", desc = "È stato verniciato per sembrare appena uscito dalla fucina.\n\nÈ ora di lasciare il segno." },
	["sp_negev_turq_terrain"] = { name = "Terrain", desc = "È stato verniciato usando una stampa idrografica con una fantasia in stile terriccio.\n\nPer una che si dichiara imparziale, tua figlia ha scelto delle amicizie piuttosto curiose. - Il padre e la fenice, parte prima." },
	["cu_nova_antique"] = { name = "Antique", desc = "È stato verniciato usando una combinazione di stampa idrografica e trasferimento a secco di decalcomanie per creare un calcio effetto legno con le rifiniture dorate.\n\nInvecchiando, migliorano due cose: la saggezza e il potere d'arresto." },
	["an_famas_sgt"] = { name = "Sergeant", desc = "È stato patinato a vari strati usando una maschera liquida per creare una fantasia in stile militare, impermeabilizzata con una rifinitura satinata.\n\nDai il buon esempio." },
	["cu_mag7_heaven"] = { name = "Heaven Guard", desc = "È stato verniciato usando una combinazione di stampa idrografica a trama fine e trasferimento a secco di una decalcomania di ali angeliche.\n\nA volte, vedere le ali degli angeli non garantisce la salvazione." },
