	["aq_etched_cz75"] = { name = "Victoria", desc = "È stata decorata con rifiniture dorate ed è stata incisa a mano con uno stile a effetto pergamena." },
	["am_p250_beaded_paint"] = { name = "Undertow", desc = "È stata verniciata usando una tinta metallizzata con una patina argentata sul carrello e una fantasia a pois blu e neri sul castello." },
	["am_fluted_tec9"] = { name = "Titanium Bit", desc = "La parte di rivestimento della canna è stata anodizzata con un colore blu reale e una rigatura accennata." },
