	["cu_usp_sandpapered"] = { name = "Road Rash", desc = "È stata verniciata con l'immagine di un proiettile vagante, e poi raschiata con la carta vetrata." },
	["hy_ssg08_marker"] = { name = "Detour", desc = "È stato decorato con un pennarello indelebile su di uno strato base bianco." },
	["am_royal"] = { name = "Chalice", desc = "È stata verniciata con uno strato base di cromatura caramellato con una tinta anodizzata effetto blu reale trasparente e ambra.\n\nHai scelto con saggezza." },
	["am_metals"] = { name = "Knight", desc = "Ha degli inserti in ottone in stile armatura rinascimentale.\n\nCome sarebbe a dire \"Non riusciamo a trovare Cocinero e Tzaki\"? - Felix Riley, Ufficiale comandante." },
	["am_chainmail"] = { name = "Chainmail", desc = "È stato verniciato con una stampa idrografica semitrasparente con una trama a maglia di ferro su di uno strato base argentato.\n\nDi tutti i membri della fazione Phoenix, nessuno è più leale della guardia del corpo personale di Valeria, Naomi." },
	["aq_handcannon"] = { name = "Hand Cannon", desc = "Ha delle parti in ottone che hanno subito un processo di patinazione all'acido ispirata dai cannoni antichi.\n\nPreparati a grandi tribolazioni nelle sfide che ti attendono." },
	["am_metal_inlay"] = { name = "Dark Age", desc = "È stato verniciato con una tinta metallizzata per sembrare che sia stato intarsiato di lamine metalliche a fantasia in stile medievale.\n\nPotrei ucciderla io, amore mio... forse ti darebbe più tempo con Valeria. - L'amore di un padre, parte seconda." },
	["hy_vines"] = { name = "Briar", desc = "È stato verniciato con una stampa idrografica di rampicanti arricciolati e fiori.\n\nUn bacio di vero amore non ti salverà dalle sue spire." },
	["so_indigo"] = { name = "Royal Blue", desc = "È stata verniciata con uno schema di colori a tinta unita indaco, cremisi e grigio.\n\nDovresti imparare a sorridere di più, Felix, ti andrebbe meglio con le donne se non avessi sempre quell'aria da rigido bastardo. - Imogen, trafficante d'armi in erba." },
	["so_indigo_and_grey"] = { name = "Indigo", desc = "È stato verniciato con uno schema di colori a tinta unita indaco, blu e grigio.\n\nAlex Kincaide, sono sicuro che avrai molto domande... prego, consentimi di risponderti. - La fenice e il cantastorie, parte prima." },
	["cu_well_traveled_ak47"] = { name = "Jet Set", desc = "Il suo calcio è stato rivestito di cuoio marrone. Quest'arma ha visto molte città.\n\nChiunque sia abbastanza attento potrebbe riuscire a collegare tutto e scoprire quello che ha fatto. Non abbiamo molto tempo per trovare una soluzione... - L'amore di un padre, parte prima." },
	["cu_green_leather"] = { name = "First Class", desc = "Ha il calcio fasciato di cuoio verde.\n\nMimosa non inclusa." },
	["cu_brown_leather_p90"] = { name = "Leather", desc = "Ha il calcio fasciato di cuoio marrone.\n\nNon temere, la sicura rispetta le norme di sicurezza aeroportuale." },
	["cu_luggage_mac10"] = { name = "Commuter", desc = "È stato verniciato con una stampa idrografica a quadretti, ed è stato ricoperto parzialmente di finta pelle di coccodrillo.\n\nHanno abboccato all'esca, muoviamoci... - Elliott Kingsman, mercenario." },
	["cu_medieval_dragon_awp"] = { name = "Dragon Lore", desc = "È stato verniciato con un drago in stile intreccio celtico.\n\nNon basterebbero 200 chiavi per aprirne i segreti." },
	["cu_luggage_p2000"] = { name = "Coach Class", desc = "È stato verniciato con una stampa idrografica a quadretti, e parte dell'impugnatura è stata ricoperta di cuoio marrone.\n\nNon è molto elegante, ma va dritta al punto." },
	["aq_pilot_deagle"] = { name = "Pilot", desc = "Ha degli inserti d'ottone a forma di ali da pilota.\n\nAssumere la posizione di preparazione all'impatto." },
	["cu_leather_xm1014"] = { name = "Red Leather", desc = "Ha il calcio e l'impugnatura ricoperti di cuoio rosso.\n\nFai le valigie. Andiamo ad ammazzare quel figlio di puttana... - L'Oni e la valchiria, parte seconda." },
	["cu_luggage_sg553"] = { name = "Traveler", desc = "È stato verniciato con un motivo a strisce ispirato alle valigie vecchio stile e ricoperto in parte di cuoio marrone.\n\nTutti hanno una storia e Javier te la tirerà fuori prima di ammazzarti." },
	["cu_luggage_usp-s"] = { name = "Business Class", desc = "È stato verniciato con un motivo e uno schema di colori ispirati alle valigie vecchio stile e ricoperto in parte di cuoio marrone.\n\nCerte persone non si accontentano di avere un sedile più comodo... vogliono pace e tranquillità." },
	["hy_plaid1"] = { name = "Olive Plaid", desc = "È stato verniciato con una stampa idrografica recante un motivo a quadretti verde oliva e beige.\n\nMi piace. Mi fa risaltare gli occhi davanti alla telecamera... - Rona Sabri, Astro nascente." },
	["chy_plaid2"] = { name = "Green Plaid", desc = "chy_plaid2" },
	["cu_m4a1-s_silence"] = { name = "Master Piece", desc = "È stato verniciato con un graffito stravagante." },
	["cu_panther_ak47"] = { name = "Jaguar", desc = "È stato verniciato con l'immagine di una pantera su di uno sfondo mimetico a strisce di tigre." },
	["cu_bratatat_negev"] = { name = "Bratatat", desc = "È stato verniciato con l'immagine di un'onomatopea in stile cartone animato e con fori di proiettile." },
