	["am_nuclear_skulls1_xm1014"] = { name = "Bone Machine", desc = "È stato cromato e successivamente velato con una stampa idrografica semitrasparente a fantasia a tema nucleare di teschi e ossa." },
	["am_nuclear_skulls2_famas"] = { name = "Styx", desc = "È stato cromato e successivamente velato con una stampa idrografica semitrasparente a fantasia a tema nucleare di teschi e ossa." },
	["am_nuclear_skulls3_mac10"] = { name = "Nuclear Garden", desc = "È stato cromato e successivamente velato con una stampa idrografica semitrasparente a fantasia a tema nucleare di teschi e ossa." },
