	["cu_cerbrus_galil"] = { name = "Cerberus", desc = "È stato verniciato con l'immagine di Cerbero, il mitico cane a tre teste che fa la guardia agli inferi." },
	["cu_tribute_ak47"] = { name = "Wasteland Rebel", desc = "È stato invecchiato, ricoperto di timbri e verniciato con dei graffiti.\n\nUn giorno, ci chiederanno perché combattiamo. Quando lo faranno, capiranno. - Naomi, guardia del corpo di Valeria Jenner." },
	["aq_glock_coiled"] = { name = "Grinder", desc = "Il carrello d'acciaio è stato verniciato usando sia maschere che a mano libera con una pittura ad olio.\n\nIl solo modo per creare un diamante è con una forte pressione." },
	["am_g3sg1_murky"] = { name = "Murky", desc = "È stato anodizzato e verniciato usando una stampa idrografica a macchie di leopardo.\n\nC'è sempre un luogo più oscuro in cui addentrarsi." },
	["aq_m4a1s_basilisk"] = { name = "Basilisk", desc = "Riporta l'incisione di un basilisco.\n\nFissare è da maleducati, ma la guerra non è certo posto per tanti salamelecchi." },
	["cu_m4a4_griffin"] = { name = "Griffin", desc = "È stato verniciato a mano con l'illustrazione di un grifone.\n\nFelix ha selezionato di persona i membri della sua squadra dagli operativi di ogni angolo del mondo... e per quanto la gente possa mettere in dubbio la loro personalità, non possono fare altrettanto coi loro risultati." },
	["sp_mag7_firebitten"] = { name = "Firestarter", desc = "È stato verniciato di pittura spray in vari strati e successivamente fatto invecchiare per dargli dei bordi dall'aspetto bruciato.\n\nComincia sempre tutto con una piccola scintilla..." },
	["cu_mp9_chevron"] = { name = "Dart", desc = "È stato verniciato usando una stampa idrografica a strisce, organizzata in una fantasia a galloni.\n\nScarica abbastanza colpi sul nemico e prima o poi colpirai il bersaglio." },
	["aq_p250_cartel"] = { name = "Cartel", desc = "Presenta un bassorilievo con scheletri, fiori e serpenti.\n\nUna leggera pressione del grilletto trasforma ogni giornata nel giorno dei morti." },
	["cu_p2000_fire_elemental"] = { name = "Fire Elemental", desc = "È stata verniciata con l'immagine di una creatura di fuoco in stile sgargiante.\n\nA volte giocare col fuoco è una buona cosa." },
	["aq_sawedoff_blackgold"] = { name = "Highwayman", desc = "È stato laccato di nero e oro usando il simbolo di un acanzio.\n\nNon c'è bisogno di tenere il mignolo alzato quando tiri il grilletto, ma è un tocco di classe." },
	["cu_scar20_intervention"] = { name = "Cardiac", desc = "È stato incollato a caldo ad uno strato in vinile con una stampa in uno schema di colori arancio e blu.\n\nBip. Bip. Biiiiiiiiiiiiiiiiiiiiiiiiiiiiiiip..." },
	["sp_ump45_d-visions"] = { name = "Delusion", desc = "È stato serigrafato con l'effige di un mollusco.\n\nCarcosa sta chiamando..." },
	["cu_xm1014_caritas"] = { name = "Tranquility", desc = "È stato verniciato a mano con l'immagine di una donna dai lunghi capelli.\n\nNon sono qui per qualcosa di cui ho bisogno; sono qui per qualcosa che voglio. - Imogen e l'Oni, parte seconda." },
	["am_awp_glory"] = { name = "Man-o'-war", desc = "Ha uno strato base di vernice metallizzata blu ed è rifinito a mano con ghirigori dorati.\n\nFai girare qualche testa, prima di farle esplodere." },
	["cu_elites_urbanstorm"] = { name = "Urban Shock", desc = "Sono state verniciate con una mimetizzazione urbana e delle rifiniture blu.\n\nIl lampo precede sempre il tuono." },
	["aq_deagle_naga"] = { name = "Naga", desc = "È stata incisa con un fregio a tema mitologico.\n\nUna belva sia nella mitologia indiana che sul campo di battaglia." },
	["cu_galil_abrasion"] = { name = "Chatterbox", desc = "È stato verniciato a mano e raschiato. Il teschio dipinto sullo sportellino di espulsione sembra sganasciarsi quando l'otturatore scarrella.\n\nC'è chi sostiene che quando ridi, il mondo rida insieme a te... ma quando il teschio di quest'arma inizia a ridere, il mondo risponde solo urlando..." },
	["cu_glock_deathtoll"] = { name = "Catacombs", desc = "È stata verniciata usando la stampa idrografica di una serie di teschi su di una base di vernice metallizzata argentata.\n\nL'intimidazione è una delle forme più oneste dell'adulazione." },
	["cu_m4a4_ancestral"] = { name = "龍王 (Dragon King)", desc = "È stato verniciato con l'immagine di un drago in uno schema di colori rosso e blu.\n\nUn'arma degna del Re Scimmia stesso. - Booth, trafficante d'armi." },
	["cu_m249_sektor"] = { name = "System Lock", desc = "È stata verniciata con uno stile fantascientifico.\n\nIl fuoco di copertura non ha mai avuto un aspetto così grandioso." },
	["am_mac10_malachite"] = { name = "Malachite", desc = "È stato verniciato usando delle tinte a effetto metallizzato, ondulate grazie all'impiego di un pettine gommato.\n\nI colori brillanti nascondono un'arma dallo scopo oscuro." },
	["cu_mp9_deadly_poison"] = { name = "Deadly Poison", desc = "È stato verniciato con forme angolose e dinamiche, e con l'immagine stilizzata di un ragno.\n\nNon è il primo morso che ti uccide, ma i 29 che lo seguono..." },
	["cu_p250_mandala"] = { name = "Muertos", desc = "È stata verniciata con dei motivi circolari.\n\nÈ intrisa di tradizione e sangue." },
	["cu_sawedoff_deva"] = { name = "Serenità", desc = "È stato verniciato a mano con l'immagine di una donna alata.\n\nLa serenità è un lusso che pochi possono permettersi. - Valeria Jenner, rivoluzionaria." },
	["aq_scar20_leak"] = { name = "Grotto", desc = "È stato spugnato e spennellato con vernici blu a effetto metallizzato.\n\nIl fondatore della Season Corporation ha ordinato la realizzazione di questa arma dopo essere stato salvato dal rinomato incursore Kotaro Izaki." },
	["aq_xm1014_sigla"] = { name = "Quicksilver", desc = "È stato verniciato usando una stampa idrografica traslucida con una fantasia a curve isometriche su di uno strato di vernice metallizzata.\n\nA volte devi sfruttare gli elementi instabili per risolvere i tuoi problemi. - Kotaro Izaki, esperto incursore." },
	["an_tiger_orange"] = { name = "Tiger Tooth", desc = "È stato anodizzato in arancione e intagliato a mano in una trama a strisce di tigre.\n\nCome le tigri, è raro... e letale..." },
	["am_marble_fade"] = { name = "Marble Fade", desc = "È stato marmorizzato con tinte metallizzate nere e argentate, e poi caramellato con tre diversi colori.\n\nLa lama ha molti colori, ma alla fine ne avrà uno solo: il rosso." },
	["am_marbleized"] = { name = "Doppler", desc = "È stato marmorizzato con tinte metallizzate nere e argentate, e poi caramellato.\n\nPerdersi nel suo colore può dimostrarsi letale." },
	["cu_ak47_mastery"] = { name = "Elite Build", desc = "It has been custom painted using a combination of hydrographics and careful stenciling and detailed with heat-transfer decals.\n\nTread softly Naomi...people in your line of work aren't known for their longevity - The Shield and The Serpent Part 2" },
	["aq_mp7_ultramodern"] = { name = "Armor Core", desc = "È stato verniciato con una pittura ad olio nera, ed è stato decorato con delle parti e degli inserti in acciaio cromato spazzolato.\n\nPerfetto per l'agente che predilige la sofisticatezza tanto quanto la potenza di fuoco. - Booth, trafficante d'armi." },
	["aq_awp_twine"] = { name = "Worm God", desc = "È stato verniciato con l'immagine di un teschio e delle radici usando delle tinte smaltate a lunga durata.\n\nCi sono radici che è meglio non prendano piede." },
	["am_bronze_sparkle"] = { name = "Bronze Deco", desc = "È stato verniciato in stile art déco usando tinte metallizzate per automobili.\n\nTutti sono alla ricerca dell'oro e dell'argento." },
	["aq_p250_contour"] = { name = "Valence", desc = "È stato verniciato in uno schema moderno di colori blu e verde che dona molto all'aspetto dell'arma.\n\nNon sto mettendo in dubbio le tue abilità, ma il tuo atteggiamento. - Il maestro e l'iconoclasta, parte prima." },
	["cu_fiveseven_banana"] = { name = "Monkey Business", desc = "È una banana.\n\nA volte va bene giocare col cibo." },
	["cu_galil_eco"] = { name = "Eco", desc = "È stato verniciato in uno schema di colori bianco e verde, con rifiniture realizzate con adesivi a trasferimento a caldo.\n\nPer il giusto compenso, l'Élite Crew aderirebbe a qualunque causa." },
	["aq_famas_jinn"] = { name = "Djinn", desc = "Ha delle rifiniture in oro e nichel, e un bassorilievo con l'immagine di un genio.\n\nOcchio a quello che desideri." },
	["cu_m4a1_hyper_beast"] = { name = "Hyper Beast", desc = "It has been custom painted with a beastly creature in psychadelic colours.\n\nYou really want to impress me Booth? Make this black light sensitive - Rona Sabri, Rising Star" },
	["cu_mag7_redhot"] = { name = "PaintKit_cu_mac10_redhot_tag", desc = "PaintKit_cu_mac10_redhot" },
	["am_negev_glory"] = { name = "PaintKit_am_awp_glory_Tag", desc = "PaintKit_am_awp_glory" },
	["cu_mac10_neonrider"] = { name = "Neon Rider", desc = "È stato verniciato con l'immagine di un centauro letale.\n\nUn terzo arma da fuoco, due terzi sballo totale." },
	["cu_sawedoff_origami"] = { name = "Origami", desc = "È stato verniciato con una fantasia ispirata agli origami.\n\nCi vuole tempo per creare qualcosa di meraviglioso, ma basta un attimo per distruggerlo." },
	["cu_cz75_precision"] = { name = "Pole Position", desc = "È stato verniciato con uno schema di colori rosso e grigio in stile corse automobilistiche.\n\nAbbiamo solo una possibilità di farcela, non la sprechiamo. - Felix Riley, Ufficiale comandante." },
	["am_ump_racer"] = { name = "Grand Prix", desc = "È stato verniciato con una fantasia classica in stile corse automobilistiche.\n\nNon scordarti la cintura." },
	["am_aqua_flecks"] = { name = "Twilight Galaxy", desc = "am_aqua_flecks" },
	["cu_chronos_g3sg1"] = { name = "Chronos", desc = "È stato decorato con la stampa idrografica di un meccanismo da orologio con colori psichedelici e rifinito con decalcomanie a trasferimento a caldo.\n\nCol tempo, tutto è possibile." },
	["hy_hades"] = { name = "Hades", desc = "È stata verniciata con una stampa idrografica di una trama di elmi dell'Antica Grecia.\n\nSe ci teneva tanto a intervistarmi, c'era sicuramente un modo più semplice per prendere un appuntamento. - Lo scudo e il cantastorie, parte seconda." },
	["hy_icarus"] = { name = "Icarus Fell", desc = "È stato verniciato con la stampa idrografica di una trama di piume e cera fusa intorno a un simbolo del sole.\n\nIl tradimento di Turner è un mio fallimento... devo rimediare. - Sebastien Hennequet, cecchino." },
	["cu_labyrinth"] = { name = "Minotaur's Labyrinth", desc = "È stato verniciato con la stampa idrografica di un labirinto isometrico.\n\nNessuno può sfuggirmi." },
	["sp_labyrinth1"] = { name = "Asterion", desc = "È stato spruzzato di vernice usando una sagoma a forma di labirinto isometrico.\n\nI re sono solo uomini... e nessun uomo dovrebbe essere venerato. - Valeria Jenner, rivoluzionaria." },
	["sp_labyrinth2"] = { name = "Pathfinder", desc = "È stata spruzzata di vernice usando una sagoma a forma di labirinto isometrico.\n\nPer quanto mi riguarda, preferisco la quarta edizione, ma il cliente ha sempre ragione. - Booth, trafficante d'armi." },
	["sp_labyrinth3"] = { name = "Daedalus", desc = "È stato spruzzato di vernice usando una sagoma a forma di labirinto isometrico.\n\nImogen... se mai scopriranno quel che hai fatto, potrebbe costarci tutto. - Una questione di famiglia, parte prima." },
	["cu_medusa_awp"] = { name = "Medusa", desc = "È stato verniciato con l'immagine di una gorgone. \n\nSe riesci a vedermi, vuol dire che sei già morto." },
	["gs_mother_of_pearl_elite"] = { name = "Duelist", desc = "Hanno le impugnature in madreperla smaltata rivestita da una patina scura.\n\nCaramelle non incluse." },
	["aa_pandora"] = { name = "Pandora's Box", desc = "È stato spruzzato di vernice con l'immagine di spiritelli stilizzati.\n\nIl mondo è in fiamme, ma se non altro abbiamo la speranza." },
	["cu_poseidon"] = { name = "Poseidon", desc = "È stato verniciato con la raffigurazione di una battaglia tra i Pesci e Poseidone.\n\nIn tre possono mantenere un segreto se almeno due sono morti." },
	["hy_zodiac1"] = { name = "Moon in Libra", desc = "Sono state verniciate con una stampa idrografica a fantasia zodiacale.\n\nOggi è una giornata ideale per trovare l'amore e uccidere i tuoi rivali." },
	["hy_zodiac2"] = { name = "Sun in Leo", desc = "È stato verniciato con una stampa idrografica a fantasia zodiacale.\n\nSarà un mese difficile per i tuoi nemici." },
	["hy_zodiac3"] = { name = "Shipping Forecast", desc = "È stata verniciata con una stampa idrografica a fantasia zodiacale.\n\nChissà cosa ci riserverà il futuro." },
	["so_khaki_green"] = { name = "Para Green", desc = "Ha ciascuna parte spruzzata di vernice a tinta unita in uno schema di colori verde cachi e nero.\n\nIl suo passato non cambia quello che è diventato. Non preoccuparti, Felix... Lo fermerò. - Il paladino e l'insegnante, parte seconda." },
	["cu_anime_aug"] = { name = "Akihabara Accept", desc = "È stato decorato con il trasferimento vinilico a caldo della copertina di una rivista anime.\n\nYatta!" },
	["am_bamboo_jungle"] = { name = "Hydroponic", desc = "È stato verniciato con una fantasia di bambù usando tinte metallizzate.\n\nNota come l'armaiolo Yukako ha usato il legno dell'AK-47 per accentuare il tema verdeggiante... non aver dubbi: Idroponico è bello e letale come la natura stessa. - Imogen, trafficante d'armi in erba." },
	["hy_bamboo_jungle_ink"] = { name = "Bamboo Print", desc = "È stato verniciato con la stampa idrografica di una serie di bambù dipinti a bomboletta.\n\nAlmeno non è uno sparapiselli." },
	["hy_bamboo_jungle_black"] = { name = "Bamboo Shadow", desc = "È stato verniciato con la stampa idrografica di una serie di bambù dipinti a pennello.\n\nKotaro ha perso suo figlio a causa di quest'arma... e un giorno la userà per fermare il responsabile." },
	["hy_bamboo_jungle"] = { name = "Bamboo Forest", desc = "È stata verniciata con la stampa idrografica di una serie di bambù dipinti a pennello.\n\nPenso ancora che ci vorrebbe un panda. - Booth, trafficante d'armi." },
	["am_geometric_steps"] = { name = "Aqua Terrace", desc = "È stato verniciato con una trama geometrica usando tinte metallizzate.\n\nE ora, serenità..." },
	["hy_geometric_steps"] = { name = "Geo Steps", desc = "È stato verniciato con la stampa idrografica di una trama geometrica." },
	["hy_geometric_steps_green"] = { name = "Counter Terrace", desc = "È stato verniciato con una stampa idrografica di una trama geometrica.\n\nEhi, Sabri, dovresti prendere questo... è assordante come te. - Carmen Cocinero, esperta di estrazioni di ostaggi." },
	["hy_geometric_steps_yellow"] = { name = "Terrace", desc = "È stata verniciata con una stampa idrografica di una trama geometrica\n\nTi fidi troppo di Valeria... finirai per farti ammazzare. - Franz Kriegeld, Tattico dei Phoenix." },
	["hy_kimono_diamonds"] = { name = "Neon Kimono", desc = "È stata verniciata con la stampa idrografica di una trama geometrica.\n\nSo che ti sto chiedendo tanto... Turner era come un figlio per te. - Il paladino e l'insegnante, parte prima." },
	["hy_kimono_diamonds_orange"] = { name = "Orange Kimono", desc = "È stato verniciato con la stampa idrografica di una trama geometrica.\n\nLe tradizioni sono cose potenti." },
	["hy_kimono_diamonds_red"] = { name = "Crimson Kimono", desc = "È stata verniciata con la stampa idrografica di un motivo geometrico.\n\nTurner, tutti gli uomini si possono spezzare... è solo una questione di trovare la giusta leva. - Il traditore e il cercatore di verità, parte seconda." },
	["sp_kimono_diamonds"] = { name = "Mint Kimono", desc = "È stata spruzzata di vernice in una trama geometrica.\n\nVieni, Booth, lascia che ti mostri su cosa stavo lavorando. - Yukako, armaiolo." },
	["am_seastorm"] = { name = "Midnight Storm", desc = "È stata verniciata con la stampa idrografica di un mare in tempesta su di uno strato base blu metallizzato.\n\nNon ti odio, Sebastien... mi fai pena. - Chase Turner, disertore della Coalizione." },
	["am_seastorm_blood"] = { name = "Sunset Storm 壱", desc = "È stata verniciata con la stampa idrografica di un mare in tempesta su di uno strato base rosso metallizzato.\n\nPensi che sia sempre la stessa, ma guarda con attenzione... i dettagli contano." },
	["am_seastorm_shojo"] = { name = "Sunset Storm 弐", desc = "am_seastorm_shojo" },
	["am_kimono_sunrise"] = { name = "Daybreak", desc = "È stato verniciato con una rappresentazione dell'alba usando vernici metallizzate.\n\nIl sole sorge sui giusti e sugli iniqui." },
	["am_so_keycolors"] = { name = "Impact Drill", desc = "am_so_keycolors" },
	["so_aqua"] = { name = "Seabird", desc = "Ha le varie parti spruzzate di vernice a tinta unita in uno schema di colori verde acqua e marrone chiaro.\n\nMio." },
	["cu_ak47_courage_alt"] = { name = "Aquamarine Revenge", desc = "È stato verniciato con l'immagine di un branco di delfini.\n\nCos'è questa eco?" },
	["cu_cz75a_chastizer"] = { name = "Yellow Jacket", desc = "È stata verniciata con forme curvilinee.\n\nVola come una farfalla, punge come un insetto odioso e implacabile." },
	["am_famas_dots"] = { name = "Neural Net", desc = "È stato verniciato con una stampa idrografica traslucida su di uno strato base metallizzato.\n\nLo so. Ma era la cosa giusta da fare... - Una questione di famiglia, parte seconda" },
	["cu_galilar_particles"] = { name = "Rocket Pop", desc = "È stato verniciato con un motivo a strisce e particelle pixellate.\n\nLa miglior cosa per una calda giornata d'estate." },
	["aq_glock18_flames_blue"] = { name = "Bunsen Burner", desc = "È stata ricoperta da una patina blu che poi è stata lucidata via dal carrello per darle un effetto fiammeggiante.\n\nTOC TOC." },
	["cu_m4a4_evil_daimyo"] = { name = "Evil Daimyo", desc = "È stato verniciato con un disegno colorato di rosso e nero.\n\nCarmen. L'ho trovato... - L'Oni e la valchiria, parte prima." },
	["cu_mp7_nemsis"] = { name = "Nemesis", desc = "È stato verniciato con l'immagine di un mostro metallico.\n\nSta venendo a prenderti!" },
	["am_mp9_nitrogen"] = { name = "Ruby Poison Dart", desc = "È stato verniciato con tinte metallizzate rosse e viola su di uno strato base nero.\n\nChi ha detto che è il diamante il miglior amico di una donna?" },
	["cu_negev_annihilator"] = { name = "Loudmouth", desc = "È stata verniciata con un disegno mostruoso e poi è stata invecchiata.\n\nTorna qui, sto parlando con te!" },
	["cu_nova_ranger"] = { name = "Ranger", desc = "È stato verniciato con una stampa idrografica a pennellate e particolareggiato con decalcomanie a trasferimento termico.\n\nChi sta tenendo i punti?" },
	["aq_p2000_boom"] = { name = "Handgun", desc = "È stata verniciata con il disegno di una mano che fa il gesto di sparare.\n\nNon è più per gioco." },
	["cu_ump45_uproar"] = { name = "Riot", desc = "È stato verniciato usando una combinazione di stampe idrografiche e decalcomanie a trasferimento termico.\n\nSe non volevi che si verificassero dei danni collaterali, avresti dovuto essere più specifico... - Javier Alviso, pugno della Fenice." },
	["cu_usp_progressiv"] = { name = "Torque", desc = "È stata verniciata in uno schema di colori moderno nero, bianco e giallo.\n\nTi stanno manipolando, Kotaro. E, se non riesci a vederlo, sei ancora più nei guai di quanto tu pensi... - Felix Riley, Ufficiale comandante." },
	["cu_ak47_winter_sport"] = { name = "Frontside Misty", desc = "È stato dipinto con colori gelidi e vivaci.\n\nMi piace lavorare nella neve... Lascia sempre un bel ricordo - Javier Alviso, pugno della Fenice." },
	["cu_dualberretta_dragons"] = { name = "Dualing Dragons", desc = "Su di esse sono incisi a laser dei draghi sul carrello e sulla canna.\n\nMarian ha sempre avuto più stile di Sylvia." },
	["cu_famas_lenta"] = { name = "Survivor Z", desc = "È stato decorato con del nastro adesivo e trattato per avere un effetto invecchiato.\n\nCos'è rosso e bianco e spara a raffica?" },
	["gs_g3sg1_flux_purple"] = { name = "Flux", desc = "È stato dipinto con vernici metalliche viola e argento.\n\nUn piano è una lista di cose che non accadono." },
	["gs_galil_nightwing"] = { name = "Stone Cold", desc = "È stato messo in risalto con una vernice metallica blu. La cassa ed il caricatore mostrano il suo marchio di fabbrica.\n\nPrima o poi hai bisogno di uscire dall'ombra del tuo mentore." },
	["gs_glock18_wrathys"] = { name = "Wraiths", desc = "Il carrello è decorato con l'immagine di anime tormentate.\n\nPrima trapassa il velo e poi l'ombra" },
	["gs_m249_nebula_crusader"] = { name = "Nebula Crusader", desc = "È stato verniciato per sembrare un classico cabinato arcade.\n\nPuoi usarlo, ma è meglio se inserisci un gettone prima" },
	["gs_m4a1s_snakebite_gold"] = { name = "Golden Coil", desc = "È stato decorato con l'aerografia di un serpente che attacca.\n\nLa vipera può colpire da ogni posizione." },
	["cu_mac10_alekhya_duo"] = { name = "Rangeen", desc = "È stato verniciato con un antico motivo egiziano.\n\nRiuscirai a resistere al suo sguardo furioso?" },
	["cu_mag7_myrcene"] = { name = "Cobalt Core", desc = "È stato dipinto con un design futuristico.\n\nTienilo al sicuro, Carmen - La Valchiria e l'amante, parte prima." },
	["cu_mp7_classified"] = { name = "Special Delivery", desc = "È stato -REDATTO-\n\nSe farò quello che vuoi, mi lascerai andare? - Phoenix e il Cantastorie, parte seconda." },
	["hy_p250_crackshot"] = { name = "Wingshot", desc = "È stato personalizzato con una pittura ad immersione.\n\nA volte un leggero colpetto è tutto quello che serve per ottenere quello che vuoi - Franz Kriegeld, Tattico dei Phoenix." },
	["gs_scar20_peacemaker03"] = { name = "Green Marine", desc = "È stata verniciata con un motivo mimetico metallizzato.\n\nAscoltate, gente.... Izaki e Cocinero potrebbero essere assenti ingiustificati, ma la nostra missione rimane la stessa: salvare Alex Kincaide - Felix Riley, Ufficiale comandante." },
	["cu_ssg08_technicality"] = { name = "Big Iron", desc = "È stato verniciato con un colore idrografico giallo ed arancione poi leggermente anticato.\n\nUna vittoria con un asterisco è pur sempre una vittoria - Rona Sabri, stella nascente." },
	["cu_usp_kill_confirmed"] = { name = "Kill Confirmed", desc = "È stato verniciato con l'immagine di un proiettile che trapassa un teschio.\n\n2 al torace, 1 alla testa." },
	["aq_xm1014_scumbria"] = { name = "Scumbria", desc = "È stato verniciato con una sobria tinta metallizzata.\n\nNon essere avventato: deve esserci qualche altro modo per attirare le attenzioni di Valeria... - L'amore di un padre, parte terza." },
	["cu_ak47_point_disarray"] = { name = "Point Disarray", desc = "È stata verniciata con una stampa idrografica geometrica.\n\nDa vicino è chaos... da lontano è bellezza - Franz Kriegeld, Tattico dei Phoenix." },
	["am_aug_jumble"] = { name = "Ricochet", desc = "È stato decorato con una verniciatura personalizzata in stile intrecciato a due tonalità.\n\nKotaro non ha bisogno della nostra protezione - La valchiria e l'amante, parte seconda." },
	["cu_bizon_noxious"] = { name = "Fuel Rod", desc = "È stata verniciata in modo tale da dare l'illusione che le munizioni siano radioattive.\n\nA volte sono i soldati semplici ad avere la meglio." },
	["aq_deagle_corinthian"] = { name = "Corinthian", desc = "È stata incisa al laser e presenta un'impugnatura personalizzata in cuoio.\n\nProtezione. Speranza. Fiducia. Perseveranza." },
	["cu_fiveseven_retrobution"] = { name = "Retrobution", desc = "È stata verniciata con una stampa idrografica retrò.\n\nAlcune cose non passano mai di moda." },
	["cu_g3sg1_executioner"] = { name = "The Executioner", desc = "È stato verniciato a mano con dei raccapriccianti disegni di ossa.\n\nUn trofeo è sempre ben accetto - Javier Alviso, il braccio forte dei Phoenix." },
	["gs_m4a4_royal_squire"] = { name = "Royal Paladin", desc = "È stato intagliato scrupolosamente e dipinto per dare un senso di splendore.\n\nQuesta telefonata non è mai avvenuta, ci siamo capiti? - Il paladino e il padre, parte prima." },
	["cu_negev_impact"] = { name = "Power Loader", desc = "È stata verniciata con una stampa idrografica che ricorda i macchinari pesanti.\n\nTemuta dalle regine. Amata dai vescovi." },
	["am_p2000_imperial_red"] = { name = "Imperial", desc = "È stata incisa al laser e verniciata con delle tinte metallizzate rosse e gialle.\n\nI miei sentimenti non sono importanti, Keo. Ho un impero da proteggere - Booth, trafficante di armi." },
	["cu_p90_shapewood"] = { name = "Shapewood", desc = "Il calcio in legno è stato intagliato a mano, smerigliato e laminato.\n\nPinzette non incluse." },
	["gs_sawedoff_necromancer"] = { name = "Yorick", desc = "È stata decorata con una stampa idrografica di un teschio e dei dettagli in oro realizzati a mano.\n\nRosencrantz e Guildenstern non avevano alcuna possibilità..." },
	["hy_scar20_jungler"] = { name = "Outbreak", desc = "È stata verniciata con diverse tonalità accese del verde.\n\nValeria sta diventando sempre più audace... è giunto il momento di affrontarla - Felix Riley, Ufficiale comandante." },
	["gs_sg553_tiger_moth"] = { name = "Tiger Moth", desc = "È stato decorato con il motivo delle ali di una falena.\n\nValeria sta diventando sempre più audace... è giunto il momento di affrontarla - Felix Riley, Ufficiale comandante." },
	["cu_tec9_avalanche"] = { name = "Avalanche", desc = "È stato verniciato con con una stampa idrografica futuristica e sofisticata.\n\nDobbiamo agire contro di lei in maniera intelligente, un solo passo falso e ci crollerà addosso - Il serpente e il cercatore della verità." },
	["aq_xm1014_hot_rod"] = { name = "Teclu Burner", desc = "È stata utilizzata della vernice metallica per aggiungere le fiamme.\n\nA volte un becco Bunsen non è sufficiente..." },
	["gs_ak47_supercharged"] = { name = "Fuel Injector", desc = "Il corpo del fucile è stato verniciato in un giallo acceso e il caricatore è stato decorato con degli ingranaggi.\n\nVivi la tua vita un quarto di caricatore alla volta." },
	["cu_bizon_citizen"] = { name = "Photic Zone", desc = "È stata decorata con un disegno personalizzato di una mimetica accentuato da una vernice fluorescente.\n\nQuello che senti non è il fragore delle onde..." },
	["aq_deserteagle_kumichodragon"] = { name = "Kumicho Dragon", desc = "I dragoni sono stati rifiniti con un'incisione al laser sul carrello e sull'impugnatura.\n\nAmmiro molto il tuo lavoro, Yukako. Un giorno o l'altro dovremmo scambiare quattro chiacchiere... -Huxley, la concorrenza." },
	["cu_fiveseven_augmented"] = { name = "Triumvirate", desc = "È stata decorata con una stampa idrografica DDPAT blu chiaro e con una verniciatura personalizzata per il carrello.\n\nNon traggo profitto dalla guerra, traggo profitto dalla condizione umana... - Booth, trafficante di armi." },
	["gs_glock18_award"] = { name = "Royal Legion", desc = "È stata scrupolosamente incisa.\n\nNaomi è conscia del fatto che un giorno morirà per Valeria." },
	["gs_m4a4_pioneer"] = { name = "The Battlestar", desc = "È stato verniciato con una stampa idrografica a carattere futuristico che ricorda l'interno di una nave spaziale.\n\nStai attento a quelli che parlano della torre di osservazione." },
	["am_mac10_electricity"] = { name = "Lapis Gator", desc = "È stata verniciata con una stampa idrografica di pelle di alligatore.\n\nIdrovolante non incluso." },
	["gs_mag7_praetorian"] = { name = "Praetorian", desc = "È stata verniciata con delle tinte metallizzate.\n\nLa guglia si sta formando." },
	["sp_mp7_impire"] = { name = "Impire", desc = "È stata verniciata con una stampa idrografica di un'orda di folletti.\n\nGli omicidi di massa non sono mai stati così adorabili" },
	["cu_ssg08_necropos"] = { name = "Necropos", desc = "È stato verniciato a mano con dei simboli runici nefasti.\n\nOgni carpentiere ha bisogno del proprio martello, ogni sarto del proprio fuso..." },
	["gs_tec9_jambiya"] = { name = "Jambiya", desc = "È stata verniciata a mano e incisa al laser con disegni del Medio Oriente.\n\nShahrazad ha raccontato 1000 storie... questa è l'ultima che sentirai." },
	["gs_usp_voltage"] = { name = "Lead Conduit", desc = "Il silenziatore e il carrello sono stati decorati con un bassorilievo personalizzato.\n\nHai avuto ciò che volevi, ma sai adesso che cosa significa? – Booth, trafficante di armi." },
	["cu_aug_swallows"] = { name = "Fleet Flock", desc = "È stata verniciata con una stampa idrografica di uccelli che volano tra le nuvole.\n\nAlcuni uccelli rappresentano pace e armonia... non è questo il caso." },
	["cu_bizon_Curse"] = { name = "Judgement of Anubis", desc = "È stata personalizzata con una verniciatura a tema egizio.\n\nIl suo grilletto pesa meno di una piuma." },
	["gs_cz75a_redastor"] = { name = "Red Astor", desc = "È stata verniciata a mano con tonalità bianche e rosse.\n\nBooth è diventato sinonimo di tragedia familiare; e chi ha tempo per le tragedie quando si parla di affari? - Huxley, la concorrenza." },
	["gs_dualberettas_ventilators"] = { name = "Ventilators", desc = "Queste pistole sono state verniciate elegantemente con uno schema di colori cromato.\n\nUn'arma per artisti, fatta da artisti." },
	["sp_g3sg1_militiaorange"] = { name = "Orange Crash", desc = "I disegni e la vernice arancione sono il segno distintivo di questo fucile.\n\nDiventa uno Shere Khan dei tempi moderni." },
	["gs_galilar_incenerator"] = { name = "Firefight", desc = "È stato decorato con un colore cremisi e un'icona dipinta a mano.\n\nBooth ha commissionato quest'arma a Valeria nel tentativo di calmare le acque con i Phoenix." },
	["cu_m249_spectre"] = { name = "Spectre", desc = "È stata verniciata in maniera sofisticata e moderna.\n\nLa guerra civile dei Phoenix sta per iniziare." },
	["cu_m4a1s_soultaker"] = { name = "Chantico's Fire", desc = "È stata verniciata a mano con colori accessi e decorata con una faccina sorridente sul calcio.\n\nProteggi ciò che ti appartiene." },
	["am_mp9_bioleak"] = { name = "Bioleak", desc = "Diverse sfumature di verde creano l'illusione di una fuoriuscita di prodotti chimici.\n\nEVITARE UN'ESPOSIZIONE DIRETTA." },
	["hy_p2000_oceani"] = { name = "Oceanic", desc = "È stata verniciata con una base di nero e decorata con disegni blu.\n\nQualcuno ha sentito Kotaro o Carmen? - Felix Riley, Ufficiale comandante." },
	["cu_p250_asiimov"] = { name = "Asiimov", desc = "È stata verniciata con una fantasia in stile fantascientifico.\n\nSe pensi che io abbia bisogno di un fucile per uccidere un uomo, allora non sai con chi stai parlando... - Mikha Biton, cecchino dei Phoenix." },
	["gs_sawedoff_fubar"] = { name = "Fubar", desc = "Questo fucile utilizzato spesso in battaglia è stato decorato con delle linee sulla canna che rappresentano le vittime che ha causato.\n\nSi inizia col botto... e finisce col silenzio." },
	["cu_sg553_atlas"] = { name = "Atlas", desc = "È stato verniciato usando una moderna stampa idrografica mimetica.\n\nIn principio questo stile era destinato alla task force della Coalizione, ma Huxley ha fatto dello spionaggio industriale per renderlo una esclusiva dei Phoenix." },
	["gs_ssg08_armacore"] = { name = "Ghost Crusader", desc = "È stato verniciato di bianco con dettagli blu ghiaccio.\n\nNon potrò mai rimpiazzare ciò che ci è stato tolto... ma posso prendere ancora di più da loro - Phoenix e l'iniziato, parte seconda." },
	["am_tec9_redblast"] = { name = "Re-Entry", desc = "La canna è stata decorata con una decalcomania di fiamme.\n\nOdio avere sempre ragione - Carmen Cocinero, specialista di estrazioni." },
	["cu_ump45_primalsaber"] = { name = "Primal Saber", desc = "È stata decorata con una decalcomania del teschio di una tigre dai denti a sciabola.\n\nPrima o poi, ogni predatore incontra la sua fine." },
	["cu_xm1014_spectrum"] = { name = "Black Tie", desc = "Decorato e cromato con tonalità nere e bianche, questo fucile a pompa è stato pensato per essere utilizzato durante occasioni formali.\n\nAbbiamo Izaki e Cocinero. Servono a qualcosa o posso semplicemente ucciderli? - Il mercenario e il serpente, parte prima." },
	["cu_lore"] = { name = "Lore", desc = "È stata decorata con una verniciatura personalizzata in stile intreccio celtico." },
	["cu_stonewash"] = { name = "Black Laminate", desc = "È stato slavato e l'impugnatura è stata verniciata con un colore nero laminato." },
	["am_marbleized_g"] = { name = "Gamma Doppler", desc = "È stato marmorizzato con tinte metallizzate nere e argentate, dopodiché è stato caramellato." },
	["gs_autotronic"] = { name = "Autotronic", desc = "È stato anodizzato in rosso e fornito di una maglia di acciaio per ridurne il peso." },
	["am_marked_up"] = { name = "Freehand", desc = "È stata verniciata in bianco e viola e successivamente decorata con dei particolari metallici." },
	["gs_aug_aristocrat"] = { name = "Aristocrat", desc = "Su questa arma è stata applicata una filigrana argentata su una base blu chiaro anodizzata.\n\nPer il 1%" },
	["gs_awp_phobos"] = { name = "Phobos", desc = "È costruito con pannelli metallici e solidi componenti elettrici.\n\nCostruisci un mondo migliore." },
	["cu_five_seven_daimyo"] = { name = "Violent Daimyo", desc = "È stata verniciata con un motivo grafico viola e nero.n\nCome vuoi dominare?" },
	["cu_glock_wasteland_rebel"] = { name = "Wasteland Rebel", desc = "È stata invecchiata, ricoperta di timbri e verniciata con dei graffiti.n\nPaga pegno." },
	["gs_m4a1_mecha_industries"] = { name = "Mecha Industries", desc = "È stata personalizzata con una serie di pannelli in ceramica indurita.\n\nTecnologia nuova di zecca." },
	["cu_m4a4_desolate_space"] = { name = "Desolate Space", desc = "È stata personalizzata con una verniciatura a tema spaziale.\n\n\"Contatto perso\"." },
	["aq_mac_10_alien_camo"] = { name = "Carnivore", desc = "È stata verniciata con una stampa idrografica rossa e viola sopra un rivestimento metallizzato.\n\nPreparati prima di servire." },
	["aq_nova_sci_fi"] = { name = "Exo", desc = "È stato verniciato con una stampa idrografica con pannelli metallici su una base con un motivo viola.\n\nCiò che conta è dentro." },
	["gs_p2000_imperial_dragon"] = { name = "Imperial Dragon", desc = "È stata personalizzata con il disegno di un dragone utilizzando tonalità metalliche di colore rosso.\n\nGuarda a ovest.\n	" },
	["gs_p250_metal_panels"] = { name = "Iron Clad", desc = "È stata decorata con pannelli di metallo inchiodati.\n\nAffidabilità e sicurezza garantite." },
	["gs_p90_full_throttle"] = { name = "Chopper", desc = "È stata decorata con il disegno di una fiamma utilizzando tinte metallizzate arancioni e blu.\n\nClutch non incluso." },
	["gs_pp_bizon_harvester"] = { name = "Harvester", desc = "È stata decorata con un motivo biomeccanico.\n\nSperimentazione biomeccanica ben riuscita." },
	["cu_r8_cybersport"] = { name = "Reboot", desc = "È stata decorata con dei particolari di colore rosso sopra i pannelli nero e argento.\n\n\"Hai provato a spegnere e riaccendere?\"" },
	["cu_sawed_off_lime"] = { name = "Limelight", desc = "È stato verniciato usando una decalcomania con trame curvilinee verde lime e bianche.\n\nTutti gli occhi su di te." },
	["gs_scar20_bloodsport"] = { name = "Bloodsport", desc = "È stata decorata con delle decalcomanie di colore bianco sopra una stampa idrografica nera e rossa.\n\nAnti-Terroristi sponsorizzati da..." },
	["cu_sg553_aerial"] = { name = "Aerial", desc = "È stata verniciata con tinte gialle e argento con una decalcomania di un'ala bianca.\n\nSuperiorità aerea." },
	["am_tec_9_sea_salt"] = { name = "Ice Cap", desc = "È stata verniciata con una stampa idrografica astratta di colore blu e bianco.\n\nSommergi il nemico." },
	["bloodhound_black_silver"] = { name = "Charred", desc = "Realizzati in pelle nera elastica.\n\nLe monete di riconoscimento sono fantastiche, ma non lasciano tracce di sangue quando prendi qualcuno alle spalle - Carmen Cocinero, esperta di estrazioni." },
	["bloodhound_snakeskin_brass"] = { name = "Snakebite", desc = "Realizzati in classica pelle e pelle di serpente.\n\nSiamo sinceri... a nessuno manca Sergei - Hans Kriegeld, Tattico dei Phoenix." },
	["bloodhound_metallic"] = { name = "Bronzed", desc = "Il cuoio è stato colorato in bronzo e grigio metallizzato.\n\nL'Operazione Bloodhound ha segnato la fine del cecchino disertore Chase Turner." },
	["bloodhound_guerrilla"] = { name = "Guerrilla", desc = "È stato creato con un mix di pelle marrone e pelle sintetica con una stampa mimetica.\n\nValeria vuole punirci, su quello non c'è dubbio... - Sebastien Hennequet, cecchino." },
	["handwrap_leathery"] = { name = "Leather", desc = "Sono realizzate in pelle, garze e cattive intenzioni.\n\nDiventa tristemente famoso." },
	["handwrap_camo_grey"] = { name = "Spruce DDPAT", desc = "Il tessuto della fascia esterna è stampato con un motivo mimetico digitale grigio.\n\nAlcuni dicono di essere tosti... altri lo dimostrano." },
	["handwrap_red_slaughter"] = { name = "Slaughter", desc = "Queste fasce color cremisi rendono difficile capire dove finisce il tessuto e dove iniziano le macchie di sangue.\n\nA chi non piace lavorare con le proprie mani?" },
	["handwrap_fabric_orange_camo"] = { name = "Badlands", desc = "Queste protezioni improvvisate sono state realizzate con materiali recuperati dal campo di battaglia.\n\nFelix ha deciso di chiudere un occhio sulla federazione di boxe non autorizzata della task force della Coalizione." },
	["slick_black"] = { name = "Lunar Weave", desc = "Del camoscio blu notte è stato intessuto con del cuoio nero corvino per dare a questi guanti un carattere senza tempo.\n\nImpediscono al sangue di sporcarti le mani... metaforicamente parlando." },
	["slick_military"] = { name = "Convoy", desc = "Sono realizzati in un misto di morbida pelle marrone e pelle scamosciata colorata.\n\nA volte, la discrezione è necessaria." },
	["slick_red"] = { name = "Crimson Weave", desc = "Del camoscio nero è stato intessuto con del cuoio scarlatto per dare a questi guanti un carattere senza tempo.\n\nLascia il segno, ma non le tue impronte digitali." },
	["slick_snakeskin_yellow"] = { name = "Diamondback", desc = "Sono stati realizzati con un misto di pelle marroncina, pelle nera e pelle di serpente.\n\nLe edizioni standard sono per i fessi... - Imogen, trafficante d'armi in erba." },
	["sporty_green"] = { name = "Hedge Maze", desc = "Questi guanti verdi e bianchi sono stati prodotti da Icarus Athletics.\n\nSolo i codardi hanno paura di volare vicino al sole." },
	["sporty_purple"] = { name = "Pandora's Box", desc = "Questi guanti neri e viola presentano un sottile disegno stampato sul palmo.\n\nAssolutamente necessari per ogni esperto di demolizioni con mani sudate." },
	["sporty_light_blue"] = { name = "Superconductor", desc = "Questi guanti blu e neri sono ideali per chi vuol essere fenomenale.\n\nNon ne vogliono nessuno." },
	["sporty_military"] = { name = "Arid", desc = "Le diverse tonalità del marrone e la leggera stampa di una mimetica danno a questi guanti un carattere distinto.\n\nSe dobbiamo essere eroi, tanto vale calarci nella parte... - Rona Sabri, stella nascente." },
	["specialist_ddpat_green_camo"] = { name = "Forest DDPAT", desc = "Il cuoio cucito a mano è stato abbinato ad un tessuto rugoso decorato con una stampa idrografica DDPAT.\n\nAlcune persone odiano sporcarsi le mani." },
	["specialist_kimono_diamonds_red"] = { name = "Crimson Kimono", desc = "Questo paio di guanti neri è stato decorato con un motivo rosso a forma di diamante.\n\nInizio a vedere rosso." },
	["specialist_emerald_web"] = { name = "Emerald Web", desc = "Il dorso di questi guanti è stato decorato con il disegno di una ragnatela.\n\nLa disciplina conduce al successo." },
	["specialist_white_orange_grey"] = { name = "Foundation", desc = "Questi guanti bianchi e neri sono stati decorati con dei dettagli arancioni.\n\nSi dice che Tortue si sia ispirata alla linea di armi da fuoco Asiimov." },
	["motorcycle_basic_black"] = { name = "Eclipse", desc = "Questo è il classico design nero che ha reso Tortue così famosa.\n\nNon chiamatela crisi di mezza età." },
	["motorcycle_mono_boom"] = { name = "Boom!", desc = "Questo paio di guanti è stato decorato con diverse onomatopee dei fumetti.\n\nPOW! BOOM! BIFF!" },
	["motorcycle_mint_triangle"] = { name = "Spearmint", desc = "La pelle bianca e le cuciture rosse rendono questi guanti tanto eleganti quanto comodi.\n\nPer quello che costano, Huxley potrebbe almeno darci qualche caramella in omaggio... - Felix Riley, Ufficiale comandante." },
	["motorcycle_triangle_blue"] = { name = "Cool Mint", desc = "Il morbido cuoio blu è stato decorato con disegni geometrici dai colori dell'artico.\n\nCombattere non è mai stato così rinfrescante." },
	["slick_snakeskin_white"] = { name = "King Snake", desc = "Sono stati realizzati a partire da pelle bianca e pelle di serpente." },
	["slick_plaid_purple"] = { name = "Imperial Plaid", desc = "Questi guanti in pelle e flanella conferiscono un portamento imperiale." },
	["slick_stitched_black_orange"] = { name = "Overtake", desc = "La velocità è l'essenza di questi guanti in pelle nera e marrone." },
	["slick_slick_stitched_green_grey"] = { name = "Racing Green", desc = "Questo paio di guanti verdi è stato decorato con del cuoio grigio e un motivo a zigzag." },
	["sporty_poison_frog_blue_white"] = { name = "Amphibious", desc = "Questi guanti sintetici blu e bianchi si asciugano in fretta e sono realizzati con un tessuto traspirante." },
	["sporty_poison_frog_red_green"] = { name = "Bronze Morph", desc = "Questi guanti sintetici rossi e verdi si asciugano in fretta e sono realizzati con un tessuto traspirante." },
	["sporty_black_webbing_yellow"] = { name = "Omega", desc = "Questi guanti neri sintetici sono stati decorati con delle accentuate rifiniture gialle." },
	["sporty_blue_pink"] = { name = "Vice", desc = "Questi guanti sintetici sono stati realizzati con un'impressionante combinazione di materiali tecnici rosa e blu." },
	["motorcycle_choco_boom"] = { name = "POW!", desc = "Questo paio di guanti è stato decorato con delle sgargianti onomatopee tipiche dei fumetti." },
	["motorcycle_basic_green_orange"] = { name = "Turtle", desc = "Questo paio di guanti verdi e grigi è stato decorato con delle rifiniture arancione chiaro." },
	["motorcycle_yellow_camo"] = { name = "Transport", desc = "Questo paio di guanti ha un leggero motivo mimetico digitale." },
	["motorcycle_trigrid_blue"] = { name = "Polygon", desc = "Questi guanti sono stati realizzati con una combinazione di cuoio e materiali sintetici in diverse sfumature del blu." },
	["handwrap_leathery_fabric_blue_skulls"] = { name = "Cobalt Skulls", desc = "Queste fasce blu composte da nastri in sbieco sono state decorate con immagini di teschi." },
	["handwrap_leathery_fabric_geometric_blue"] = { name = "Overprint", desc = "Queste fasce nere composte da nastri in sbieco sono state decorate con motivi geometrici in gomma." },
	["handwrap_leathery_ducttape"] = { name = "Duct Tape", desc = "Queste fasce improvvisate sono sorprendentemente resistenti ma non particolarmente traspiranti." },
	["handwrap_leathery_fabric_green_camo"] = { name = "Arboreal", desc = "Queste fasce blu composte da nastri in sbieco sono state decorate con un motivo mimetico." },
	["bloodhound_hydra_black_green"] = { name = "Emerald", desc = "Realizzati in pelle nera elastica e decorati con un emblema verde." },
	["bloodhound_hydra_green_leather_mesh_brass"] = { name = "Mangrove", desc = "Realizzati in un misto di pelle, maglia sintetica e ottone." },
	["bloodhound_hydra_snakeskin_brass"] = { name = "Rattler", desc = "Realizzati in un misto di cuoio, pelle di serpente e ottone." },
	["bloodhound_hydra_case_hardened"] = { name = "Case Hardened", desc = "Questi guanti in cuoio vegano presentano un motivo metallico variegato." },
	["specialist_webs_red"] = { name = "Crimson Web", desc = "Questi guanti rossi e grigi sono stati decorati con un motivo gommato a ragnatele." },
	["specialist_forest_brown"] = { name = "Buckshot", desc = "Questi pratici guanti sembrano fatti apposta per la caccia." },
	["specialist_fade"] = { name = "Fade", desc = "Questi guanti multicolore hanno delle rifiniture metalliche." },
