	["cu_AK47_Anarchy"] = { name = "Neon Revolution", desc = "È stato decorato con un eclettico miscuglio di decalcomanie idrografiche e successivamente verniciato con la tecnica del gocciolamento.\n\nNessuna squadra suicida potrebbe farne a meno." },
	["gs_AUG_Syd_Mead"] = { name = "Syd Mead", desc = "È stata personalizzata dal rinominato artista Syd Mead.\n\nIn passato ci ha regalato visioni del futuro; ora ha messo il futuro nelle nostre mani..." },
	["gs_CZ75_Tread"] = { name = "Imprint", desc = "È stata scrupolosamente incisa a mano.\n\nLe armi da fuoco di lusso vanno di moda in Repubblica Ceca." },
	["aq_Desert_Eagle_Constable"] = { name = "Directive", desc = "È stata progettata per aiutare i tutori della legge del futuro a fermare i delinquenti.\n\nA te la prossima mossa..." },
	["gs_FAMAS_Rally"] = { name = "Roll Cage", desc = "È stata decorata con delle decalcomanie personalizzate che sembrano il telaio di un'auto ricoperto di sangue.\n\nLa sicurezza prima di tutto." },
	["aq_Five_Seven_Scumbria"] = { name = "Scumbria", desc = "È stata decorata con una serie di strisce leggere.\n\nNon puoi lasciarlo morire, Felix; devi salvarlo! - La figlia e il paladino, parte prima." },
	["gs_G3SG1_Ventilator"] = { name = "Ventilator", desc = "È stato decorato con tonalità nere e cromate e rifinito con un'impugnatura di gomma. n\nNon preoccuparti, Naomi, la farò pagare a Felix - Mikha Biton, cecchino dei Phoenix." },
	["cu_Glock18_Weasel"] = { name = "Weasel", desc = "È stata verniciata con la stampa idrografica di Bobby, la donnola birichina. Con Bobby non si scherza.\n\nSolo perché è carino, non significa che non morda." },
	["cu_MAG7_Tribal"] = { name = "Petroglyph", desc = "È stata verniciata in modo da dare l'impressione di un'incisione su roccia.\n\nI conflitti esistono da quando il primo uomo delle caverne ha imparato a raccogliere una pietra – Booth, trafficante d'armi." },
	["cu_MP9_Narcis"] = { name = "Airlock", desc = "È stata verniciata di bianco e il caricatore è stato colorato di rosso.\n\nPerfetta per sparare ai tostapane." },
	["hy_Negev_Dazzle"] = { name = "Dazzle", desc = "È stato ricoperto da varie decalcomanie di strisce di zebra.\n\nUn'arma che ti farà girare la testa." },
	["cu_P90_Grimm"] = { name = "Grim", desc = "È stato decorato con una stampa idrografica di spettri sanguinari e successivamente decorato con dei dettagli verdi.\n\nImogen... mi stai vendendo un'arma o un poster? – Rona Sabri, stella nascente." },
	["gs_SCAR20_Powercore"] = { name = "Powercore", desc = "È stato verniciato di verde con dettagli di colore giallo e successivamente decorato con un codice a barre.\n\nRegola numero 1: la sfida richiede soddisfazione." },
	["cu_SG556_Triarch"] = { name = "Triarch", desc = "Questo fucile è stato commissionato da una famiglia reale e decorato con una stampa mimetica a tema desertico.\n\nRicordagli del tuo amore." },
	["gs_TEC9_Supercharged"] = { name = "Fuel Injector", desc = "Il corpo è stato verniciato di giallo e il caricatore è stato decorato con la stampa idrografica di un motore a ingranaggi. L'impugnatura in cuoio tessuto a mano completa questo classico di Seattle.\n\nMeccanici non inclusi." },
	["cu_UMP45_Metritera"] = { name = "Briefing", desc = "È stata verniciata con una stampa idrografica di una mappa topografica.\n\nSarà una riunione piuttosto corta..." },
	["hy_XM1014_Fractal_Blue"] = { name = "Slipstream", desc = "È stato verniciato in grigio fumo e vibranti tonalità di blu.\n\nPazienta, Elliott; hanno ancora un ruolo da svolgere... - Il mercenario e il serpente, parte seconda." },
	["am_czv2_mf"] = { name = "Polymer", desc = "Il carrello è stato decorato con una decalcomania che assomiglia alla formula di un polimero.\n\nI Phoenix non sono più un vostro problema, sappiamo benissimo cavarcela da soli... - La figlia e il paladino, seconda parte." },
	["aq_glock_dark-fall"] = { name = "Ironwork", desc = "Il carrello è stato decorato con delle incisioni al laser rappresentanti edera e spine.\n\nAbbandonato a sé stesso, ogni giardino diventa un disordinato groviglio di erbacce." },
	["cu_ssg08_dragonfire_scope"] = { name = "Dragonfire", desc = "È stato verniciato con la stampa idrografica di un mostruoso drago sputafuoco.\n\nProteggi il tuo tesoro e aspetta chiunque desideri rubartelo." },
	["gs_dual_berettas_golden_venice"] = { name = "Royal Consorts", desc = "Tra l'impugnatura color smeraldo, le placche laterali con l'incisione di un grifone e il carrello inciso al laser, queste pistole ridefiniscono il concetto di eleganza.\n\nAffrontami e libera le colombe." },
	["gs_famas_mecha"] = { name = "Mecha Industries", desc = "Grazie al contrasto tra il nero e il bianco, quest'arma sembra essere uscita direttamente da un film di fantascienza.\n\nCapelli blu a punta e compagno robot non inclusi." },
	["gs_final_pooldeadv2"] = { name = "Cirrus", desc = "È stata verniciata in blu metallizzato ed è stata applicata una decalcomania che rappresenta un teschio.\n\nScendi dalle nuvole." },
	["gs_g3sg1_viper_yellow"] = { name = "Stinger", desc = "È stata decorata con dettagli di colore giallo e grigio.\n\nUn momento di dolore. Poi, niente." },
	["cu_galil_ar-camo"] = { name = "Black Sand", desc = "È stato verniciato di nero e con un motivo mimetico.\n\nMikah Baton sta per iniziare la sua campagna contro la task force della Coalizione." },
	["aa_hide-mp9"] = { name = "Sand Scale", desc = "È stato verniciato in toni di grigio e marrone chiaro.\n\nDobbiamo prepararci alla rappresaglia - Felix Riley, Ufficiale comandante." },
	["cu_m4a1_flashback"] = { name = "Flashback", desc = "È stato verniciato a mano per ricordare la doppia natura dell'uomo.\n\nPer il joker che è in ognuno di noi." },
	["gs_m4a4_sector"] = { name = "Buzz Kill", desc = "Quest'arma da fuoco della Sektor Industry è stata elegantemente verniciata di giallo e verde.\n\nIndispensabile per una festa di successo." },
	["am_mag7_malform"] = { name = "Sonar", desc = "È stato decorato con una leggera tonalità turchese.\n\nHai ragione, perdere è una nuova esperienza per me – L'Oni e la valchiria, parte seconda." },
	["am_nova_sand"] = { name = "Gila", desc = "È stato ricoperto da una decalcomania di pelle di lucertola.\n\nTi comporti come se non fossi mai stato tenuto prigioniero dai mercenari – L'Oni e la valchiria, parte prima." },
	["gs_p2000-sport"] = { name = "Turf", desc = "Il carrello e l'impugnatura sono stati verniciati di verde metallizzato.\n\nL'erba del nemico è sempre più rossa." },
	["gs_p90_shallow_grave"] = { name = "Shallow Grave", desc = "Le impugnature sono state verniciate in modo da sembrare delle pietre runiche, mentre il calcio è stato decorato con la stampa idrografica di uno scheletro.\n\nCon il numero di cadaveri che stai accumulando, chi ha tempo per scavare?" },
	["cu_usp_cyrex"] = { name = "Cyrex", desc = "È stata decorata con delicati dettagli rossi, per dare un tocco futuristico.\n\nImogen era il passato, Felix. Io sono il futuro - Huxley, la concorrenza." },
	["cu_wp_sawedoff"] = { name = "Wasteland Princess", desc = "Qualcuno ha tenuto la conta delle vittime sulla canna.\n\nUn regalo perfetto per il sociopatico che ha tutto..." },
	["gs_ak47_bloodsport"] = { name = "Bloodsport", desc = "È stata decorata con delle decalcomanie di colore bianco sopra una stampa idrografica nera e rossa.\n\nServe un co-pilota." },
	["cu_awp_psychopath"] = { name = "Fever Dream", desc = "È stato verniciato con disegni rosa, blu e viola su una base bianca.\n\nIl delirio è pericoloso." },
