	["gs_r8_llamacannon"] = { name = "Llama Cannon", desc = "Il manico color avorio è abbinato ad elaborate incisioni sulla parte in metallo blu.\n\nTira la leva." },
	["cu_tec9_cracked_opal"] = { name = "Cracked Opal", desc = "È stato verniciato a mano per ricordare del legno antico e incrinato con una base blu elettrico.\n\nCangiante come un anello che cambia colore." },
	["hy_scar20_jungle_slipstream"] = { name = "Jungle Slipstream", desc = "È stato verniciato con una stampa idrografica verde e gialla.\n\nCrea il tuo eco personale" },
	["gs_sg553_phantom"] = { name = "Phantom", desc = "È stato verniciato a mano con un motivo blu e grigio e decorato con dettagli arancioni.\n\nUn'acquisizione di ScyTech" },
	["gs_cz75_tacticat"] = { name = "Tacticat", desc = "È stato verniciato a mano in rosa e viola con piccole decalcomanie feline.\n\n24 proiettili, 9 vite" },
	["cu_ump45_x-ray_machine"] = { name = "Exposure", desc = "È stato verniciato applicando un motivo che ricorda i raggi X: cassa toracica e non solo.\n\n\"Togli tutti gli oggetti metallici dalle tasche\"" },
	["aq_xm1014_ziggy_anarchy"] = { name = "Ziggy", desc = "È stato decorato con un motivo casuale verdino, viola e argento.\n\nIntroduciamo un po' di anarchia" },
