	["gs_awp_death"] = { name = "Mortis", desc = "È stato verniciato ispirandosi alla carta tarocco della Morte.\n\nOgni fine è un nuovo inizio" },
	["cu_bizon_riot"] = { name = "Night Riot", desc = "È stato verniciato con un motivo blu mimetico\n\nProvoca una rivolta" },
