	["aa_glock_18_urban_moon_fever"] = { name = "Moonrise", desc = "Un paesaggio urbano randomizzato in primo piano davanti a una notte viola di luna piena.\n\nCerca la stella polare" },
	["cu_m4a4_neo_noir"] = { name = "Neo-Noir", desc = "È stato verniciato con una figura femminile stilizzata blu magenta su uno sfondo grigio.\n\n\"Ha afferrato quello che poteva e poi è scomparsa in una distopia decadente\"" },
	["gs_mp7_bloodsport"] = { name = "Bloodsport", desc = "È stata ricoperta di decalcomanie bianche su di un'idrografica rossa e nera.\n\nAffittasi" },
