	["sp_negev_lionfish"] = { name = "Lionfish", desc = "È stato spruzzato di vernice con una fantasia che ricorda il pesce leone.\n\nNon smettere di nuotare" },
	["gs_nova_anchorite"] = { name = "Wild Six", desc = "È stato verniciato con un motivo post-apocalittico.\n\nFucile mio, regole mie" },
	["cu_p2000_urban_hazard"] = { name = "Urban Hazard", desc = "È stata verniciata a mano con un'impugnatura mimetica in scala di grigi e una canna rossa.\n\nCaschi di protezione obbligatori da questo punto in avanti" },
	["gs_revolver_tread"] = { name = "Grip", desc = "Le parti in metallo sono state stampate in maniera estremamente dettagliata e l'impugnatura è stata rivestita di gomma con un motivo blu.\n\nOgni nemico è un chiodo da battere" },
	["sp_sg533_aloha"] = { name = "Aloha", desc = "È stato spruzzato di vernice con un motivo blu ibisco.\n\nIl partner ideale" },
	["aq_mag7_swag7"] = { name = "SWAG-7", desc = "È stato ricoperto con disegni monocromatici argento e nero.\n\nO ce l'hai o non ce l'hai" },
	["cu_ump45_white_fang"] = { name = "Arctic Wolf", desc = "È stato verniciato con un motivo che ricorda il manto bianco di un lupo artico.\n\n\"Ogni round è 1 vs 5\"" },
	["cu_usp_cut"] = { name = "Cortex", desc = "È stata decorata con dettagli rosa su una base nera e argento.\n\nOcchio alla testa" },
