	["cu_ak_neon_rider"] = { name = "Neon Rider", desc = "È possibile vedere un centauro letale in primo piano una base al neon.\n\nDue quinti sballo totale, tre quinti sintetizzatore" },
	["hy_aug_torn_orange"] = { name = "Amber Slipstream", desc = "È stato verniciato a mano con un colore grigio fumo e una vibrante idrografica arancione.\n\nSi prega di tenere le mani e le braccia all'interno del veicolo" },
