	["aa_ump45_moonrise_sunset"] = { name = "Moonrise", desc = "Un paesaggio di una città sullo sfondo lascia il posto a un cielo viola e rosso in una notte di luna piena.\n\nCerca la stella polare" },
	["cu_bizon_road_warrior"] = { name = "Embargo", desc = "Assemblata con oggetti trovati, quest'arma ha una base verde acqua e varie pubblicità trasmesse all'Avana negli anni '50.\n\n¡Chao pescao!" },
	["gs_ak47_nibbler"] = { name = "Rat Rod", desc = "Recuperato da una discarica di terre desolate, la verniciatura blu originale è stata dipinta, incisa ed esposta alle intemperie.\n\nCorrere non serve a niente." },
	["cu_aug_whitefang"] = { name = "Arctic Wolf", desc = "È stato verniciato con un motivo che ricorda il manto bianco di un lupo artico.\n\nOgni round è 1 vs 5." },
	["cu_awp_virus"] = { name = "Containment Breach", desc = "È stato verniciato con un motivo che ritrae dei ratti in fuga da una foresta radioattiva verde fosforescente.\n\nChissà che malattie radioattive portano…" },
	["gs_mp5_etch"] = { name = "Acid Wash", desc = "Prima dell'assemblaggio, lo strato più esterno blu metallizzato è stato esposto all'acido. È stato abbellito con rifiniture in ottone.\n\nNero, blu e ottone dappertutto." },
	["gs_tec9_decimator"] = { name = "Decimator", desc = "È stato colorato in modo personalizzato con un blu totalmente radicale e rifiniture rosa.\n\nDisponibile con 4 comodi pagamenti da 124,95 $!" },
	["cu_nova_featherswing"] = { name = "Plume", desc = "È stato verniciato con una sovrapposizione di piume marroni e un ramo di legno.\n\nArruffiamo qualche penna." },
	["cu_g3sg1_blacksand"] = { name = "Black Sand", desc = "È stato verniciato di nero e con un motivo militare mimetico.\n\nLa task force della Coalizione stava vincendo, quindi Mikah ha scelto un approccio più... aggressivo." },
	["gs_r8_memento"] = { name = "Memento", desc = "La sua lussuosa impugnatura in avorio si sposa bene con il metallo lavorato utilizzato per telaio, canna e cilindro.\n\nAl galoppo!" },
	["cu_mp7_replica"] = { name = "Neon Ply", desc = "È stato laminato e riempito di tutti i colori fosforescenti possibili e immaginabili.\n\n\"Colori dell'arcobaleno? Mai sentiti.\"" },
	["cu_p2000_obsidian"] = { name = "Obsidian", desc = "È stato colorato in modo personalizzato così da sembrare sommerso nella lava, raffreddato e poi solidificato in ossidiana.\n\nDà un significato completamente nuovo al termine \"cannone di vetro.\"" },
	["gs_dual_elites_rose"] = { name = "Balance", desc = "Le impugnature sono state decorate con una rosa rossa su una base nera e una filigrana argento.\n\nIn tutte le cose." },
	["sp_scar20_striker_dust"] = { name = "Torn", desc = "È stato verniciato a spruzzo con un motivo a strisce casuale.\n\nA brandelli..." },
	["cu_sg553_reactor"] = { name = "Colony IV", desc = "Una scheletrica formica rossa è stata dipinta a mano sopra un cumulo di maschere antigas e una corrente gialla-arancione.\n\nEntra a far parte del loro mondo." },
	["gs_mac10_stalker"] = { name = "Stalker", desc = "È stato verniciato con un gatto blu fluo e strisce gialle e nere.\n\nQui, micio micio..." },
	["cu_ssg08_tickler"] = { name = "Bloodshot", desc = "È stato verniciato con delle mostruosità che includono tentacoli, denti e un solo occhio.\n\nPer guardarti meglio." },
	["gs_m249_warbird_veteran"] = { name = "Warbird", desc = "Inizialmente verniciato di blu con uno scudo giallo, il tempo e l'usura hanno preso ormai il sopravvento.\n\nIl vecchio rapace ha bisogno di riposo." },
	["aq_m249_aztec"] = { name = "Aztec", desc = "Alcune parti di questo M249, estratto da rovine azteche, sono arrugginite e coperte di muschio.\n\nMai fidarsi di un ponte di corda." },
	["gs_dual_elites_classic"] = { name = "Élite 1.6", desc = "Su questa classica Doppia Beretta è stata stampata la scritta ELITE.\n\nUn classico del Source." },
	["cu_famas_nuke_tension"] = { name = "Decommissioned", desc = "Questo motivo personalizzato è ispirato agli elementi di de_nuke di Counter-Strike 1.6.\n\nIn caso di ritrovamento si prega di riconsegnarlo alla Centrale nucleare di Cedar Creek." },
	["cu_tec9_flash"] = { name = "Flash Out", desc = "È stata verniciata per ricordare una granata flashbang.\n\nRe: lezioni di danza con Jordan." },
	["cu_fiveseven_gsg9"] = { name = "Buddy", desc = "Personalizzata con un motivo mimetico e la faccia di un AT sulla parte posteriore.\n\nTi copro le spalle." },
	["gs_p250_inferno"] = { name = "Inferno", desc = "È stata verniciata con un motivo personalizzato che riflette lo stile di de_inferno.\n\n\"Guardate il tempo!\"" },
	["gs_mac10_dust_crate"] = { name = "Classic Crate", desc = "È stato verniciato per somigliare alle casse metalliche verdi che un tempo si trovavano su de_dust2.\n\n\"Rush B, don't stop.\"" },
	["gs_mag7_popdog"] = { name = "Popdog", desc = "È stato verniciato con un motivo personalizzato che ricorda il vagone di un treno con il \"Pop Dog\" verniciato su un lato.\n\n\"The dog that refreshes!\"" },
	["cu_mp9_hydra"] = { name = "Hydra", desc = "È stato verniciato con un motivo basato sull'Operazione Hydra, l'ottava operazione di CS:GO.\n\nValeva la pena aspettare." },
	["cu_p90_nostalgia"] = { name = "Nostalgia", desc = "È stato verniciato con un motivo personalizzato con un AT del menu del Counter-Strike originale.\n\nDue minuti per installarlo, una vita per padroneggiarlo." },
	["am_aug_death_by_doggy"] = { name = "Death by Puppy", desc = "È stato applicato un coloratissimo motivo a tema Pop Dog.\n\nDa non confondersi con \"ladder\"." },
	["cu_scar_assault"] = { name = "Assault", desc = "È stato applicato a mano il logo di una struttura di ricerca bianca su una base blu.\n\nDisponibile anche in rosso." },
	["gs_mp5_fbi"] = { name = "Agent", desc = "È stato verniciato in blu FBI e rifinito con dettagli gialli.\n\n\"It's time to earn our stories.\"" },
	["cu_ump_bomb"] = { name = "Plastique", desc = "È stato applicato un motivo personalizzato con cavi, una scheda madre verde con circuiti e un tastierino numerico.\n\n73556**." },
	["cu_awp_wildfire"] = { name = "Wildfire", desc = "È stato verniciato a mano con il logo dell'Operazione Wildfire su una base nera e rossa.\n\n\"The nail that sticks out gets the hammer\"." },
	["cu_glock_hero"] = { name = "Sacrifice", desc = "Questa personalizzazione rende omaggio al leggendario disinnesco di olofmeister su Overpass durante l'ESL One Cologne 2014.\n\n\"The defuse is coming in—he's still going!\"" },
	["gs_famas_legacy_gold"] = { name = "Commemoration", desc = "È stato verniciato con un motivo personalizzato color oro con vari dettagli per celebrare i 20 anni di Counter-Strike.\n\n100 di questi anni." },
	["gs_ak47_gold_arabesque"] = { name = "Gold Arabesque", desc = "È stato verniciato a mano con un design intricato sul calcio e sulla guardia. Il metallo è stato verniciato d'oro e accenti filigranati.\n\n\"Cos'altro avrà in serbo Booth?\"" },
	["am_lizard_red"] = { name = "Orange Anolis", desc = "È stato verniciato con un motivo a scaglie di lucertola rosse e dorate.\n\nDa scagliare contro i nemici." },
	["hy_brush_camo_tan"] = { name = "Desert Brush", desc = "È stato verniciato con un motivo idrografico color sabbia.\n\nTest mimetico numero 1." },
	["hy_ddpat_urban_red"] = { name = "Red DDPAT", desc = "È stato verniciato usando una stampa ad acqua rossa del Digital Disruptive Pattern (DDPAT).\n\nQuando sarai abbastanza vicino da notare i pixel sarà già troppo tardi." },
	["hy_dry_wood"] = { name = "Spalted Wood", desc = "È stato verniciato con un motivo idrografico di ferro color legna.\n\nTocca ferro, avrai bisogno di parecchia fortuna - Felix Riley, ufficiale in comando." },
	["hy_desert_multicam"] = { name = "Black & Tan", desc = "È stato verniciato con uno schema idrografico MultiCam nero e marrone chiaro.\n\nNon puoi mancare il bersaglio se non spari." },
	["hy_torn_camo_paints"] = { name = "Quick Sand", desc = "Il caricatore e la cassa sono stati verniciati con un motivo idrografico marrone chiaro e blu. La canna è stata verniciata di blu scuro.\n\n\"Vederlo è un vero sollievo\"." },
	["sp_moro_carving_lightblue"] = { name = "New Roots", desc = "È stato spruzzato di vernice con un motivo a viti e fiori in varie tonalità di verde.\n\nSpazio per crescere." },
	["sp_moro_carving_yellow"] = { name = "Old Roots", desc = "È stato spruzzato di vernice con un motivo a viti e fiori in varie tonalità di verde.\n\nTradizionale, stagionato, temprato. Puoi anche dire \"vecchio.\"" },
	["sp_moro_textile_purple_yellow"] = { name = "Withered Vine", desc = "È stato spruzzato di vernice con un motivo floreale viola e dorato.\n\nDove alcuni vedono fastidio, io vedo bellezza." },
	["sp_palm_night"] = { name = "Midnight Palm", desc = "È stato spruzzato di vernice con fronde di palma nere su uno sfondo mimetico grigio e viola.\n\n\"Il cartello dice che siamo chiusi!\"" },
	["sp_desert_skulls"] = { name = "Bleached", desc = "È stato spruzzato di vernice con un motivo di teschi di animali.\n\nTutto ciò che resta." },
	["sp_zebracam_red"] = { name = "Prey", desc = "È stato verniciato a spruzzo con un motivo di strisce di zebra color ruggine.\n\nMangia, dormi, caccia, ripeti." },
	["cu_ssg08_scorpion"] = { name = "Death Strike", desc = "Sabbia scorre su uno scorpione imperatore su questo SSG 08 personalizzato.\n\nSentirai la puntura..." },
	["hy_desert_bloom"] = { name = "Parched", desc = "È stato spruzzato di vernice con fiori rigogliosi su una base arida e fratturata.\n\nLa natura vince sempre." },
	["am_ddpat_purple"] = { name = "Purple DDPAT", desc = "È stato verniciato usando una stampa ad acqua viola del Digital Disruptive Pattern (DDPAT).\n\nQuando sarai abbastanza vicino da notare i pixel sarà già troppo tardi." },
	["gs_awp_hydra"] = { name = "Desert Hydra", desc = "Serpenti dorati strisciano verso la canna di questo AWP personalizzato. Il mirino, la canna e la parte posteriore del calcio sono stati dipinti di bianco.\n\nTaglia una testa e ne appariranno altre due. È un rischio che vuoi correre? - Valeria Jenner, rivoluzionaria." },
	["gs_deagle_fennec"] = { name = "Fennec Fox", desc = "È stato verniciato con un motivo personalizzato con una volpe del deserto sulla canna.\n\nPer sentirti meglio." },
	["am_mirage_flowers_metalic"] = { name = "Music Box", desc = "È stato applicato un motivo damascato d'oro.\n\nGara di fantasia." },
	["am_moro_textile_bright"] = { name = "Elegant Vines", desc = "È stato applicato un motivo floreale rosso e verde.\n\nDove alcuni vedono fastidio, io vedo bellezza." },
	["am_navy_shine"] = { name = "Navy Sheen", desc = "È stata applicata una trama VariCamo anodizzata blu navy.\n\nPerché è così blu?" },
	["gs_aug_sand_storm"] = { name = "Sand Storm", desc = "È stato verniciato con un motivo personalizzato che ritrae una tempesta di sabbia in corso sotto un cielo stellato.\n\nLa bellezza è tutto attorno a noi, devi solo sapere dove guardare." },
	["sp_dry_wood"] = { name = "Drift Wood", desc = "È stato verniciato a spruzzo utilizzando una vernice marrone con un motivo idrografico di ferro color legna.\n\nTocca ferro, avrai bisogno di parecchia fortuna - Felix Riley, ufficiale comandante." },
	["sp_desert_skulls_dawn"] = { name = "Drought", desc = "È stato spruzzato di vernice con un motivo di teschi di animali.\n\nTutto ciò che resta." },
	["sp_mirage_flowers_tan"] = { name = "Sienna Damask", desc = "È stato verniciato a spruzzo con un motivo floreale damascato sopra alla base marroncina dell'arma.\n\nTecnicamente, le mimetizzazioni migliori sono quelle che non noti..." },
	["sp_moro_carving_burnt"] = { name = "Humidor", desc = "È stato spruzzato di vernice con un vitigno marrone e viola e un motivo floreale.\n\n\"Che cosa nascondono laggiù?\"" },
	["sp_moro_textile_green_vine"] = { name = "Verdant Growth", desc = "È stato spruzzato di vernice con un motivo floreale in varie sfumature di verde.\n\nDove alcuni vedono un fastidio, io vedo la bellezza." },
	["gs_mp5_neon_flektarn"] = { name = "Oxide Oasis", desc = "È stato applicato uno sgargiante motivo mimetico arancione e verde acqua. L'arma è stata rifinita con dettagli dorati.\n\nUn'arma così silenziosa non dovrebbe essere così sgargiante." },
	["aa_desert_bloom_bright"] = { name = "Desert Blossom", desc = "È stato spruzzato di vernice con fiori rigogliosi su una base arida e fratturata.\n\nLa natura vince sempre." },
	["hy_lizard_skin"] = { name = "Anolis", desc = "È stato verniciato con un motivo idrografico a scaglie di lucertola verdi e nere.\n\nDa scagliare contro i nemici." },
	["cu_ak-47_phantom_disruptor"] = { name = "Phantom Disruptor", desc = "Una nebulosa creatura evanescente è stata dipinta su uno sfondo nero opaco.\n\n\"Può essere ciò che vuole, dove vuole, ma sceglie comunque di rimanere con te...\"" },
	["gs_aug_thunderstorm"] = { name = "Tom Cat", desc = "La base canna di fucile è stata rifinita con dettagli dorati, frecce che puntano verso il retro e un gatto dallo sguardo furbo.\n\nI tuoni sono le fusa della natura." },
	["cu_awp_vein"] = { name = "Capillary", desc = "È stato verniciato con un intricato motivo di vene rosso corallo e verde acqua.\n\nLa chiave per il cuore di un agente costa 4750 $." },
	["cu_cz75_cerakote"] = { name = "Distressed", desc = "La rifinitura in ceramica color rame si sta deteriorando con l'utilizzo.\n\nCome il vino." },
	["cu_deagle_replica"] = { name = "Blue Ply", desc = "È stata laminata e riempita di grigio sull'impugnatura e di blu sul carrello.\n\nATTENZIONE: questa è una replica e non è stata messa alla prova sul campo con vere munizioni." },
	["cu_m4a1s_csgo2048"] = { name = "Player Two", desc = "È stato verniciato con colori accesi e presenta versioni animate di un AT GIGN e Pop Dog..\n\nPremi Start..." },
	["cu_mac10_nacre"] = { name = "Disco Tech", desc = "È stato verniciato con una finitura che ricorda un autoadesivo olografico in vinile. Il suo aspetto cambia con la luce.\n\nOcchio a dove miri." },
	["gs_mag7_justice"] = { name = "Justice", desc = "È stato verniciato ispirandosi alla carta \"Giustizia\" dei tarocchi.\n\nUna livella." },
	["cu_mp5_desert_strike"] = { name = "Desert Strike", desc = "È stato verniciato con un motivo in stile tecnologico in uno schema di colori marrone chiaro e nero.\n\nColpisci per primo, colpisci per ultimo." },
	["cu_negev_prototype"] = { name = "Prototype", desc = "È stato verniciato in bianco e rosso in attesa del completamento del progetto.\n\nv1.2.1, PAT. 3422107." },
	["aq_p2000_acid_clover"] = { name = "Acid Etched", desc = "È stata applicata una vernice multicolore con un raro quadrifoglio.\n\nTi senti fortunato?" },
	["gs_r8_leviathan"] = { name = "Bone Forged", desc = "L'impugnatura di questo revolver è stata verniciata con i resti di un'antica creatura marina.\n\nDiecimila leghe..." },
	["cu_sawedoff_apocalypto"] = { name = "Apocalypto", desc = "Una mano in decomposizione che afferra un pianeta esplosivo verniciato su uno spazio aperto viola sullo sfondo.\n\n\"Hai un minuto?\"" },
	["gs_scar20_enforcer"] = { name = "Enforcer", desc = "Un gradiente delle sfumature del tramonto è stato accuratamente verniciato su varie parti dell'arma.\n\nSoluzioni automatiche di SYMETRY SMART GUN CORP." },
	["cu_sg553_darkwing"] = { name = "Darkwing", desc = "Una verniciatura rossa e nera con piume e altri dettagli scuri.\n\nGli occhi dell'aquila sono letali, non troverai uccelli più pericolosi." },
	["cu_ssg08_fever_dream"] = { name = "Fever Dream", desc = "È stato verniciato con folli disegni rosa, blu e viola su una base nera.\n\nH@@HH@H@H@HA!!" },
	["cu_glock18_warmaiden"] = { name = "Bullet Queen", desc = "Sono stati verniciati dei proiettili minacciosi e una Regina di guerra in abito rosa su una base gialla e nera.\n\n\"Smetterò di sparare quando smetterai di parlare.\"" },
	["cu_negev_ultralight"] = { name = "Ultralight", desc = "È stato dipinto con un motivo digitale mimetico grigio e delle frecce verde acqua.\n\nHai da accendere?" },
	["cu_ak47_anubis"] = { name = "Legion of Anubis", desc = "Dipinto per raffigurare gli dei egizi Anubi e Ra, a rappresentare il bilanciamento tra la vita e la morte.\n\nChe possa egli far pendere l'ago della bilancia in tuo favore." },
	["aq_p2000_lost_world"] = { name = "Gnarled", desc = "Un mostruoso alieno copre l'impugnatura e la canna di questa P2000 dipinta a mano.\n\nDagli angoli più oscuri del vuoto infinito." },
	["cu_mag7_monster_call"] = { name = "Monster Call", desc = "È stato dipinto con una base blu e creature sottomarine in primo piano.\n\nC'è sempre un pesce più grosso e più verde." },
	["cu_printstream"] = { name = "Printstream", desc = "È stata verniciata con un motivo bianco e nero e dettagli perlati.\n\nWHITE_1; BLACK_1; PEARLESCENT_1.;" },
	["cu_glock_eyecontact"] = { name = "Vogue", desc = "Questa Glock ispirata alla pop-art presenta un paio di occhi con uno sguardo profondo sulla canna.\n\n\"Guardami, ti sto sparando!\"" },
	["gs_tec9_guerilla"] = { name = "Brother", desc = "È stato dipinto con un motivo mimetico marrone e oro e un Terrorista nella parte posteriore.\n\nTi guardo le spalle." },
	["cu_mac10_isoonna"] = { name = "Allure", desc = "Una donna su una base verde acqua, anche chiamata iso-onna, si erge di fronte a carpe koi e onde che si infrangono su questo MAC-10 dipinto a mano.\n\nLa bellezza genera dolore." },
	["gs_sg553_rusty"] = { name = "Ol' Rusty", desc = "Originariamente in acciaio lucido, l'umidità ha modificato l'aspetto dell'arma. \"Ol' Rusty\" è stato stampato sopra al grilletto.\n\nUn vecchio amico arrugginito." },
	["cu_ssg08_mainframe"] = { name = "Mainframe 001", desc = "È stato applicato un motivo blu e viola in stile high tech.\n\nQuali sono i segreti di _001?" },
	["cu_p250_cassette"] = { name = "Cassette", desc = "È stato applicato un motivo ispirato alle confezioni delle cassette degli anni '90\n\nBeccati il nastro." },
	["gs_p90_container"] = { name = "Freight", desc = "Il suo motivo ricorda il vagone di un treno coperto di graffiti.\n\nL'arte non è vandalismo." },
	["cu_xm1014_amulet_blue"] = { name = "Entombed", desc = "Sotterrato per millenni, questo XM-1014 estratto di recente è stato dipinto con una base blu e dettagli in stile egizio.\n\nFaraonico." },
	["cu_m4a4_queenfairy"] = { name = "Tooth Fairy", desc = "È stato applicato un motivo che rappresenta incisivi, molari e una rappresentazione da brividi della fatina dei denti.\n\nQuesti denti non devono esse per forza tuoi…" },
	["gs_galil_phoenix"] = { name = "Connexion", desc = "I loghi della fazione Phoenix sono stati spruzzati sulla sua base bianca e rossa.\n\nStanno portando le armi pesanti." },
	["gs_bizon_hellraider"] = { name = "Runic", desc = "È stato applicato un motivo che presenta rune dall'origine sconosciuta e un caricatore che sembra una mitragliatrice Gatling.\n\nFagli vedere l'inferno." },
	["gs_mp5sd_wasteland_legacy"] = { name = "Kitbash", desc = "20 anni di design di MP5 hanno contribuito a questa verniciatura personalizzata, tenuta insieme da nastro adesivo, cerniere e colla.\n\nSe all'inizio (o alla fine) non va per il verso giusto..." },
	["gs_awp_exoskeleton"] = { name = "Exoskeleton", desc = "Al metallo di questo AWP è stato applicato un motivo con teschi intrecciati.\n\nInclinazioni." },
	["gs_cz75_vendetta"] = { name = "Vendetta", desc = "Negli anni è stata incisa, disegnata e temprata dall'uso.\n\nDalle mie mani fredde e morte." },
	["hy_p90_dino_rampage"] = { name = "Cocoa Rampage", desc = "È stato applicato un motivo idrografico color oliva e oro con dinosauri in stile cartoon.\n\nDinosauri, alieni e comete, dove siamo?" },
	["gs_dual_elites_dezastre"] = { name = "Dezastre", desc = "Sono state verniciate con un design tecnologico nero, bianco e rosso.\n\nSeguire sempre le istruzioni d'uso del produttore per motivi di sicurezza." },
	["cu_five_seven_diary"] = { name = "Fairy Tale", desc = "È stata verniciata con una principessa, unicorni, splendidi fiori e un castello in lontananza.\n\n\"Sarà una splendida giornata, me lo sento.\"" },
	["gs_g3sg1_disrupt"] = { name = "Digital Mesh", desc = "È stato verniciato con un motivo mimetico DDPAT rosso, bianco e grigio.\n\nIntricato da morire." },
	["gs_galil_vandal"] = { name = "Vandal", desc = "Lo sfondo verde è stato ricoperto da disegni bianchi sparsi.\n\nOgni disegno racconta una storia, alcune più memorabili di altre." },
	["cu_p250_infect"] = { name = "Contaminant", desc = "La verniciatura superficiale è stata ormai consumata, lasciando così un'arma deteriorata con poca vita rimanente.\n\nÈ messa male come sembra." },
	["cu_m249_deep_relief"] = { name = "Deep Relief", desc = "Uno strato esterno color sabbia lascia intravedere quelli di un verde sempre più scuro procedendo in profondità. Una verniciatura personalizzata che puoi davvero sentire.\n\nQuanti proiettili servono per arrivare al centro?" },
	["cu_m4a4_cyberpunk"] = { name = "Cyber Security", desc = "È stato verniciato con strisce blu, rosse e dorate su una base nera.\n\nDISPONIBILE PRESSO IL TUO RIVENDITORE CIBERNETICO." },
	["gs_mp5_conditionzero"] = { name = "Condition Zero", desc = "Il motivo personalizzato blu e giallo rende omaggio a Counter-Strike: Condition Zero.\n\n\"Lanciarazzi a ore tre! Schivare!\"" },
	["cu_nova_polymer"] = { name = "Clear Polymer", desc = "È stato verniciato per far sembrare trasparente la scocca, consentendoti di vedere l'interno dell'arma.\n\nLe tue intenzioni sono cristalline." },
	["cu_glock_noir"] = { name = "Neo-Noir", desc = "È stata verniciata con una donna stilizzata blu e magenta su uno sfondo grigio.\n\n\"Le cose sembravano andare meglio e iniziava a sperare, ma poi ha guardato su\"." },
	["cu_ssg08_chromatic"] = { name = "Parallax", desc = "I colori di questo motivo personalizzato sono stati applicati in posizioni sfalsate, producendo un effetto strabiliante.\n\nSpara due volte, uccidi una volta." },
	["gs_ump_gold_bismuth"] = { name = "Gold Bismuth", desc = "Un motivo in bismuto dorato tra due strati di vernice bianchi e neri.\n\n\"Hai dei gusti costosi…\"" },
	["cu_usp_krokos"] = { name = "Monster Mashup", desc = "Un motivo personalizzato che rappresenta un mostro verde e altre icone ispirate all'arte urbana popolare. Il silenziatore ha una finitura perlata.\n\nX__X" },
	["cu_csgo_camo"] = { name = "Global Offensive", desc = "È stato verniciato con un motivo mimetico blu, giallo e grigio.\n\n\"Il primo contatto sarà ad Harz, Germania, ore 17:25 GMT+1...\"" },
	["am_authority_brown"] = { name = "Charter", desc = "È stato applicato un motivo marrone di simboli, scarabocchi e parole della Magna Carta.\n\nLa giustizia è uguale per tutti, anche per te." },
	["hy_authority_purple"] = { name = "Magna Carta", desc = "È stato applicato un motivo viola di simboli, scarabocchi e parole della Magna Carta.\n\nLa giustizia è uguale per tutti, anche per te." },
	["am_intelligence_grey"] = { name = "Surveillance", desc = "Un motivo personalizzato che rappresenta una mappa sovrapposta a un HUD a veduta aerea.\n\nBersaglio acquisito." },
	["am_intelligence_orange"] = { name = "Threat Detected", desc = "Un motivo personalizzato che rappresenta una mappa sovrapposta a un HUD a veduta aerea.\n\nBersaglio acquisito." },
	["am_intelligence_magenta"] = { name = "Target Acquired", desc = "Un motivo personalizzato che rappresenta una mappa sovrapposta a un HUD a veduta aerea.\n\nBersaglio acquisito." },
	["am_numbers_bronze"] = { name = "The Bronze", desc = "È stata applicata una stampa multicolore con numeri randomizzati.\n\nI numeri non mentono, io sì." },
	["am_numbers_red_blue"] = { name = "Dispatch", desc = "È stata applicata una stampa multicolore con numeri randomizzati.\n\nI numeri non mentono, io sì." },
