	["aa_fade_red_blue"] = { name = "Berries And Cherries", desc = "Il carrello è stato verniciato metà rosso e metà blu.\n\nYuhuu!" },
	["cu_ump_crime_scene"] = { name = "Crime Scene", desc = "È stato verniciato con un nastro giallo da scena del crimine su una base rossa e blu.\n\nRipulite l'area, abbiamo del lavoro da fare…" },
	["am_m4a1s_bluesmoke"] = { name = "Blue Phosphor", desc = "Su una verniciatura blu cromata sono stati applicati diversi strati di vernice blu metallizzata.\n\nNel blu, dipinto di blu." },
	["cu_ak_xray"] = { name = "Raggi X", desc = "Un AK-47 personalizzato che mostra il suo interno ai raggi X.\n\nPotrebbe non piacerti quello che stai per vedere..." },
	["am_heist_plans_green"] = { name = "Heist", desc = "La base è stata verniciata con un motivo beige anodizzato. Sembra che qualcuno abbia disegnato sulla mappa dopo che questa è stata applicata.\n\nPianificare è un conto, eseguire è un altro." },
	["am_heist_plans_purple"] = { name = "Night Heist", desc = "La base è stata verniciata con un motivo viola anodizzato. Sembra che qualcuno abbia disegnato sulla mappa dopo che questa è stata applicata.\n\nPianificare è un conto, eseguire è un altro." },
	["am_heist_plans_yellow"] = { name = "Vault Heist", desc = "La base è stata verniciata con un motivo grigio anodizzato. Sembra che qualcuno abbia disegnato sulla mappa dopo che questa è stata applicata.\n\nPianificare è un conto, eseguire è un altro." },
	["hy_houndstooth_brown"] = { name = "Houndstooth", desc = "È stata applicata una stampa idrografica a quadri.\n\nPreparati per il ruolo che desideri, non per quello che possiedi." },
	["gs_mac10_snake"] = { name = "Hot Snakes", desc = "È stato verniciato con un motivo in stile pelle di serpente e un serpente attorcigliato.\n\nQuello è il suono dei proiettili che ti sfiorano." },
	["hy_phoenix_tags_lilac"] = { name = "Phoenix Chalk", desc = "È stato applicato un motivo multicolore di vari graffiti Phoenix Connexion.\n\n\"Sei stupido a pensare che vincerai!\"" },
	["am_phoenix_tags_blue"] = { name = "Phoenix Marker", desc = "È stato applicato un motivo multicolore di vari graffiti Phoenix Connexion.\n\n\"Ormai non possono più fermarci!\"" },
	["hy_phoenix_tags_red"] = { name = "Phoenix Stencil", desc = "È stato applicato un motivo multicolore di vari graffiti Phoenix Connexion.\n\n\"Non possiamo essere uccisi!\"" },
	["sp_phoenix_tags_purple"] = { name = "Phoenix Blacklight", desc = "È stato applicato un motivo multicolore di vari graffiti Phoenix Connexion.\n\n\"Balleremo sulle vostre tombe!\"" },
	["hy_tigers_tan"] = { name = "Clay Ambush", desc = "È stata applicata una stampa idrografica marrone e rossa con una tigre.\n\nIn genere predatori solitari, faranno un'eccezione per te." },
	["am_tigers_brown"] = { name = "Tiger Pit", desc = "È stata applicata una stampa idrografica marrone e rossa con una tigre su una base nera.\n\nOvunque, ma non qui." },
	["am_tigers_blue"] = { name = "Silk Tiger", desc = "È stata applicata una stampa idrografica marrone e rossa con una tigre su una base blu.\n\nUn patrimonio nazionale. La tigre, non l'AWP." },
	["cu_m4a1_snake"] = { name = "Welcome to the Jungle", desc = "Questo motivo personalizzato presenta un'iconografia antica e un crotalo dorato davanti a una giungla al tramonto.\n\nColpisci senza preavviso." },
	["cu_ak_jaguar"] = { name = "Panthera onca", desc = "Il legno sulla cassa e sulla guardia presenta delle stupende incisioni che spesso distolgono l'attenzione dal giaguaro personalizzato verniciato sulla parte laterale.\n\nChi dice che non puoi cambiare pelle?" },
	["cu_jaguar_p90"] = { name = "Run and Hide", desc = "È stato verniciato con un motivo maculato personalizzato e rifinito con pelle lavorata.\n\nChiunque può essere modaiolo, ma pochissimi possiedono un vero stile." },
	["aa_ancient_brown"] = { name = "Ancient Earth", desc = "È stato applicato un intricato motivo di linee marroni e oro con antiche creature.\n\nA te l'onore." },
	["am_ancient_warm"] = { name = "Ancient Lore", desc = "È stato applicato un motivo di tessere dai pallidi toni verdi, beige e rossi.\n\n\"Vi è speranza anche per coloro che hanno intrapreso il percorso sbagliato\"." },
	["aa_ruins_green"] = { name = "Lush Ruins", desc = "Gli antichi dèi vengono oscurati nello sfondo di questo motivo adornato da un particolare foliage.\n\n\"Occhi al cielo, qualcuno ci osserva...\"" },
	["hy_ancient_tiles_peach"] = { name = "Blast From the Past", desc = "È stato applicato un motivo di tessere antiche dai pallidi toni verdi e beige.\n\n\"Vi è speranza anche per coloro che hanno intrapreso il percorso sbagliato\"." },
	["am_gold_brick"] = { name = "Gold Brick", desc = "La sua finitura dorata brilla alla luce, evidenziando ogni bellissima imperfezione dell'arma.\n\nNon è tutto oro..." },
	["sp_ancient_bright"] = { name = "Ancient Visions", desc = "È stato applicato un intricato motivo di linee verdi e rosse con antiche creature.\n\nA te l'onore." },
	["hy_ruins_red"] = { name = "Dusk Ruins", desc = "Gli antichi dèi vengono oscurati nello sfondo di questo motivo adornato da un particolare foliage.\n\n\"Occhi al cielo, qualcuno ci osserva...\"" },
	["am_jade"] = { name = "Carved Jade", desc = "È stato applicato uno strato base di cromatura con una verniciatura ad effetto trasparente di colore verde giada anodizzato.\n\nLa crème de la crème." },
	["am_ancient_wine"] = { name = "Ancient Ritual", desc = "È stato applicato un intricato motivo di linee marroni e argento con antiche creature.\n\nA te l'onore." },
	["hy_drywood_green"] = { name = "Tall Grass", desc = "È stata applicata una stampa idrografica con erba verde.\n\nTra l'erba alta non accade nulla di buono." },
	["am_black_panther"] = { name = "Panther Camo", desc = "È stata applicata una finitura maculata nero pantera.\n\nTrova il tuo posticino all'ombra." },
	["gs_ak47_professional"] = { name = "Slate", desc = "È stata applicata una verniciatura personalizzata che può essere descritta solo come \"nero su nero, su grigio ardesia\".\n\n\"Chiamate un medico, ne avranno bisogno.\"" },
	["cu_cz75_whirlwind"] = { name = "Circaetus", desc = "Su questa CZ75 è stato dipinto un rapace con la sua preda.\n\nSnake eater senza la sigla accattivante." },
	["cu_mp9_food_chain"] = { name = "Food Chain", desc = "Questo MP9 dalle colorazioni accese è stato verniciato con una mostruosa catena alimentare.\n\nMangia, o verrai mangiato." },
	["cu_galil_chroma_pink"] = { name = "Chromatic Aberration", desc = "I colori di questo motivo personalizzato sono stati applicati in posizioni sfalsate, producendo un effetto strabiliante.\n\n(Non proprio) un piacere per gli occhi..." },
	["gs_glock_polymer"] = { name = "Clear Polymer", desc = "È stata verniciata con un carrello marroncino e un grilletto blu. Il telaio è realizzato in un polimero trasparente.\n\nVisto e piaciuto." },
	["gs_usps_hangedman"] = { name = "The Traitor", desc = "È stata verniciata con un motivo ispirato al tarocco dell'impiccato.\n\nI fallimenti passati sono i tracciati dei successi futuri." },
	["cu_m4a4_love"] = { name = "In Living Color", desc = "È stato verniciato usando colori al neon e coperto con disegni fatti a mano.\n\nLotta per uno, lotta per tutti." },
	["gs_m249_combine"] = { name = "O.S.I.P.R.", desc = "Questo M249 è stato verniciato in grigio canna di fucile e decorato con un logo Combine.\n\nOverwatch Standard Issue Pulse Rifle." },
	["cu_negev_devtexture"] = { name = "dev_texture", desc = "È stato verniciato con una base arancione e texture grigie.\n\nhammer.exe time." },
	["gs_p250_cybershell"] = { name = "Cyber Shell", desc = "È stata verniciata con un carrello argentato e l'impugnatura ha una texture esagonale.\n\nRappresenti la prima linea delle nostre difese di cybersicurezza." },
	["cu_mac10_portable"] = { name = "Button Masher", desc = "È stato verniciato per ricordare un gamepad e vari pulsanti in una scocca trasparente.\n\nLe \"console war\" assumono tutto un altro significato." },
	["aq_xm1014_punk"] = { name = "XOXO", desc = "È stato applicato un motivo dai colori vivaci in stile punk.\n\nEhi, alla grande…" },
	["gs_r8_rustking"] = { name = "Junk Yard", desc = "La base blu è stata dipinta con disegni e scritte fatte a mano. Il cilindro e il mirino frontale sono stati dipinti di rosso.\n\nRoba da re." },
	["gs_sg553_deathmetal"] = { name = "Heavy Metal", desc = "La sua base nera è stata decorata con dettagli arancioni e gialli. Sono state dipinte tre stelle nella parte posteriore del calcio.\n\n\"Uno di questi è il pulsante Turbo, non ricordo quale…\"" },
	["am_ump45_electrowave"] = { name = "Oscillator", desc = "È stato applicato un motivo randomizzato arancione, nero e grigio.\n\nTutti hanno un punto di rottura. Qual è il tuo?" },
	["cu_deag_trigger_discipline"] = { name = "Trigger Discipline", desc = "È stato applicato un motivo rosa isometrico sullla canna. Il mirino frontale e il cane sono stati dipinti di blu.\n\nLa pazienza stessa è la ricompensa." },
	["sp_nova_wind_dispersal"] = { name = "Windblown", desc = "È stato verniciato a spruzzo con un motivo casuale di denti di leone.\n\nFuori dal mio prato." },
	["am_nuclear_pattern4_famas"] = { name = "Meltdown", desc = "È stato verniciato con un motivo di simboli nucleari utilizzando vernici metallizzate.\n\nStai alla larga per un po'..." },
	["aa_spacerace_orange"] = { name = "Space Race", desc = "È stato applicato un motivo a tema spaziale composto da stelle e satelliti.\n\nInviare e ricevere." },
	["sp_spacerace_blue"] = { name = "Sputnik", desc = "È stato applicato un motivo a tema spaziale composto da stelle e satelliti.\n\nInviare e ricevere." },
	["sp_technowar_red"] = { name = "Fizzy POP", desc = "È stato applicato un motivo di ispirazione techno-retrò marroncino e rosso.\n\nLe guerre si vincono con le informazioni." },
	["hy_technowar_rwb"] = { name = "POP AWP", desc = "È stato applicato un motivo di ispirazione techno-retrò bianco, rosso e blu.\n\nLe guerre si vincono con le informazioni." },
	["hy_trainarchitect_green"] = { name = "Spring Twilly", desc = "È stato applicato un motivo idrografico nero e verde di design assortiti.\n\nUltima chance: il treno sta partendo dalla stazione." },
	["hy_trainarchitect"] = { name = "Autumn Twilly", desc = "È stato applicato un motivo idrografico rosso e marroncino di design assortiti.\n\nUltima chance: il treno sta partendo dalla stazione." },
	["gs_m4a4_coalition"] = { name = "The Coalition", desc = "È stato verniciato con un motivo personalizzato oro e nero con triangoli intrecciati e rifiniture blu.\n\nQuesta alleanza è temporanea, ma mi stai facendo cambiare idea…" },
	["cu_mac10_propaganda"] = { name = "Propaganda", desc = "È stato verniciato con un motivo personalizzato con un paio di occhi e varie parole in russo, tra cui \"controllo\" e \"paura\".\n\nNon ci credi, vero?" },
	["gs_train_cz75"] = { name = "Syndicate", desc = "È stato verniciato con un motivo personalizzato oro e nero con triangoli intrecciati e rifiniture blu.\n\nSi va insieme." },
	["hy_ak47lam_green"] = { name = "Green Laminate", desc = "È stato applicato un laminato verde al calcio, all'impugnatura e alla guardia di questo AK.\n\nLe cose più semplici sono le migliori." },
	["sp_galil_caution"] = { name = "ATTENZIONE!", desc = "È stato spruzzato di vernice con galloni in contrasto neri e gialli.\n\nIl piacere di sentirsi fuori posto." },
	["hy_geometric_steps_pearl"] = { name = "Prism Terrace", desc = "È stato applicato un motivo di illusione ottica perlato.\n\nDove ha inizio e dove finisce, se finisce?" },
	["gs_m4a1_vertigo"] = { name = "Imminent Danger", desc = "È stato verniciato con un motivo personalizzato di segnaletica di sicurezza multicolore.\n\nNon dire che non ti avevo avvisato… - Booth, trafficante d'armi." },
	["hy_vertigogeo"] = { name = "Schematic", desc = "È stato applicato un motivo con una griglia isometrica multicolore.\n\nTu ricordi il dolore, io ricordo la vittoria" },
	["hy_vertigospray_blue"] = { name = "Strategie", desc = "È stato applicato un motivo idrografico dei piani di costruzione del 51° piano, dell'aria condizionata e dello schema elettrico.\n\nTutto va secondo i piani…" },
	["hy_vertigoillusion"] = { name = "Framework", desc = "È stato applicato un motivo idrografico grigio e marroncino con un'illusione ottica.\n\nHai dato una bella occhiata?" },
	["hy_vertigoillusion_yellow"] = { name = "Interlock", desc = "È stato applicato un motivo idrografico giallo e blu con un'illusione ottica.\n\nHai dato una bella occhiata?" },
	["hy_vertigospray"] = { name = "Infrastructure", desc = "È stato applicato un motivo idrografico dei piani di costruzione del 51° piano, dell'aria condizionata e dello schema elettrico.\n\nTutto va secondo i piani…" },
	["aa_vertigogeo_neon"] = { name = "Digital Architect", desc = "È stato applicato un motivo di griglia isometrica multicolore.\n\nTu ricordi il dolore, io ricordo la vittoria." },
	["cu_sg553_caution"] = { name = "Hazard Pay", desc = "È stato verniciato con un motivo personalizzato con una base gialla e dettagli arancioni di coni stradali.\n\nButta al vento la prudenza, un proiettile alla volta." },
	["aa_wiring"] = { name = "Breaker Box", desc = "È stato applicato un motivo anodizzato di uno schema elettrico\n\nSi prega di consultare un elettricista prima di procedere con le riparazioni." },
	["aa_wiring_yellow"] = { name = "Faulty Wiring", desc = "È stato applicato un motivo di uno schema elettrico\n\nSi prega di consultare un elettricista prima di procedere con le riparazioni." },
	["sp_engine_dirty"] = { name = "Oil Change", desc = "È stato spruzzato di vernice con un motivo di parti di motore.\n\n\"Non è messo molto bene...\"" },
	["aa_engine_performance"] = { name = "Mechanism", desc = "È stato applicato un motivo anodizzato di componenti di motore.\n\n\"Non è messo molto bene...\"" },
	["cu_vertigo_fiveseven"] = { name = "Fall Hazard", desc = "È stata applicata una vernice multicolore personalizzata che dice \"FALL\" e \"CONSTRUCTION\".\n\nMind the gap." },
	["sp_tire_tread_red"] = { name = "Red Tire", desc = "È stato spruzzato con un motivo nero e rosso simile a un copertone.\n\nGomma bruciata." },
	["sp_tire_tread_blue"] = { name = "Blue Tire", desc = "È stato spruzzato con un motivo nero e blu simile a un copertone.\n\nGomma bruciata." },
	["gs_ak47_abstract"] = { name = "Leet Museo", desc = "È stato verniciato con una rappresentazione cubista di una squadra d'élite Terrorista in avanzamento su Dust II.\n\n\"Tutto ciò che puoi immaginare è reale.\"" },
	["gs_aug_plague"] = { name = "Plague", desc = "È stato verniciato con un motivo che si ispira al fantasy dark e all'horror.\n\n\"Come una falena attirata dalla luce.\"" },
	["gs_mag7_bismuth"] = { name = "BI83 Spectrum", desc = "È stato verniciato per avere l'aspetto di cristalli di bismuto iridescente.\n\nPerché sono le cose più rare a essere le più belle?" },
	["cu_deagle_kitch"] = { name = "Ocean Drive", desc = "È stato verniciato usando colori molto saturi e svariate forme.\n\nCon un'arma del genere, non sbagli mai." },
	["gs_dual_berettas_tread"] = { name = "Tread", desc = "Su entrambe le Beretta è stato inciso un motivo ondoso. Ai carrelli è stata applicata una vernice dorata.\n\nI dettagli raffinati impiegano tempo, aggiungono molto e vengono notati poco." },
	["cu_famas_spectron"] = { name = "ZX Spectron", desc = "Questo motivo personalizzato si ispira ai vecchi PC da gioco. La base nera è stata rifinita con accenti multicolore.\n\nClive ne andrebbe fiero." },
	["gs_five_seven_efusion"] = { name = "Boost Protocol", desc = "È stato verniciato usando colori dal forte contrasto. L'icona del robot sotto il mirino posteriore corrisponde all'illustrazione sul carrello.\n\nNominativo: MECHA 0272." },
	["aa_mp9_fuji_pink"] = { name = "Mount Fuji", desc = "È stato applicato un dipinto acrilico del monte Fuji all'alba. Il dipinto viene usato come motivo, quindi ogni arma potrebbe apparire diversa.\n\nSorge un sole rosso." },
	["cu_g3sg1_tacticalmap"] = { name = "Keeping Tabs", desc = "È stato verniciato ispirandosi al display della mappa tattica di Zona di pericolo.\n\nHai tutte le informazioni che ti servono - Franz Kriegeld, tattico Phoenix." },
	["cu_mp7_khaki"] = { name = "Guerrilla", desc = "Questo motivo personalizzato presenta una base verde, dettagli neri e una scimmia vista di fronte.\n\nIl leone non è mai stato il re della giungla." },
	["gs_m4_flowers"] = { name = "Spider Lily", desc = "È stato verniciato con una base blu scuro e gigli ragno rossi sopra il caricatore e il grilletto.\n\nI colori brillanti sono un avvertimento, non un invito." },
	["cu_mac10_toybox"] = { name = "Toybox", desc = "Questo motivo personalizzato presenta disegni a colori sgargianti di polli, alieni, disinnescatori e altro.\n\nNon lasciare casino." },
	["gs_bizon_flasher"] = { name = "Lumen", desc = "È stato verniciato di nero con accenti rossi. Il caricatore è stato verniciato in modo da ricordare una torcia.\n\nE luce fu." },
	["cu_glock_snackattack"] = { name = "Snack Attack", desc = "È stato verniciato con decorazioni a motivo cibo spazzatura sospese nella resina.\n\nLa merenda combattiva." },
	["cu_ssg_overtake"] = { name = "Turbo Peek", desc = "Auto da corsa gareggiano sul lato di questo fucile da cecchino color neon.\n\nLe derapate sono un diritto, non un privilegio." },
	["cu_usp_black_lotus"] = { name = "Black Lotus", desc = "È stato verniciato con un fiore di loto nero sul carrello.\n\nAggiunge 3 uccisioni durante il round delle pistole, poi viene scartata." },
	["gs_xm1014_watchdog"] = { name = "Watchdog", desc = "È stato personalizzzato con una base verde giada e due leoni di guardia che guardano in direzioni opposte. Un terzo leone è stato verniciato sotto il mirino posteriore.\n\nProteggi e prospera." },
	["cu_ak47_nightwish"] = { name = "Nightwish", desc = "È stato verniciato con un cervo e acchiappasogni in colori al neon e rifinito con un effetto perlescente.\n\nL'ho tenuto da parte per un'occasione speciale... - Booth, trafficante di armi." },
	["cu_bizon_spacecat"] = { name = "Space Cat", desc = "È stato verniciato con una raffigurazione in stile spaziale di un gatto su una base nera e viola.\n\nLe zampe della creazione." },
	["cu_elites_beware"] = { name = "Melondrama", desc = "Queste Beretta sono state verniciate con una raffigurazione contorta e spaventosa di un'anguria su uno sfondo blu navy.\n\nDolci sogni..." },
	["gs_famas_rapid_eyes"] = { name = "Rapid Eye Movement", desc = "È stato verniciato a quadrati e rettangoli intrecciati in varie tonalità di rosa. All'interno occhi realistici.\n\nGli irrequieti occhi del sognatore." },
	["cu_fiveseven_alpha_omega"] = { name = "Scrawl", desc = "È stata dipinta a mano usando tratti forti su una base gialla e verde.\n\nIl primo e l'ultimo." },
	["cu_g3sg1_glade"] = { name = "Dream Glade", desc = "Una foresta verde lascia spazio al cielo azzurro al centro di questa verniciatura personalizzata.\n\n\"Eccola, una radura nella foresta!\"" },
	["gs_m4a1s_insomnia"] = { name = "Night Terror", desc = "I pensieri sparsi della mente di un insonne sono stati applicati su questa verniciatura personalizzata.\n\nIn seno alla dolce notte io cerco la luce." },
	["cu_mac10_pixie"] = { name = "Ensnared", desc = "Una verniciatura con folletti intrappolati all'interno dell'arma e desiderosi di fuggire.\n\nDevono ancora incontrare qualcuno che non riescano a ingannare." },
	["cu_mag7_predictor"] = { name = "Foresight", desc = "Questa verniciatura personalizzata presenta un polpo a cui viene chiesto di prevedere il risultato di una partita, che cerca di raggiungere la scritta \"Win\".\n\nMi sento fortunato..." },
	["cu_mp7_fear"] = { name = "Abyssal Apparition", desc = "Questa verniciatura personalizzata raffigura delle anime perdute che cadono in un vortice di incubi.\n\nNon puoi fuggire al tuo destino." },
	["gs_mp9_starlight"] = { name = "Starlight Protector", desc = "Questa verniciatura personalizzata raffigura un unicorno con una chioma arcobaleno su una base bianca. È stata poi rifinita con dettagli dorati.\n\nChe possa questa divinità proteggerti, se dovessi essere scelto." },
	["am_xm_zombie_offensive"] = { name = "Zombie Offensive", desc = "È stato applicato un modello personalizzato con agenti di Counter-Strike in versione zombi.\n\n\"Gioco tra non morti\"." },
