	["cu_p2k_flying_dream"] = { name = "Lifted Spirits", desc = "È stata verniciata con una foresta al di sotto di corpi che ascendono al cielo.\n\nMolte anime perdute non desiderano essere trovate." },
	["sp_scar_chickenfight"] = { name = "Poultrygeist", desc = "È stato verniciato a spruzzo con un motivo che raffigura polli in un campo di battaglia.\n\nUn pollo guida uno squalo in battaglia..." },
