	["cu_awp_chroma_pink"] = { name = "Chromatic Aberration", desc = "I colori di questo motivo personalizzato sono stati applicati in posizioni sfalsate, producendo un effetto strabiliante.\n\n(Non proprio) un piacere per gli occhi..." },
	["gs_revolver_purple_elite"] = { name = "Crazy 8", desc = "È stato verniciato con un motivo personalizzato che vede una canna viola e un numero 8 sull'impugnatura.\n\nIspirato agli eventi dell'8 dicembre 2015." },
	["gs_famas_corp_meow"] = { name = "Meow 36", desc = "La sua base verde è stata incisa con il numero 36.\n\nTecnicamente sono quattro gatti, ma chi si prenderà mai la briga di contare?" },
	["cu_galil_destroyer"] = { name = "Destroyer", desc = "È stato verniciato con un motivo personalizzato con la scritta \"DESTROYER\", che però può essere vista solo da alcune angolazioni.\n\nDistruggili tutti." },
	["cu_m249_downvote"] = { name = "Downtown", desc = "È stato verniciato con frecce blu e rosa che puntano in ogni direzione.\n\nUna verniciatura a spruzzo un po' diversa." },
	["cu_m4a4_elite_tactical"] = { name = "Poly Mag", desc = "Questo motivo personalizzato presenta un caricatore polimerico trasparente e le parti restanti sono colorate di marrone.\n\nProcedura operativa standard." },
