	["cu_negev_clear_sky"] = { name = "Drop Me", desc = "Un segnale di stop è stato verniciato vicino sull'impugnatura d'assalto su una base principalmente marrone.\n\nSe lo stop non ti ferma, una scarica di proiettili funzionerà meglio." },
